// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {MigrationMode} from "../types/MigrationMode.sol";

/// @title IMigrator
/// @notice Interface for the Migrator contract (abstract)
interface IMigrator {
    /// @notice Error thrown when a token route is missing
    error MissingTokenRoutes();
    /// @notice Error thrown when too many token routes are provided
    error TooManyTokenRoutes();
    /// @notice Error thrown when a token and route mismatch
    error TokenAndRouteMismatch(address token);
    /// @notice Error thrown when multiple tokens and routes mismatch
    error TokensAndRoutesMismatch(address token0, address token1);
    /// @notice Error thrown when an amount is too low
    error AmountTooLow(uint256 amount, uint256 amountMin);

    /// @notice Event emitted when a migration is started
    event MigrationStarted(
        bytes32 indexed migrationId,
        uint256 indexed positionId,
        uint256 indexed chainId,
        address settler,
        MigrationMode mode,
        address sender,
        address token,
        uint256 amount
    );

    /// @notice Struct for a token route
    struct TokenRoute {
        address token;
        uint256 amountOutMin;
        bytes route;
    }

    /// @notice Struct for migration parameters
    struct MigrationParams {
        uint256 chainId;
        address settler;
        TokenRoute[] tokenRoutes;
        bytes settlementParams;
    }
}

src/types/MigrationData.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {MigrationMode} from "./MigrationMode.sol";

/// @title MigrationData
/// @notice Struct for migration data
struct MigrationData {
    uint256 sourceChainId;
    address migrator;
    uint256 nonce;
    MigrationMode mode;
    bytes routesData;
    bytes settlementData;
}

using MigrationDataLibrary for MigrationData global;

/// @title MigrationDataLibrary
/// @notice Library for handling migration data
library MigrationDataLibrary {
    /// @notice Function to create a migration ID from data
    /// @param self The migration data
    /// @return The migration ID
    function toId(MigrationData memory self) internal pure returns (bytes32) {
        return keccak256(
            abi.encode(self.sourceChainId, self.migrator, self.nonce, self.mode, self.routesData, self.settlementData)
        );
    }
}

src/types/MigrationMode.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

type MigrationMode is uint8;

using {equals as ==} for MigrationMode global;

function equals(MigrationMode a, MigrationMode b) pure returns (bool) {
    return MigrationMode.unwrap(a) == MigrationMode.unwrap(b);
}

/// @title MigrationModes
/// @notice Migration modes
library MigrationModes {
    /// @notice Single migration mode
    MigrationMode constant SINGLE = MigrationMode.wrap(1);
    /// @notice Dual migration mode
    MigrationMode constant DUAL = MigrationMode.wrap(2);
}

