// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.30;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

abstract contract InsuranceBaseProvider is
  Initializable,
  OwnableUpgradeable,
  PausableUpgradeable,
  ReentrancyGuardUpgradeable
{
  /**
   * @dev Event emitted when a policy is solid
   *
   * Due to the nature of the different implementations of insurance providers, event emission of policy sold is difficult to normalise
   * Instead, we will take a prescriptive approach with the expectation that the event will be emitted when a policy is sold
   *
   * 1. The event will be named in the following format: CoverPurchased_{provider}
   * 2. The event will be emitted with the following minimal and non-exhaustive properties:
   *      - [indexed] productId: The ID of the product/cover as defined by the provider
   *      - [indexed] addressCovered: The address that is covered by this policy
   *      - durationCovered: the number of days the cover is valid for
   *      - amountCovered: The amount covered
   *      - premiumAmount: The amount of premium paid for this policy
   *      - referralCode: The referral code used for this purchase
   *
   *  - Note that the event format leaves a single indexed property unused for flexibility to the implementing provider
   */
  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor() {
    _disableInitializers();
  }

  function pauseAll() external onlyOwner whenNotPaused {
    _pause();
  }

  function unPauseAll() external onlyOwner whenPaused {
    _unpause();
  }
}

