pragma solidity ^0.8.30;

import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "./InsuranceBaseProvider.sol";
import "./interfaces/insurace/product/IProduct.sol";
import "./interfaces/insurace/cover/ICover.sol";
import "./interfaces/insurace/cover/ICoverData.sol";

contract NautilusInsurace is InsuranceBaseProvider {
  using SafeERC20Upgradeable for IERC20Upgradeable;
  using SafeMathUpgradeable for uint256;
  using AddressUpgradeable for address;

  address private coverAddress;
  ICover private insuraceCover;

  address public product;

  // Events
  event CoverPurchased_Insurace(
    uint16 indexed productId,
    address indexed addressCovered,
    uint256 durationCovered,
    uint256 amountCovered,
    uint256 referralCode,
    uint256 premiumAmount
  );

  // Initialise function
  function initialize(address _coverAddress) external initializer {
    __Ownable_init();
    __Pausable_init();
    require(_coverAddress != address(0), "The cover address cannot be zero address.");

    coverAddress = _coverAddress;
    insuraceCover = ICover(coverAddress);
    product = insuraceCover.product();
  }

  function buyCovers(
    uint16[] calldata products,
    uint16[] calldata durationInDays,
    uint256[] calldata amounts,
    address[] calldata addresses,
    uint256 premiumAmount,
    uint256 referralCode,
    uint256[] calldata helperParameters,
    uint256[] calldata securityParameters,
    string calldata freeText,
    uint8[] calldata v,
    bytes32[] calldata r,
    bytes32[] calldata s
  ) public payable whenNotPaused nonReentrant {
    insuraceCover.buyCoverV3{value: msg.value}(
      products,
      durationInDays,
      amounts,
      addresses,
      premiumAmount,
      referralCode,
      helperParameters,
      securityParameters,
      freeText,
      v,
      r,
      s
    );

    // Emit event for each cover
    uint256 coverCount = products.length;
    for (uint i; i < coverCount; ) {
      emit CoverPurchased_Insurace(
        products[i],
        addresses[i],
        durationInDays[i],
        amounts[i],
        referralCode,
        premiumAmount
      );
      unchecked {
        i++;
      }
    }
  }

  function getProductCount() public view returns (uint256) {
    return IProduct(product).getProductCount();
  }

  function getProductDetails(
    uint256 productId
  ) public view returns (bytes32, bytes32, bytes32, uint256, uint256, uint256, bool) {
    return IProduct(product).getProductDetails(productId);
  }

  function getCoverCount(address ownerAddress) public view returns (uint256) {
    address coverData = insuraceCover.data();
    uint256 coverCount = ICoverData(coverData).getCoverCount(ownerAddress);

    return coverCount;
  }

  function getCoverInfo(
    address owner,
    uint256 coverId
  ) public view returns (uint256, uint256, uint256, uint256, address) {
    ICoverData coverData = ICoverData(insuraceCover.data());
    uint256 coverStatus = coverData.getAdjustedCoverStatus(owner, coverId);
    uint256 coverBeginTime = coverData.getCoverBeginTimestamp(owner, coverId);
    uint256 coverEndTime = coverData.getCoverEndTimestamp(owner, coverId);
    uint256 coverAmount = coverData.getCoverAmount(owner, coverId);
    address coverCurrency = coverData.getCoverCurrency(owner, coverId);

    return (coverStatus, coverBeginTime, coverEndTime, coverAmount, coverCurrency);
  }
}
