contracts/LayerrBridge.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.30;

import {IERC20} from "./interfaces/IERC20.sol";
import {IV3SwapRouter} from "./interfaces/IV3SwapRouter.sol";
import {IStargateRouter} from "./interfaces/IStargateRouter.sol";
import {ILayerrBridge} from "./interfaces/ILayerrBridge.sol";
import {IWETH} from "./interfaces/IWETH.sol";
import {MintOrder} from "./lib/MinterStructs.sol";
import {ILayerrMinter} from "./interfaces/ILayerrMinter.sol";

/**
 * @title LayerrBridge
 * @author 0xth0mas (Layerr)
 * @notice LayerrBridge is powered by Stargate Finance and LayerZero
 *         to allow crosschain minting of NFTs with the LayerrMinter
 *         contract and native-to-native bridging of tokens.
 */
contract LayerrBridge is ILayerrBridge {

    /// @dev Layerr-owned account 
    address public owner = 0x0000000000799dfE79Ed462822EC68eF9a6199e6;
    /// @dev LayerrMinter interface
    ILayerrMinter public constant layerrMinter = ILayerrMinter(0x000000000000D58696577347F78259bD376F1BEC);
    /// @dev Stargate router contract interface for cross-chain bridging
    IStargateRouter public stargateRouter;
    /// @dev Automatic market maker router interface for local token swaps
    IV3SwapRouter public ammRouter;
    /// @dev WETH9 contract
    IWETH public weth;
    /// @dev Stargate native token contract
    IWETH public sgNative;

    /// @dev Layerr transaction fee in BPS
    uint256 private constant LAYERR_BPS = 25;
    /// @dev Denominator for BPS calculations
    uint256 private constant BPS_DENOMINATOR = 10_000;
    /// @dev Stargate transaction type for gas estimation
    uint8 private constant TYPE_SWAP_REMOTE = 1;

    modifier onlyOwner() {
        if (msg.sender != owner) {
            revert NotContractOwner();
        }
        _;
    }

    constructor() {
        _setInitialRouterAddresses();
    }

    /**
     * @inheritdoc ILayerrBridge
     */
    function remoteMintWithLocalNative(
        uint16 dstChainId,
        address bridgeToken,
        uint16 srcPoolId,
        uint16 dstPoolId,
        uint256 gasProvided,
        uint256 localSwapAmountOutMin,
        uint256 remoteSwapAmountOutMin,
        uint256 remoteGasUnits,
        MintOrder[] calldata mintOrders,
        bytes32 bridgeTrackingHash
    ) external payable {
        if(gasProvided == 0 || gasProvided > msg.value) revert InsufficientGas();
        uint256 swapValueIn;
        uint256 layerrFee;
        unchecked {
            swapValueIn = msg.value - gasProvided;
            layerrFee = swapValueIn * LAYERR_BPS / BPS_DENOMINATOR;
            swapValueIn -= layerrFee;
        }

        unchecked {
            _transferNative(owner, layerrFee);
        }

        uint256 bridgeAmount;
        if(bridgeToken == address(sgNative)) {
            bridgeAmount = swapValueIn;
            sgNative.deposit{value: swapValueIn}();
        } else {
            bridgeAmount = _swapNativeForTokens(bridgeToken, swapValueIn);
        }
        IERC20(bridgeToken).approve(address(stargateRouter), bridgeAmount);

        // encode payload data to send to destination contract, which it will handle with sgReceive()
        bytes memory data = abi.encode(remoteSwapAmountOutMin, mintOrders, msg.sender, bridgeTrackingHash);

        // Stargate's Router.swap() function sends the tokens to the destination chain.
        stargateRouter.swap{value:gasProvided}(
            dstChainId,
            srcPoolId,
            dstPoolId,
            payable(msg.sender),
            bridgeAmount,
            localSwapAmountOutMin,
            IStargateRouter.lzTxObj(remoteGasUnits, 0, "0x"),
            abi.encodePacked(address(this)),
            data
        );
    }

    /**
     * @inheritdoc ILayerrBridge
     */
    function swapNativeForNative(
        uint16 dstChainId,
        address bridgeToken,
        uint16 srcPoolId,
        uint16 dstPoolId,
        uint256 gasProvided,
        uint256 localSwapAmountOutMin,
        uint256 remoteSwapAmountOutMin,
        uint256 remoteGasUnits,
        bytes32 bridgeTrackingHash
    ) external payable {
        if(gasProvided == 0 || gasProvided > msg.value) revert InsufficientGas();
        uint256 swapValueIn;
        uint256 layerrFee;
        unchecked {
            swapValueIn = msg.value - gasProvided;
            layerrFee = swapValueIn * LAYERR_BPS / BPS_DENOMINATOR;
            swapValueIn -= layerrFee;
        }

        unchecked {
            _transferNative(owner, layerrFee);
        }

        uint256 bridgeAmount;
        if(bridgeToken == address(sgNative)) {
            bridgeAmount = swapValueIn;
            sgNative.deposit{value: swapValueIn}();
        } else {
            bridgeAmount = _swapNativeForTokens(bridgeToken, swapValueIn);
        }
        IERC20(bridgeToken).approve(address(stargateRouter), bridgeAmount);

        // encode payload data to send to destination contract, which it will handle with sgReceive()
        bytes memory data = abi.encode(remoteSwapAmountOutMin, msg.sender, bridgeTrackingHash);

        // Stargate's Router.swap() function sends the tokens to the destination chain.
        stargateRouter.swap{value:gasProvided}(
            dstChainId,
            srcPoolId,
            dstPoolId,
            payable(msg.sender),
            bridgeAmount,
            localSwapAmountOutMin,
            IStargateRouter.lzTxObj(remoteGasUnits, 0, "0x"),
            abi.encodePacked(address(this)),
            data
        );
    }

    /**
     * @inheritdoc ILayerrBridge
     */
    function estimateMintAndBridgeGas(
        uint16 dstChainId, 
        uint256 remoteSwapAmountOutMin, 
        uint256 remoteGasUnits, 
        MintOrder[] calldata mintOrders
    ) external view returns(uint256) {
        bytes memory data = abi.encode(remoteSwapAmountOutMin, mintOrders, msg.sender, keccak256(""));

        (uint256 gasAmount, ) = stargateRouter.quoteLayerZeroFee(
            dstChainId, 
            TYPE_SWAP_REMOTE, 
            abi.encodePacked(address(this)), 
            data, 
            IStargateRouter.lzTxObj(remoteGasUnits, 0, "0x")
        );
        return gasAmount;
    }

    /**
     * @inheritdoc ILayerrBridge
     */
    function estimateBridgeOnlyGas(
        uint16 dstChainId, 
        uint256 remoteSwapAmountOutMin, 
        uint256 remoteGasUnits
    ) external view returns(uint256) {
        bytes memory data = abi.encode(remoteSwapAmountOutMin, msg.sender, keccak256(""));

        (uint256 gasAmount, ) = stargateRouter.quoteLayerZeroFee(
            dstChainId, 
            TYPE_SWAP_REMOTE, 
            abi.encodePacked(address(this)), 
            data, 
            IStargateRouter.lzTxObj(remoteGasUnits, 0, "0x")
        );
        return gasAmount;
    }

    /**
     * @notice Receive the tokens and payload from the Stargate Route
     * @dev Swap/bridge-only transactions will have a 64-byte payload
     *      Swap/bridge/mint transaction payloads will be greater than 64 bytes
     */
    function sgReceive(
        uint16, 
        bytes memory, 
        uint256, 
        address tokenAddress, 
        uint256 amountFromStargate, 
        bytes memory payload
    ) override external {
        if(msg.sender != address(stargateRouter)) revert NotStargateRouter();

        uint256 nativeBalanceStart = address(this).balance;

        uint256 swapAmountOutMin;
        MintOrder[] memory mintOrders;
        address mintTo;
        bool swapAndMint;
        bytes32 bridgeTrackingHash;
        if(payload.length == 96) {
            (swapAmountOutMin, mintTo, bridgeTrackingHash) = abi.decode(payload, (uint256, address, bytes32));
        } else {
            (swapAmountOutMin, mintOrders, mintTo, bridgeTrackingHash) = abi.decode(payload, (uint256, MintOrder[], address, bytes32));
            swapAndMint = true;
        }

        uint256 amountOut;
        if(tokenAddress == address(sgNative)) {
            //withdraw native token if balance > 0, amountOut = amountFromStargate
            //future-proof if bridge is added to sgNative's noUnwrapTo
            uint256 sgNativeBalance = sgNative.balanceOf(address(this));
            if(sgNativeBalance > 0) {
                try sgNative.withdraw(sgNativeBalance) { } catch { }
            }
            amountOut = amountFromStargate;
            nativeBalanceStart = address(this).balance - amountFromStargate;
        } else {
            //non-native token swap through AMM
            amountOut = _swapTokensForNative(tokenAddress, amountFromStargate, swapAmountOutMin) - nativeBalanceStart;
        }
        if(amountOut == 0) {
            //swap failed, send tokens to minter, skip minting
            try IERC20(tokenAddress).transfer(mintTo, amountFromStargate) {
                emit RemoteSwapUnsuccessful(mintTo, tokenAddress, amountFromStargate, bridgeTrackingHash);
            } catch {
                emit RemoteSwapAndTransferUnsuccessful(mintTo, tokenAddress, amountFromStargate, bridgeTrackingHash);
            }
        } else {
            //swap successful, attempt to mint if mintOrder provided, only supply value from swap
            if(swapAndMint) {
                try layerrMinter.mintBatchTo{value: amountOut}(mintTo, mintOrders, 0) {
                    emit RemoteMintSuccessful(mintTo, bridgeTrackingHash);
                } catch {
                    emit RemoteMintUnsuccessful(mintTo, bridgeTrackingHash);
                }   
            }
        }

        //transfer remaining native tokens to minter
        uint256 nativeBalanceRemaining = address(this).balance;
        if(nativeBalanceRemaining > nativeBalanceStart) {
            _transferNative(mintTo, (nativeBalanceRemaining - nativeBalanceStart));
        }
    }

    /**
     * @inheritdoc ILayerrBridge
     */
    function setRouterAddresses(
        address _stargateRouter, 
        address _ammRouter, 
        address _sgNative
    ) external onlyOwner {
        //only allow update if address was not previously set by 
        //constructor or this function
        if(address(stargateRouter) == address(0)) {
            stargateRouter = IStargateRouter(_stargateRouter);
        }
        if(address(ammRouter) == address(0)) {
            ammRouter = IV3SwapRouter(_ammRouter);
        }
        if(address(sgNative) == address(0)) {
            sgNative = IWETH(_sgNative);
        }
        if(address(weth) == address(0)) {
            weth = IWETH(ammRouter.WETH9());
        }
    }

    /**
     * @dev Swaps `amountIn` of native token for `tokenAddress`
     *      We do not check for minimum output here. Minimum output
     *      is enforced through the Stargate swap.
     * @param tokenAddress the address of the token to swap for
     * @param amountIn the amount of native token to swap
     */
    function _swapNativeForTokens(
        address tokenAddress,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        amountOut = ammRouter.exactInputSingle{value: amountIn}(
            IV3SwapRouter.ExactInputSingleParams({
                tokenIn: address(weth),
                tokenOut: tokenAddress,
                fee: 500,
                recipient: address(this),
                amountIn: amountIn,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
    }


    /**
     * @dev Swaps contract's balance of `tokenAddress` for chain native
     *      token. Enforces `amountOutMin` as minimum tokens to receive.
     *      Native token is received from AMM as WETH and unwrapped.
     * @param tokenAddress the address of the token to swap from
     * @param amountFromStargate the amount of tokens received from Stargate
     * @param amountOutMin the minimum amount of native tokens to receive
     * @return amountOut new native account balance
     */
    function _swapTokensForNative(
        address tokenAddress,
        uint256 amountFromStargate,
        uint256 amountOutMin
    ) internal returns (uint256 amountOut) {
        //prevent revert, contract will forward tokens if amountOut = 0
        try IERC20(tokenAddress).approve(address(ammRouter), amountFromStargate) { } catch { } 

        try ammRouter.exactInputSingle(
            IV3SwapRouter.ExactInputSingleParams({
                tokenIn: tokenAddress,
                tokenOut: address(weth),
                fee: 500,
                recipient: address(this),
                amountIn: amountFromStargate,
                amountOutMinimum: amountOutMin,
                sqrtPriceLimitX96: 0
            })
        ) {
            try weth.withdraw(weth.balanceOf(address(this))) { } catch { }
        } catch { }
        amountOut = address(this).balance;
    }

    /**
     * @notice Transfers `amount` of native token to `to` address. Reverts if the transfer fails.
     * @param to address to send native token to
     * @param amount amount of native token to send
     */
    function _transferNative(address to, uint256 amount) internal {
        (bool sent, ) = payable(to).call{value: amount}("");
        if (!sent) {
            if(address(this).balance < amount) {
                revert InsufficientBalance();
            } else {
                revert PaymentFailed();
            }
        }
    }

    /**
     * @dev preset addresses for initial deployment of contracts via CREATE2
     */
    function _setInitialRouterAddresses() internal {
        if(block.chainid == 1) { //Ethereum mainnet
            stargateRouter = IStargateRouter(0x8731d54E9D02c286767d56ac03e8037C07e01e98);
            ammRouter = IV3SwapRouter(0x68b3465833fb72A70ecDF485E0e4C7bD8665Fc45);
            sgNative = IWETH(0x72E2F4830b9E45d52F80aC08CB2bEC0FeF72eD9c);
        } else if(block.chainid == 56) { //BSC
            stargateRouter = IStargateRouter(0x4a364f8c717cAAD9A442737Eb7b8A55cc6cf18D8);
            ammRouter = IV3SwapRouter(0xB971eF87ede563556b2ED4b1C0b0019111Dd85d2);
        } else if(block.chainid == 42161) { //Arbitrum
            stargateRouter = IStargateRouter(0x53Bf833A5d6c4ddA888F69c22C88C9f356a41614);
            ammRouter = IV3SwapRouter(0x68b3465833fb72A70ecDF485E0e4C7bD8665Fc45);
            sgNative = IWETH(0x82CbeCF39bEe528B5476FE6d1550af59a9dB6Fc0);
        } else if(block.chainid == 10) { //Optimism
            stargateRouter = IStargateRouter(0xB0D502E938ed5f4df2E681fE6E419ff29631d62b);
            ammRouter = IV3SwapRouter(0x68b3465833fb72A70ecDF485E0e4C7bD8665Fc45);
            sgNative = IWETH(0xb69c8CBCD90A39D8D3d3ccf0a3E968511C3856A0);
        } else if(block.chainid == 43114) { //Avalanche
            stargateRouter = IStargateRouter(0x45A01E4e04F14f7A4a6702c74187c5F6222033cd);
            ammRouter = IV3SwapRouter(0xbb00FF08d01D300023C629E8fFfFcb65A5a578cE);
        } else if(block.chainid == 137) { //Polygon
            stargateRouter = IStargateRouter(0x45A01E4e04F14f7A4a6702c74187c5F6222033cd);
            ammRouter = IV3SwapRouter(0x68b3465833fb72A70ecDF485E0e4C7bD8665Fc45);
        } else if(block.chainid == 8453) { //Base
            stargateRouter = IStargateRouter(0x45f1A95A4D3f3836523F5c83673c797f4d4d263B);
            ammRouter = IV3SwapRouter(0x2626664c2603336E57B271c5C0b26F421741e481);
            sgNative = IWETH(0x224D8Fd7aB6AD4c6eb4611Ce56EF35Dec2277F03);
        }
        if(address(ammRouter) != address(0)) {
            weth = IWETH(ammRouter.WETH9());
        }
    }
    
    receive() external payable {}
    fallback() external payable {}
}

contracts/interfaces/ILayerrBridge.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.30;

import {IStargateReceiver} from "./IStargateReceiver.sol";
import {MintOrder} from "../lib/MinterStructs.sol";

/**
 * @title ILayerrBridge
 * @author 0xth0mas (Layerr)
 * @notice ILayerrBridge interface defines functions, events and errors for the LayerrBridge
 */
interface ILayerrBridge is IStargateReceiver {

    /// @dev thrown when an address that is not the contract owner attempts to update routers
    error NotContractOwner();
    /// @dev thrown when attempting to send more value than the contract holds
    error InsufficientBalance();
    /// @dev thrown when not enough gas is supplied for the bridge transaction
    error InsufficientGas();
    /// @dev thrown when there is an error sending payment
    error PaymentFailed();
    /// @dev thrown when an address that is not the Stargate Router calls sgReceive
    error NotStargateRouter();

    /// @dev emitted when a swap is unsuccessful on remote chain and tokens are forwarded to recipient
    event RemoteSwapUnsuccessful(address indexed to, address tokenAddress, uint256 amount, bytes32 indexed remoteTxHash);
    /// @dev emitted when a swap is unsuccessful on remote chain and tokens could not be forwarded
    event RemoteSwapAndTransferUnsuccessful(address indexed to, address tokenAddress, uint256 amount, bytes32 indexed remoteTxHash);
    /// @dev emitted when a mint is successful
    event RemoteMintSuccessful(address indexed to, bytes32 indexed remoteTxHash);
    /// @dev emitted when a mint was not successful
    event RemoteMintUnsuccessful(address indexed to, bytes32 indexed remoteTxHash);

    /**
     * @notice Initiates a swap, bridge and mint transaction
     * @dev Transaction will revert if we do not receive the localSwapAmountOutMin on local chain
     *      Transaction will NOT revert if remote chain is below remoteSwapAmountOutMin but will not
     *      swap the token on the remote chain and instead forward the token balance to the recipient.
     * @param dstChainId destination chain ID from LayerZero/Stargate
     * @param bridgeToken token address for the token that will be bridged
     * @param srcPoolId source pool ID from LayerZero/Stargate
     * @param dstPoolId destination pool ID from LayerZero/Stargate
     * @param gasProvided amount of gas in wei to be sent to Stargate for LayerZero transaction
     * @param localSwapAmountOutMin minimum amount of tokens to receive after all local swaps
     * @param remoteSwapAmountOutMin minimum amount of tokens to receive on remote chain
     * @param remoteGasUnits amount of gas units to pay for on the remote chain
     * @param mintOrders the mint orders to be executed on the remote chain
     * @param bridgeTrackingHash value supplied by dapp to track bridge transactions 
     */
    function remoteMintWithLocalNative(
        uint16 dstChainId,
        address bridgeToken,
        uint16 srcPoolId,
        uint16 dstPoolId,
        uint256 gasProvided,
        uint256 localSwapAmountOutMin,
        uint256 remoteSwapAmountOutMin,
        uint256 remoteGasUnits,
        MintOrder[] calldata mintOrders,
        bytes32 bridgeTrackingHash
    ) external payable;

    /**
     * @notice Initiates a swap and bridge transaction
     * @dev Transaction will revert if we do not receive the localSwapAmountOutMin on local chain
     *      Transaction will NOT revert if remote chain is below remoteSwapAmountOutMin but will not
     *      swap the token on the remote chain and instead forward the token balance to the recipient.
     * @param dstChainId destination chain ID from LayerZero/Stargate
     * @param bridgeToken token address for the token that will be bridged
     * @param srcPoolId source pool ID from LayerZero/Stargate
     * @param dstPoolId destination pool ID from LayerZero/Stargate
     * @param gasProvided amount of gas in wei to be sent to Stargate for LayerZero transaction
     * @param localSwapAmountOutMin minimum amount of tokens to receive after all local swaps
     * @param remoteSwapAmountOutMin minimum amount of tokens to receive on remote chain
     * @param remoteGasUnits amount of gas units to pay for on the remote chain
     * @param bridgeTrackingHash value supplied by dapp to track bridge transactions
     */
    function swapNativeForNative(
        uint16 dstChainId,
        address bridgeToken,
        uint16 srcPoolId,
        uint16 dstPoolId,
        uint256 gasProvided,
        uint256 localSwapAmountOutMin,
        uint256 remoteSwapAmountOutMin,
        uint256 remoteGasUnits,
        bytes32 bridgeTrackingHash
    ) external payable;

    /**
     * @notice Estimate the gas payment to LayerZero for the a swap/bridge/mint transaction
     * @param dstChainId destination chain ID from LayerZero/Stargate
     * @param remoteSwapAmountOutMin minimum amount of tokens to receive on remote chain
     * @param remoteGasUnits amount of gas units to pay for on the remote chain
     * @param mintOrders the mint orders to be executed on the remote chain
     */
    function estimateMintAndBridgeGas(
        uint16 dstChainId, 
        uint256 remoteSwapAmountOutMin, 
        uint256 remoteGasUnits, 
        MintOrder[] calldata mintOrders
    ) external view returns(uint256);

    
    /**
     * @notice Estimate the gas payment to LayerZero for the a swap/bridge transaction
     * @param dstChainId destination chain ID from LayerZero/Stargate
     * @param remoteSwapAmountOutMin minimum amount of tokens to receive on remote chain
     * @param remoteGasUnits amount of gas units to pay for on the remote chain
     */
    function estimateBridgeOnlyGas(
        uint16 dstChainId, 
        uint256 remoteSwapAmountOutMin, 
        uint256 remoteGasUnits
    ) external view returns(uint256);

    /**
     * @notice Sets the router and Stargate native token addresses 
     * @dev access restricted to contract owner
     * @param _stargateRouter the local Stargate router contract for bridging
     * @param _ammRouter the router contract to use for local token swaps
     * @param _sgNative the Stargate native token contract
     */
    function setRouterAddresses(
        address _stargateRouter,
        address _ammRouter,
        address _sgNative
    ) external;
}

contracts/interfaces/ILayerrMinter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {MintOrder, MintParameters, MintToken, BurnToken, PaymentToken} from "../lib/MinterStructs.sol";

/**
 * @title ILayerrMinter
 * @author 0xth0mas (Layerr)
 * @notice ILayerrMinter interface defines functions required in the LayerrMinter to be callable by token contracts
 */
interface ILayerrMinter {

    /// @dev Event emitted when a mint order is fulfilled
    event MintOrderFulfilled(
        bytes32 indexed mintParametersDigest,
        address indexed minter,
        uint256 indexed quantity
    );

    /// @dev Event emitted when a token contract updates an allowed signer for EIP712 signatures
    event ContractAllowedSignerUpdate(
        address indexed _contract,
        address indexed _signer,
        bool indexed _allowed
    );

    /// @dev Event emitted when a token contract updates an allowed oracle signer for offchain authorization of a wallet to use a signature
    event ContractOracleUpdated(
        address indexed _contract,
        address indexed _oracle,
        bool indexed _allowed
    );

    /// @dev Event emitted when a signer updates their nonce with LayerrMinter. Updating a nonce invalidates all previously signed EIP712 signatures.
    event SignerNonceIncremented(
        address indexed _signer,
        uint256 indexed _nonce
    );

    /// @dev Event emitted when a specific signature's validity is updated with the LayerrMinter contract.
    event SignatureValidityUpdated(
        address indexed _contract,
        bool indexed invalid,
        bytes32 mintParametersDigests
    );

    /// @dev Thrown when the amount of native tokens supplied in msg.value is insufficient for the mint order
    error InsufficientPayment();

    /// @dev Thrown when a payment fails to be forwarded to the intended recipient
    error PaymentFailed();

    /// @dev Thrown when a MintParameters payment token uses a token type value other than native or ERC20
    error InvalidPaymentTokenType();

    /// @dev Thrown when a MintParameters burn token uses a token type value other than ERC20, ERC721 or ERC1155
    error InvalidBurnTokenType();

    /// @dev Thrown when a MintParameters mint token uses a token type value other than ERC20, ERC721 or ERC1155
    error InvalidMintTokenType();

    /// @dev Thrown when a MintParameters burn token uses a burn type value other than contract burn or send to dead
    error InvalidBurnType();

    /// @dev Thrown when a MintParameters burn token requires a specific burn token id and the tokenId supplied does not match
    error InvalidBurnTokenId();

    /// @dev Thrown when a MintParameters burn token requires a specific ERC721 token and the burn amount is greater than 1
    error CannotBurnMultipleERC721WithSameId();

    /// @dev Thrown when attempting to mint with MintParameters that have a start time greater than the current block time
    error MintHasNotStarted();

    /// @dev Thrown when attempting to mint with MintParameters that have an end time less than the current block time
    error MintHasEnded();

    /// @dev Thrown when a MintParameters has a merkleroot set but the supplied merkle proof is invalid
    error InvalidMerkleProof();

    /// @dev Thrown when a MintOrder will cause a token's minted supply to exceed the defined maximum supply in MintParameters
    error MintExceedsMaxSupply();

    /// @dev Thrown when a MintOrder will cause a minter's minted amount to exceed the defined max per wallet in MintParameters
    error MintExceedsMaxPerWallet();

    /// @dev Thrown when a MintParameters mint token has a specific ERC721 token and the mint amount is greater than 1
    error CannotMintMultipleERC721WithSameId();

    /// @dev Thrown when the recovered signer for the MintParameters is not an allowed signer for the mint token
    error NotAllowedSigner();

    /// @dev Thrown when the recovered signer's nonce does not match the current nonce in LayerrMinter
    error SignerNonceInvalid();

    /// @dev Thrown when a signature has been marked as invalid for a mint token contract
    error SignatureInvalid();

    /// @dev Thrown when MintParameters requires an oracle signature and the recovered signer is not an allowed oracle for the contract
    error InvalidOracleSignature();

    /// @dev Thrown when MintParameters has a max signature use set and the MintOrder will exceed the maximum uses
    error ExceedsMaxSignatureUsage();

    /// @dev Thrown when attempting to increment nonce on behalf of another account and the signature is invalid
    error InvalidSignatureToIncrementNonce();

    /**
     * @notice This function is called by token contracts to update allowed signers for minting
     * @param _signer address of the EIP712 signer
     * @param _allowed if the `_signer` is allowed to sign for minting
     */
    function setContractAllowedSigner(address _signer, bool _allowed) external;

    /**
     * @notice This function is called by token contracts to update allowed oracles for offchain authorizations
     * @param _oracle address of the oracle
     * @param _allowed if the `_oracle` is allowed to sign offchain authorizations
     */
    function setContractAllowedOracle(address _oracle, bool _allowed) external;

    /**
     * @notice This function is called by token contracts to update validity of signatures for the LayerrMinter contract
     * @dev `invalid` should be true to invalidate signatures, the default state of `invalid` being false means 
     *      a signature is valid for a contract assuming all other conditions are met
     * @param mintParametersDigests an array of message digests for MintParameters to update validity of
     * @param invalid if the supplied digests will be marked as valid or invalid
     */
    function setSignatureValidity(
        bytes32[] calldata mintParametersDigests,
        bool invalid
    ) external;

    /**
     * @notice Increments the nonce for a signer to invalidate all previous signed MintParameters
     */
    function incrementSignerNonce() external;

    /**
     * @notice Increments the nonce on behalf of another account by validating a signature from that account
     * @dev The signature is an eth personal sign message of the current signer nonce plus the chain id
     *      ex. current nonce 0 on chain 5 would be a signature of \x19Ethereum Signed Message:\n15
     *          current nonce 50 on chain 1 would be a signature of \x19Ethereum Signed Message:\n251
     * @param signer account to increment nonce for
     * @param signature signature proof that the request is coming from the account
     */
    function incrementNonceFor(address signer, bytes calldata signature) external;

    /**
     * @notice Validates and processes a single MintOrder, tokens are minted to msg.sender
     * @param mintOrder struct containing the details of the mint order
     */
    function mint(
        MintOrder calldata mintOrder
    ) external payable;

    /**
     * @notice Validates and processes an array of MintOrders, tokens are minted to msg.sender
     * @param mintOrders array of structs containing the details of the mint orders
     */
    function mintBatch(
        MintOrder[] calldata mintOrders
    ) external payable;

    /**
     * @notice Validates and processes a single MintOrder, tokens are minted to `mintToWallet`
     * @param mintToWallet the address tokens will be minted to
     * @param mintOrder struct containing the details of the mint order
     * @param paymentContext Contextual information related to the payment process
     *                     (Note: This parameter is required for integration with 
     *                     the payment processor and does not impact the behavior 
     *                     of the function)
     */
    function mintTo(
        address mintToWallet,
        MintOrder calldata mintOrder,
        uint256 paymentContext
    ) external payable;

    /**
     * @notice Validates and processes an array of MintOrders, tokens are minted to `mintToWallet`
     * @param mintToWallet the address tokens will be minted to
     * @param mintOrders array of structs containing the details of the mint orders
     * @param paymentContext Contextual information related to the payment process
     *                     (Note: This parameter is required for integration with 
     *                     the payment processor and does not impact the behavior 
     *                     of the function)
     */
    function mintBatchTo(
        address mintToWallet,
        MintOrder[] calldata mintOrders,
        uint256 paymentContext
    ) external payable;
}

