pragma solidity ^0.8.30;

interface IBSKT {
    function initialize(
        string calldata _name,
        string calldata _symbol,
        address _owner,
        address _factoryAddress,
        address[] calldata _tokens,
        uint256[] calldata _weights,
        address _bsktPair,
        string calldata _tokenURI,
        string calldata _id,
        string calldata _description
    ) external;

    function contribute(uint256[] calldata minAmountsOut, bytes calldata signature, uint256 _deadline) external payable;
    function withdraw(uint256 _liquidity) external;
    function withdrawETH(uint256 _liquidity, uint256[] calldata _minAmountsOutFee, bytes calldata _signatureFee, uint256[] calldata _minAmountsOut, bytes calldata _signature, uint256 _deadline) external;
    function rebalance(address[] calldata _newTokens, uint256[] calldata _newWeights, uint256[] calldata _minAmountsOutnewPrev, bytes calldata _signaturenewPrev, uint256[] calldata _minAmountsOutnew, bytes calldata _signaturenew, uint256 _deadline) external;
    function emergencyStable(address[] calldata _newTokens, uint256[] calldata _newWeights, uint256[] calldata _minAmountsOutnewPrev, bytes calldata _signaturenewPrev, uint256[] calldata _minAmountsOutnew, bytes calldata _signaturenew, uint256 _deadline) external;
    function claimFee(uint256 amount, uint256[] calldata _minAmountsOut, bytes calldata _signature, uint256 _deadline, bool withdrawIndividualTokens, bool withdrawFullAmount) external;
    function getTokenDetails(uint256 _index) external view returns (address token, uint256 weight);
    function getTokenDetails() external view returns (address[] memory tokens, uint256[] memory weights);
    function totalTokens() external view returns (uint256 tokenLength);
    function getTokenValueByWETH() external view returns (uint256 value);
    function contractURI() external view returns (string memory);
    function supportsInterface(bytes4 interfaceId) external view returns (bool);
    function royaltyInfo(uint256, uint256) external view returns (address receiver, uint256 royaltyAmount);
    function getOwner() external view returns (address owner);
}

