        // x_lo]
        (uint256 twos, uint256 twosInv) = _twos(y);

        assembly ("memory-safe") {
            // Divide `y` by the power of two
            ry := div(y, twos)

            // Divide [x_hi x_lo] by the power of two
            rx_hi := div(x_hi, twos)
            rx_lo := or(div(x_lo, twos), mul(x_hi, twosInv))
