contracts/implementations/ZeUSD_CDP.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import '@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import '../interfaces/access/IAccessController.sol';
import '../interfaces/access/IRegistry.sol';
import '../libraries/DataTypes.sol';
import '../utils/AccessChecker.sol';
import '../utils/Constants.sol';
import '../libraries/SystemRoles.sol';
import '../interfaces/IZeUSD_CDP.sol';
import '@openzeppelin/contracts/interfaces/IERC165.sol';

/**
 * @title Deposit NFT
 * @author ZeUSD Protocol Team
 * @notice NFT contract representing user deposits in the protocol
 * @dev Implements UUPS upgradeable pattern with ERC721 extensions
 * @custom:security-contact paras@zoth.io
 *
 * Core Features:
 * - ERC721 token standard
 * - URI storage for metadata
 * - Enumerable extension for tracking
 * - Deposit metadata storage
 * - Role-based access control
 *
 * Security Considerations:
 * - Only router can mint/burn
 * - Admin-controlled URI updates
 * - Protected upgrade mechanism
 * - Token ID uniqueness
 * - Metadata integrity
 *
 * Storage Layout:
 * - Token metadata
 * - Deposit details
 * - URI configuration
 * - Access control state
 *
 * Role Requirements:
 * DEFAULT_ADMIN_ROLE:
 * - Can update base URI
 * - Can upgrade contract
 *
 * UPGRADER_ROLE:
 * - Can perform contract upgrades
 */
contract ZeUSD_CDP is
    ERC721URIStorageUpgradeable,
    ERC721EnumerableUpgradeable,
    UUPSUpgradeable,
    IZeUSD_CDP
{
    /// @notice Access controller contract reference
    /// @dev Used for role checks
    IAccessController public accessController;

    /// @notice Router contract that can mint/burn NFTs
    /// @dev Only address allowed to mint and burn
    address public router;

    /// @notice Base URI for token metadata
    /// @dev Used as prefix for all token URIs
    string public baseURI;

    /// @notice Auto-incrementing token ID counter
    /// @dev Ensures unique token IDs
    uint256 private _tokenIdCounter;

    /// @notice Deposit details mapped to token ID
    /// @dev Stores deposit metadata for each token
    mapping(uint256 => DataTypes.DepositMetadata) public depositDetails;

    /// @notice Next token ID counter
    /// @dev Used to get the next token ID without incrementing
    uint256 private _nextTokenId;

    /**
     * @notice Emitted when a deposit NFT is minted
     * @param tokenId ID of minted token
     * @param owner Address receiving the token
     * @param metadata Associated deposit metadata
     */
    event ZeUSD_CDPMinted(
        uint256 indexed tokenId,
        address indexed owner,
        DataTypes.DepositMetadata metadata
    );

    /**
     * @notice Emitted when a deposit NFT is burned
     * @param tokenId ID of burned token
     * @param owner Last owner of the token
     */
    event ZeUSD_CDPBurned(uint256 indexed tokenId, address indexed owner);

    /**
     * @notice Emitted when base URI is updated
     * @param newBaseURI New base URI value
     */
    event BaseURIUpdated(string newBaseURI);

    /**
     * @notice Emitted when deposit metadata is updated
     * @param tokenId ID of the token whose metadata was updated
     * @param metadata Updated deposit metadata
     */
    event DepositMetadataUpdated(uint256 indexed tokenId, DataTypes.DepositMetadata metadata);

    /**
     * @notice Error thrown for invalid address inputs
     * @param addr The invalid address
     */
    error InvalidAddress(address addr);

    /**
     * @notice Error thrown for unauthorized operations
     * @param message Error description
     */
    error Unauthorized(string message);

    /**
     * @notice Error thrown when token doesn't exist
     * @param tokenId ID of non-existent token
     */
    error TokenNotExists(uint256 tokenId);

    function initialize(
        string memory name,
        string memory symbol,
        string memory initialBaseURI,
        address _router,
        address registryContract
    ) public initializer {
        __ERC721_init(name, symbol);
        __ERC721URIStorage_init();
        __ERC721Enumerable_init();
        __UUPSUpgradeable_init();

        if (_router == address(0)) revert InvalidAddress(_router);
        if (registryContract == address(0)) revert InvalidAddress(registryContract);

        router = _router;
        baseURI = initialBaseURI;
        accessController = IAccessController(
            IRegistry(registryContract).getContract(Constants.CONTRACT_ACCESS_CONTROLLER)
        );
    }

    /**
     * @notice Gets the next token ID without incrementing
     * @return The next token ID that will be used
     */
    function getNextTokenId() external view returns (uint256) {
        return _nextTokenId;
    }

    /**
     * @notice Mints a new deposit NFT
     * @dev Only callable by router
     * @param to Address to mint the NFT to
     * @param metadata Deposit metadata to store
     * @return tokenId The ID of the minted token
     * @custom:security Validates router and recipient
     * @custom:emits ZeUSD_CDPMinted
     */
    function mint(
        address to,
        DataTypes.DepositMetadata calldata metadata
    ) external returns (uint256 tokenId) {
        if (msg.sender != router) revert Unauthorized('Only router can mint');
        if (to == address(0)) revert InvalidAddress(to);

        tokenId = _nextTokenId++;
        _safeMint(to, tokenId);
        depositDetails[tokenId] = metadata;

        emit ZeUSD_CDPMinted(tokenId, to, metadata);
        return tokenId;
    }

    /**
     * @notice Burns a deposit NFT
     * @dev Can be called by router or token owner
     * @param tokenId ID of token to burn
     * @custom:security Validates caller authorization
     * @custom:emits ZeUSD_CDPBurned
     */
    function burn(uint256 tokenId) external {
        if (!_exists(tokenId)) revert TokenNotExists(tokenId);
        if (msg.sender != router && msg.sender != ownerOf(tokenId))
            revert Unauthorized('Not authorized to burn');

        address owner = ownerOf(tokenId);
        _burn(tokenId);
        delete depositDetails[tokenId];

        emit ZeUSD_CDPBurned(tokenId, owner);
    }

    /**
     * @notice Burns a deposit NFT from an user's address
     * @dev Only callable by DEFAULT_ADMIN_ROLE
     * @param tokenId ID of token to burn
     * @param user Address of the attacker
     * @custom:security Validates admin role and token ownership
     * @custom:emits ZeUSD_CDPBurned
     */
    function burnFromUser(uint256 tokenId, address user) external {
        AccessChecker.checkRole(accessController, SystemRoles.DEFAULT_ADMIN_ROLE, msg.sender);

        if (!_exists(tokenId)) revert TokenNotExists(tokenId);
        if (ownerOf(tokenId) != user) revert Unauthorized('Token not owned by user');

        _burn(tokenId);
        delete depositDetails[tokenId];

        emit ZeUSD_CDPBurned(tokenId, user);
    }

    /**
     * @notice Updates the base URI for token metadata
     * @dev Only callable by admin
     * @param newBaseURI New base URI to set
     * @custom:security Requires DEFAULT_ADMIN_ROLE
     * @custom:emits BaseURIUpdated
     */
    function setBaseURI(string memory newBaseURI) external {
        AccessChecker.checkRole(accessController, SystemRoles.DEFAULT_ADMIN_ROLE, msg.sender);
        baseURI = newBaseURI;
        emit BaseURIUpdated(newBaseURI);
    }

    /**
     * @notice Returns the base URI for token metadata
     * @return string Base URI
     */
    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    /**
     * @notice Generates the token URI combining base URI and token ID
     * @param tokenId Token ID to get URI for
     * @return Full token URI
     * @custom:security Validates token existence
     */
    function tokenURI(
        uint256 tokenId
    ) public view override(ERC721Upgradeable, ERC721URIStorageUpgradeable) returns (string memory) {
        if (!_exists(tokenId)) revert TokenNotExists(tokenId);

        string memory baseTokenURI = _baseURI();
        if (bytes(baseTokenURI).length == 0) {
            return '';
        }

        return string(abi.encodePacked(baseTokenURI, _toString(tokenId)));
    }

    /**
     * @notice Gets deposit details for a specific token ID
     * @param tokenId ID of the token to query
     * @return metadata Deposit metadata associated with the token
     * @custom:security Validates token existence
     */
    function getDepositDetails(
        uint256 tokenId
    ) external view override returns (DataTypes.DepositMetadata memory metadata) {
        require(_exists(tokenId), 'Token does not exist');
        return depositDetails[tokenId];
    }

    /**
     * @notice Gets all token IDs owned by a specific address
     * @param owner Address to query tokens for
     * @return Array of token IDs
     */
    function getTokensByOwner(address owner) external view returns (uint256[] memory) {
        uint256 balance = balanceOf(owner);
        uint256[] memory tokenIds = new uint256[](balance);

        for (uint256 i = 0; i < balance; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(owner, i);
        }

        return tokenIds;
    }

    /**
     * @notice Converts uint256 to string
     * @param value Number to convert
     * @return string String representation
     */
    function _toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return '0';
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

    /**
     * @notice Authorizes contract upgrades
     * @dev Only UPGRADER_ROLE can upgrade the contract
     * @custom:security Critical upgrade operation
     */
    function _authorizeUpgrade(address /*newImplementation*/) internal view override {
        AccessChecker.checkRole(accessController, SystemRoles.UPGRADER_ROLE, msg.sender);
    }

    /**
     * @notice Interface support check
     * @dev ERC165 interface detection
     * @param interfaceId Interface identifier to check
     * @return bool True if interface is supported
     */
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(ERC721URIStorageUpgradeable, ERC721EnumerableUpgradeable, IERC165)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @notice Checks if a token exists
     * @param tokenId Token ID to check
     * @return bool True if token exists
     */
    function _exists(uint256 tokenId) internal view returns (bool) {
        try this.ownerOf(tokenId) returns (address) {
            return true;
        } catch {
            return false;
        }
    }

    /**
     * @notice Increases balance for enumerable extension
     * @dev Required override for ERC721Enumerable
     * @param account Address to increase balance for
     * @param amount Amount to increase by
     */
    function _increaseBalance(
        address account,
        uint128 amount
    ) internal virtual override(ERC721Upgradeable, ERC721EnumerableUpgradeable) {
        super._increaseBalance(account, amount);
    }

    /**
     * @notice Updates token ownership
     * @dev Required override for ERC721Enumerable
     * @param to New owner address
     * @param tokenId Token ID to update
     * @param auth Address authorized for transfer
     * @return address Previous owner
     */
    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal virtual override(ERC721Upgradeable, ERC721EnumerableUpgradeable) returns (address) {
        return super._update(to, tokenId, auth);
    }

    /**
     * @notice Sets the router contract address
     * @dev Only callable by DEFAULT_ADMIN_ROLE
     * @param _router New router address
     */
    function setRouter(address _router) external {
        AccessChecker.checkRole(accessController, SystemRoles.DEFAULT_ADMIN_ROLE, msg.sender);
        if (_router == address(0)) revert InvalidAddress(_router);
        router = _router;
    }

    /**
     * @notice Updates deposit metadata for a specific token
     * @dev Only callable by DEFAULT_ADMIN_ROLE
     * @param tokenId ID of the token to update
     * @param params Parameters for updating deposit metadata
     * @custom:security Validates caller authorization and token existence
     * @custom:emits DepositMetadataUpdated
     */
    function updateDepositMetadata(
        uint256 tokenId,
        DataTypes.MetadataUpdateParams calldata params
    ) external {
        AccessChecker.checkRole(accessController, SystemRoles.DEFAULT_ADMIN_ROLE, msg.sender);
        if (!_exists(tokenId)) revert TokenNotExists(tokenId);

        DataTypes.DepositMetadata storage metadata = depositDetails[tokenId];

        if (params.updateIssuer) {
            if (params.issuer == address(0)) revert InvalidAddress(params.issuer);
            metadata.issuer = params.issuer;
        }

        if (params.updateCollateralAddress) {
            if (params.collateralAddress == address(0))
                revert InvalidAddress(params.collateralAddress);
            metadata.collateralAddress = params.collateralAddress;
        }

        if (params.updateAsset) {
            if (params.asset == address(0)) revert InvalidAddress(params.asset);
            metadata.asset = params.asset;
        }

        if (params.updateAmount) {
            metadata.amount = params.amount;
        }

        if (params.updateZeusdMinted) {
            metadata.zeusdMinted = params.zeusdMinted;
        }

        if (params.updateDepositTimestamp) {
            metadata.depositTimestamp = params.depositTimestamp;
        }

        if (params.updateCollateralPrice) {
            metadata.collateralPrice = params.collateralPrice;
        }

        if (params.updateSubVault) {
            if (params.subVault == address(0)) revert InvalidAddress(params.subVault);
            metadata.subVault = params.subVault;
        }

        if (params.updateIntegrationType) {
            metadata.integrationType = params.integrationType;
        }

        if (params.updateTokenType) {
            metadata.tokenType = params.tokenType;
        }

        emit DepositMetadataUpdated(tokenId, metadata);
    }

    /**
     * @notice Updates collateral prices for multiple tokens in batch
     * @dev Only callable by DEFAULT_ADMIN_ROLE
     * @param tokenIds Array of token IDs to update
     * @param collateralPrice New collateral price to set for all tokens
     * @custom:security Validates caller authorization and token existence
     * @custom:emits DepositMetadataUpdated for each token
     */
    function updateBatchCollateralPrices(
        uint256[] calldata tokenIds,
        uint256 collateralPrice
    ) external {
        AccessChecker.checkRole(accessController, SystemRoles.DEFAULT_ADMIN_ROLE, msg.sender);

        for (uint256 i = 0; i < tokenIds.length; i++) {
            uint256 tokenId = tokenIds[i];
            if (!_exists(tokenId)) revert TokenNotExists(tokenId);

            DataTypes.DepositMetadata storage metadata = depositDetails[tokenId];
            metadata.collateralPrice = collateralPrice;

            emit DepositMetadataUpdated(tokenId, metadata);
        }
    }
}

contracts/implementations/ZeUSD.sol.content:
// ZeUSD.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import '@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20BurnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import '../interfaces/IZeUSD.sol';

/// @title ZeUSD Token Implementation
/// @notice Implementation of the ZeUSD stablecoin with upgradeability and access control
/// @dev Implements UUPS upgradeable pattern and OpenZeppelin standard token features
/// @custom:security-contact paras@zoth.io
contract ZeUSD is
    IZeUSD,
    Initializable,
    AccessControlUpgradeable,
    ERC20Upgradeable,
    ERC20BurnableUpgradeable,
    OwnableUpgradeable,
    UUPSUpgradeable
{
    /// @notice Role for admin access
    /// @dev Has authority to manage blacklist and other admin functions
    bytes32 public constant ADMIN_ROLE = keccak256('ADMIN_ROLE');

    /// @notice Router contract address with minting privileges
    /// @dev Critical for token minting control
    /// @custom:security Only trusted addresses should be set as router
    address public router;

    /// @notice Mapping to track blacklisted addresses
    /// @dev true = blacklisted, false = not blacklisted
    /// @custom:security This mapping is critical for compliance and security measures
    mapping(address => bool) private _blacklist;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Initializes the ZeUSD token
    /// @dev Sets up initial state of the contract
    /// @param tokenName Name of the token
    /// @param tokenSymbol Symbol of the token
    /// @param _admin Address of the initial admin
    /// @custom:security Critical initialization function - can only be called once
    function initialize(
        string memory tokenName,
        string memory tokenSymbol,
        address _admin
    ) public initializer {
        if (_admin == address(0)) revert InvalidAddress(_admin);
        __ERC20_init(tokenName, tokenSymbol);
        __Ownable_init(_admin);
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
        _setRoleAdmin(ADMIN_ROLE, DEFAULT_ADMIN_ROLE);
    }

    function decimals() public view virtual override returns (uint8) {
        return 6;
    }

    /// @notice Updates the router address
    /// @dev Only callable by admin
    /// @param newRouter Address of the new router
    /// @custom:security Router has minting privileges - verify carefully
    function setRouter(address newRouter) external override onlyRole(ADMIN_ROLE) {
        if (newRouter == address(0)) revert InvalidAddress(newRouter);
        router = newRouter;
        emit RouterUpdated(newRouter);
    }

    /**
     * @notice Burns tokens from a blacklisted address
     * @dev Only callable by admin
     * @param amount Amount of tokens to burn
     * @param blacklistedUser Address of the blacklisted user to burn from
     * @custom:security Critical function affecting user balances
     */
    function burnFromBlacklist(
        uint256 amount,
        address blacklistedUser
    ) external onlyRole(ADMIN_ROLE) {
        _burn(blacklistedUser, amount);
    }

    /// @notice Mints new tokens
    /// @dev Only callable by router
    /// @param to Recipient of the minted tokens
    /// @param amount Amount of tokens to mint
    /// @custom:security Critical function affecting total supply
    function mint(address to, uint256 amount) public override onlyRouter {
        _mint(to, amount);
    }

    /// @notice Burns tokens from caller
    /// @dev Only callable by router
    /// @param amount Amount of tokens to burn
    /// @custom:security Critical function affecting total supply
    function burn(
        uint256 amount
    ) public virtual override(IZeUSD, ERC20BurnableUpgradeable) onlyRouter {
        _burn(msg.sender, amount);
    }

    /// @notice Burns tokens from specified account
    /// @dev Only callable by router, requires approval
    /// @param account Address to burn from
    /// @param amount Amount of tokens to burn
    /// @custom:security Critical function affecting user balances
    function burnFrom(
        address account,
        uint256 amount
    ) public virtual override(IZeUSD, ERC20BurnableUpgradeable) onlyRouter {
        _spendAllowance(account, msg.sender, amount);
        _burn(account, amount);
    }

    /// @notice Updates token balances
    /// @dev Internal override of ERC20 _update
    /// @param from Address tokens are transferred from
    /// @param to Address tokens are transferred to
    /// @param amount Amount of tokens to transfer
    /// @custom:security Core transfer functionality
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override notBlacklisted(from, to) {
        super._update(from, to, amount);
    }

    /// @notice Sets the blacklist status for an account
    /// @dev Only callable by admin role
    /// @param account Address to update blacklist status for
    /// @param status New blacklist status (true = blacklisted)
    /// @custom:security This function can significantly impact user access
    function setBlacklistStatus(address account, bool status) public override onlyRole(ADMIN_ROLE) {
        _blacklist[account] = status;
        emit Blacklisted(account, status);
    }

    /// @notice Checks if an account is blacklisted
    /// @dev Public view function to check blacklist status
    /// @param account Address to check
    /// @return bool True if account is blacklisted, false otherwise
    /// @custom:security Critical for compliance checks
    function isBlacklisted(address account) public view override returns (bool) {
        return _blacklist[account];
    }

    /// @notice Prevents blacklisted addresses from transferring tokens
    /// @dev Checks both sender and receiver addresses, allows burning for blacklisted users
    /// @param from Address sending tokens
    /// @param to Address receiving tokens
    /// @custom:security Critical modifier for enforcing compliance
    modifier notBlacklisted(address from, address to) {
        // Allow burning (transfer to address(0)) even for blacklisted users
        if (to != address(0)) {
            if (isBlacklisted(from) || isBlacklisted(to))
                revert Unauthorized('ZeUSD: from or to user is blacklisted');
        }
        _;
    }

    /// @notice Restricts access to router address
    /// @dev Throws if router not set or caller is not the router
    /// @custom:security Core modifier for minting/burning functions
    modifier onlyRouter() {
        if (router == address(0)) revert RouterNotSet('ZeUSD: Router address is not set');
        if (msg.sender != router) revert NotRouter('ZeUSD: Only router can call this function');
        _;
    }

    /// @notice Internal function to authorize upgrades
    /// @dev Only callable by owner
    /// @param newImplementation Address of new implementation
    /// @custom:security Critical for contract upgrades
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyRole(DEFAULT_ADMIN_ROLE) {}
}

contracts/interfaces/access/IAccessContr.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import '../../libraries/SystemRoles.sol';

/**
 * @title Access Controller Interface
 * @author ZeUSD Protocol Team
 * @notice Interface for managing role-based access control across the protocol
 * @dev Combines standard role management with custom role configuration
 * @custom:security-contact paras@zoth.io
 */
interface IAccessController {
    /**
     * @notice Checks if an account has a specific role
     * @param role Role identifier to check
     * @param account Account to verify
     * @return bool True if account has the role
     * @dev Core function for role verification
     */
    function hasRole(bytes32 role, address account) external view returns (bool);

    /**
     * @notice Grants a role to an account
     * @param role Role to grant
     * @param account Account to receive the role
     * @dev Only callable by role admin
     */
    function grantRole(bytes32 role, address account) external;

    /**
     * @notice Revokes a role from an account
     * @param role Role to revoke
     * @param account Account to revoke from
     * @dev Only callable by role admin
     */
    function revokeRole(bytes32 role, address account) external;

    /**
     * @notice Gets configuration for a specific role
     * @param role Role identifier
     * @return RoleConfig Configuration struct for the role
     * @dev Returns role settings and constraints
     */
    function getRoleConfig(bytes32 role) external view returns (SystemRoles.RoleConfig memory);

    /**
     * @notice Initializes a new role with configuration
     * @param role Role identifier to initialize
     * @param config Role configuration settings
     * @param adminRole Role that will administer this role
     * @dev Sets up new role with specified parameters
     */
    function initializeRole(
        bytes32 role,
        SystemRoles.RoleConfig memory config,
        bytes32 adminRole
    ) external;

    /**
     * @notice Gets the admin role for a role
     * @param role Role to query
     * @return bytes32 Admin role identifier
     * @dev Returns role that can manage the queried role
     */
    function getRoleAdmin(bytes32 role) external view returns (bytes32);

    /**
     * @notice Sets the admin role for a role
     * @param role Role to modify
     * @param adminRole New admin role
     * @dev Changes which role can manage the specified role
     */
    function setRoleAdmin(bytes32 role, bytes32 adminRole) external;

    /**
     * @notice Gets all members with a specific role
     * @param role Role to query
     * @return address[] Array of addresses with the role
     * @dev Returns complete list of role members
     */
    function getRoleMembers(bytes32 role) external view returns (address[] memory);

    /**
     * @notice Checks if a role has been initialized
     * @param role Role to check
     * @return bool True if role is initialized
     * @dev Verifies role existence and setup
     */
    function isRoleInitialized(bytes32 role) external view returns (bool);
}

