// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.30;

import '../events/ICollateralVaultEvents.sol';
import '../errors/ICollateralVaultErrors.sol';
import '../libraries/DataTypes.sol';

/**
 * @title Collateral Vault Interface
 * @author ZeUSD Protocol Team
 * @notice Main interface for the CollateralVault contract defining all external functions
 * @dev Implements UUPS upgradeable pattern functionality
 */
interface ICollateralVault is ICollateralVaultEvents, ICollateralVaultErrors {
    /**
     * @notice Sets the router contract address
     * @param _router Address of the router contract
     * @dev Can only be set once by admin
     */
    function setRouter(address _router) external;

    /**
     * @notice Registers or updates a subvault configuration
     * @param integrationType Type of integration
     * @param collateralAddress Address of the collateral asset
     * @param subVaultAddress Address of the subvault
     * @param price Price of the collateral
     * @param ltv Loan-to-Value ratio
     * @param isActive Active status of the subvault
     * @param tokenType Token classification
     * @dev Only callable by ADMIN_ROLE
     */
    function registerSubVault(
        string calldata integrationType,
        address collateralAddress,
        address subVaultAddress,
        uint256 price,
        uint256 ltv,
        bool isActive,
        DataTypes.TokenType tokenType
    ) external;

    /**
     * @notice Updates specific parameters of a subvault
     * @param collateralAddress Address of the collateral asset
     * @param params Update parameters struct
     * @dev Only callable by ADMIN_ROLE
     */
    function updateSubVaultConfig(
        address collateralAddress,
        DataTypes.SubVaultUpdateParams calldata params
    ) external;

    /**
     * @notice Calculates the amount of ZeUSD that can be minted for a given collateral amount
     * @param collateralAddress Address of the collateral asset
     * @param asset Address of the specific asset being used
     * @param tokenType Type classification of the token
     * @param amount Amount of collateral being considered
     * @return mintAmount Amount of ZeUSD that can be minted
     */
    function calculateMintAmount(
        address collateralAddress,
        address asset,
        DataTypes.TokenType tokenType,
        uint256 amount
    ) external view returns (uint256 mintAmount);

    /**
     * @notice Retrieves all registered subvaults and their details
     * @return collaterals Array of collateral addresses
     * @return details Array of corresponding CollateralDetails structs
     */
    function getAllSubVaults()
        external
        view
        returns (address[] memory collaterals, DataTypes.CollateralDetails[] memory details);

    /**
     * @notice Retrieves all active subvaults and their details
     * @return collaterals Array of active collateral addresses
     * @return details Array of corresponding CollateralDetails structs
     */
    function getActiveSubVaults()
        external
        view
        returns (address[] memory collaterals, DataTypes.CollateralDetails[] memory details);

    /**
     * @notice Gets the subvault address for a given collateral
     * @param collateralAddress Address of the collateral asset
     * @return subVaultAddress Address of the corresponding subvault
     */
    function getSubVaultAddress(
        address collateralAddress
    ) external view returns (address subVaultAddress);

    /**
     * @notice Gets full configuration details for a collateral's subvault
     * @param collateralAddress Address of the collateral asset
     * @return details Full configuration details struct
     */
    function getSubVaultDetails(
        address collateralAddress
    ) external view returns (DataTypes.CollateralDetails memory details);

    /**
     * @notice Gets count of registered and active subvaults
     * @return total Total number of registered subvaults
     * @return active Number of active subvaults
     */
    function getSubVaultCounts() external view returns (uint256 total, uint256 active);

    /**
     * @notice Removes a subvault registration
     * @param collateralAddress Address of the collateral asset
     * @dev Only callable by ADMIN_ROLE
     */
    function removeSubVault(address collateralAddress) external;

    /**
     * @notice Pauses all vault operations
     * @dev Only callable by ADMIN_ROLE
     */
    function pause() external;

    /**
     * @notice Unpauses vault operations
     * @dev Only callable by ADMIN_ROLE
     */
    function unpause() external;

    /**
     * @notice Validates and prepares deposit parameters
     * @param user Address of the depositor
     * @param collateralAddress Address of the collateral asset
     * @param asset Address of the specific asset being deposited
     * @param amount Amount being deposited
     * @param tokenId ID of the NFT
     * @return metadata Deposit metadata for NFT
     */
    function validateAndPrepareDeposit(
        address user,
        address collateralAddress,
        address asset,
        uint256 amount,
        uint256 tokenId
    ) external view returns (DataTypes.DepositMetadata memory metadata);

    /**
     * @notice Gets user active positions for a specific subvault
     * @param user Address to query positions for
     * @param subVault Subvault to filter by
     * @return metadata Array of active deposit metadata
     */
    function getUserActivePositionsBySubVault(
        address user,
        address subVault
    ) external view returns (DataTypes.DepositMetadata[] memory metadata);
}

contracts/interfaces/ISubVault.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.30;

import '../events/ISubVaultEvents.sol';
import '../errors/ISubVaultErrors.sol';

/**
 * @title Asset Specific SubVault Interface
 * @author ZeUSD Protocol Team
 * @notice Interface for specialized vaults handling primary and secondary assets
 * @dev Implements deposit/withdrawal functionality with primary asset focus
 * @custom:security-contact paras@zoth.io
 */
interface ISubVault is ISubVaultEvents, ISubVaultErrors {
    /// @notice SECURITY CONSIDERATIONS:
    /// - Primary asset operations must be validated separately
    /// - Secondary assets require additional validation
    /// - Balance checks before all operations
    /// - Emergency mode restrictions
    /// - Proper approval management for FundVaultV2
    /// - Asset-specific transfer validations
    ///
    /// STATE MANAGEMENT:
    /// - Normal: Full functionality for all assets
    /// - Paused: No operations allowed
    /// - Emergency: Only emergency withdrawals
    /// - Primary Asset: Always supported
    /// - Secondary Assets: Can be added/removed
    ///
    /// INTEGRATION REQUIREMENTS:
    /// - Must validate primary asset operations first
    /// - Must implement separate flows for primary/secondary assets
    /// - Must maintain accurate balances for all assets
    /// - Must emit appropriate events for tracking
    /// - Must handle FundVaultV2 interactions safely
    /// - Must implement proper access control
    ///
    /// ASSET HANDLING:
    /// Primary Asset:
    /// - Cannot be removed
    /// - Direct integration with FundVaultV2
    /// - Specialized event emission
    ///
    /// Secondary Assets:
    /// - Can be added/removed by admin
    /// - May require conversion logic
    /// - Separate event emission

    /**
     * @notice Handles deposit of any supported asset
     * @param user Address of the depositing user
     * @param asset Address of the asset being deposited
     * @param amount Amount to deposit
     * @return success Whether the deposit was successful
     * @dev Different handling for primary vs secondary assets
     */
    function handleDeposit(address user, address asset, uint256 amount) external returns (bool);

    /**
     * @notice Handles withdrawal of any supported asset
     * @param user Address of the withdrawing user
     * @param asset Address of the asset to withdraw
     * @param amount Amount to withdraw
     * @return success Whether the withdrawal was successful
     * @dev Different handling for primary vs secondary assets
     */
    function handleWithdraw(address user, address asset, uint256 amount) external returns (bool);

    /**
     * @notice Gets the current oracle price for the asset if available
     * @param asset Address of the asset to get price for
     * @return price Current oracle price (0 if not available)
     * @return success Whether oracle price was successfully fetched
     */
    function getOraclePrice(address asset) external view returns (uint256 price, bool success);

    /**
     * @notice Executes emergency withdrawal for any supported asset
     * @param asset Address of the asset to withdraw
     * @param to Recipient address
     * @param amount Amount to withdraw
     * @param reason Reason for emergency withdrawal
     * @return success Whether the withdrawal was successful
     * @dev Available in emergency mode only, special handling for primary asset
     */
    function withdrawEmergency(
        address asset,
        address to,
        uint256 amount,
        string calldata reason
    ) external returns (bool);

    /**
     * @notice Adds support for a secondary asset
     * @param asset Address of the asset to add
     * @param reason Reason for adding the asset
     * @dev Cannot add primary asset, reverts if asset already supported
     */
    function addAsset(address asset, string calldata reason) external;

    /**
     * @notice Removes support for a secondary asset
     * @param asset Address of the asset to remove
     * @param reason Reason for removing the asset
     * @dev Cannot remove primary asset, reverts if asset not supported
     */
    function removeAsset(address asset, string calldata reason) external;

    /**
     * @notice Gets complete list of supported assets
     * @return Array of supported asset addresses
     * @dev Primary asset is always first in the array
     */
    function getSupportedAssets() external view returns (address[] memory);

    /**
     * @notice Gets true for supported asset
     * @param asset Address of the asset to check
     * @return Whether the asset is supported
     */
    function isAssetSupported(address asset) external view returns (bool);

    /**
     * @notice Gets current emergency status
     * @return isEmergencyMode Whether emergency mode is active
     * @return isPaused Whether vault is paused
     * @return timeUntilNextAction Time until next emergency action allowed
     * @dev Used to check vault status before operations
     */
    function getEmergencyStatus()
        external
        view
        returns (bool isEmergencyMode, bool isPaused, uint256 timeUntilNextAction);

    /**
     * @notice Checks if an asset is the primary asset
     * @param asset Asset address to check
     * @return bool True if asset is primary asset
     * @dev Used to determine asset handling flow
     */
    function isPrimaryAsset(address asset) external view returns (bool);

    /**
     * @notice Gets the primary asset address
     * @return address Address of primary asset
     * @dev Primary asset cannot be changed after deployment
     */
    function getPrimaryAsset() external view returns (address);

    /**
     * @notice Enables emergency mode
     * @dev Pauses operations and starts emergency delay timer
     */
    function enableEmergencyMode() external;

    /**
     * @notice Disables emergency mode
     * @dev Can only be called after emergency delay period
     */
    function disableEmergencyMode() external;

    /**
     * @notice Pauses all vault operations
     * @dev Separate from emergency mode
     */
    function pause() external;

    /**
     * @notice Unpauses vault operations
     * @dev Cannot unpause if in emergency mode
     */
    function unpause() external;
}

contracts/interfaces/IWithdrawalSystem.s.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.30;

import '../events/IWithdrawalSystemEvents.sol';
import '../errors/IWithdrawalSystemErrors.sol';
import '../libraries/WithdrawalSystemTypes.sol';
import '../libraries/DataTypes.sol';

/**
 * @title Withdrawal System Interface
 * @author ZeUSD Protocol Team
 * @notice Interface for managing withdrawal requests and processing in the ZeUSD protocol
 * @dev Handles the complete lifecycle of withdrawal requests from initiation to claim
 */
interface IWithdrawalSystem is IWithdrawalSystemEvents, IWithdrawalSystemErrors {
    /**
     * @notice Initiates a withdrawal request for a deposit NFT
     * @param nftTokenId The ID of the deposit NFT to withdraw against
     * @param user Address of the user requesting withdrawal
     * @param amount Amount of asset to withdraw
     * @param metadata Original deposit metadata associated with the NFT
     * @return requestId Unique identifier for the withdrawal request
     * @custom:security Requires NFT ownership verification
     * @custom:emits WithdrawalRequested
     */
    function initiateWithdrawal(
        uint256 nftTokenId,
        address user,
        uint256 amount,
        DataTypes.DepositMetadata calldata metadata
    ) external returns (uint256 requestId);

    /**
     * @notice Processes a batch of pending withdrawal requests
     * @dev Groups and processes requests by asset and subvault
     * @custom:security Only callable by authorized operators
     * @custom:emits WithdrawalProcessed for each request
     */
    function processBatch() external;

    /**
     * @notice Claims a processed withdrawal
     * @param requestId ID of the withdrawal request to claim
     * @custom:security Only callable by request owner when status is READY
     * @custom:emits WithdrawalClaimed
     */
    function claimWithdrawal(uint256 requestId) external;

    /**
     * @notice Sets configuration for an asset's withdrawal parameters
     * @param asset Address of the asset to configure
     * @param config New configuration parameters for the asset
     * @custom:security Only callable by admin
     */
    function setAssetConfig(
        address asset,
        WithdrawalSystemTypes.AssetConfig calldata config
    ) external;

    /**
     * @notice Gets groups of requests ready for processing
     * @return subVaults Array of subvault addresses
     * @return assets Array of asset addresses
     * @return totalAmounts Array of total amounts per group
     * @return availableBalances Array of available balances per group
     * @dev Used to determine which requests can be processed
     */
    function getProcessableGroups()
        external
        view
        returns (
            address[] memory subVaults,
            address[] memory assets,
            uint256[] memory totalAmounts,
            uint256[] memory availableBalances
        );

    /**
     * @notice Checks if a withdrawal request is ready for claiming
     * @param requestId ID of the withdrawal request to check
     * @return isClaimable Whether the request can be claimed
     * @return status Current status of the request
     * @return expiryTime Timestamp when request expires
     */
    function isRequestClaimable(
        uint256 requestId
    )
        external
        view
        returns (bool isClaimable, WithdrawalSystemTypes.RequestStatus status, uint256 expiryTime);

    /**
     * @notice Processes a specific withdrawal request
     * @param requestId ID of the request to process
     */
    function processRequest(uint256 requestId) external;

    /**
     * @notice Gets all withdrawal requests for a user
     * @param user Address of the user
     * @return requestIds Array of request IDs belonging to the user
     * @return requests Array of corresponding request details
     */
    function getUserRequests(
        address user
    )
        external
        view
        returns (
            uint256[] memory requestIds,
            WithdrawalSystemTypes.WithdrawalRequest[] memory requests
        );

    /**
     * @notice Gets all active withdrawal requests for a user
     * @param user Address of the user
     * @return requestIds Array of active request IDs
     * @return requests Array of corresponding request details
     * @dev Returns only IN_QUEUE and READY requests that haven't expired
     */
    function getUserActiveRequests(
        address user
    )
        external
        view
        returns (
            uint256[] memory requestIds,
            WithdrawalSystemTypes.WithdrawalRequest[] memory requests
        );
}

contracts/interfaces/IZeUSD_CDP.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.30;

import '../libraries/DataTypes.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol';

/**
 * @title Deposit NFT Interface
 * @author ZeUSD Protocol Team
 * @notice Interface for managing deposit NFTs in the ZeUSD protocol
 * @dev Extends ERC721 functionality with deposit-specific features
 */
interface IZeUSD_CDP is IERC721Enumerable {
    /**
     * @notice Gets all token IDs owned by a specific address
     * @dev Used for retrieving user's deposit positions
     * @param owner Address to query
     * @return Array of token IDs owned by the address
     * @custom:security No access control required, public view
     */
    function getTokensByOwner(address owner) external view returns (uint256[] memory);

    /**
     * @notice Gets deposit details for a specific token ID
     * @dev Retrieves the original deposit metadata associated with the NFT
     * @param tokenId ID of the token to query
     * @return metadata Deposit metadata for NFT
     * @custom:security Token must exist
     */
    function getDepositDetails(
        uint256 tokenId
    ) external view returns (DataTypes.DepositMetadata memory metadata);

    /**
     * @notice Mints a new NFT with deposit details
     * @dev Creates a new deposit NFT and stores associated metadata
     * @param to Address to mint the NFT to
     * @param metadata Deposit metadata to associate with the NFT
     * @return tokenId ID of the newly minted NFT
     * @custom:security Only callable by router
     * @custom:emits Transfer
     */
    function mint(
        address to,
        DataTypes.DepositMetadata calldata metadata
    ) external returns (uint256);

    /**
     * @notice Burns an NFT
     * @dev Permanently removes an NFT and its associated metadata
     * @param tokenId ID of the token to burn
     * @custom:security Only callable by authorized contracts
     * @custom:emits Transfer to zero address
     */
    function burn(uint256 tokenId) external;

    /**
     * @notice Gets the next token ID without incrementing
     * @return The next token ID that will be used
     */
    function getNextTokenId() external view returns (uint256);
}

contracts/interfaces/IZeUSD.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import '../events/IZeUSDEvents.sol';
import '../errors/IZeUSDErrors.sol';

/**
 * @title ZeUSD Interface
 * @author ZeUSD Protocol Team
 * @notice Main interface for the ZeUSD token contract
 * @dev Extends standard ERC20 functionality with protocol-specific features
 * @custom:security-contact paras@zoth.io
 */
interface IZeUSD is IZeUSDEvents, IZeUSDErrors {
    /**
     * @notice Returns the router address
     * @return address Current router contract address
     * @dev Critical for protocol operations
     */
    function router() external view returns (address);

    /**
     * @notice Sets the blacklist status for an account
     * @param account Address to update blacklist status for
     * @param status New blacklist status (true = blacklisted)
     * @dev Only callable by admin role
     * @custom:emits Blacklisted
     */
    function setBlacklistStatus(address account, bool status) external;

    /**
     * @notice Checks if an account is blacklisted
     * @param account Address to check
     * @return bool True if account is blacklisted
     * @dev Used for compliance checks
     */
    function isBlacklisted(address account) external view returns (bool);

    /**
     * @notice Updates the router address
     * @param newRouter Address of the new router
     * @dev Only callable by admin role
     * @custom:emits RouterUpdated
     */
    function setRouter(address newRouter) external;

    /**
     * @notice Mints new tokens to a specified address
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     * @dev Only callable by router
     * @custom:emits Transfer
     */
    function mint(address to, uint256 amount) external;

    /**
     * @notice Burns tokens from the caller's address
     * @param amount Amount of tokens to burn
     * @dev Requires sufficient balance
     * @custom:emits Transfer
     */
    function burn(uint256 amount) external;

    /**
     * @notice Burns tokens from a specified address
     * @param account Address to burn tokens from
     * @param amount Amount of tokens to burn
     * @dev Requires approval if caller != account
     * @custom:emits Transfer
     */
    function burnFrom(address account, uint256 amount) external;
}

contracts/interfaces/IZeUSDRouter.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.30;

import { SendParam, MessagingFee, MessagingReceipt, OFTReceipt } from '@layerzerolabs/oft-evm/contracts/interfaces/IOFT.sol';
import '../events/IZeUSDRouterEvents.sol';
import '../errors/IZeUSDRouterErrors.sol';
import '../libraries/DataTypes.sol';

/**
 * @title ZeUSD Router Main Interface
 * @author ZeUSD Protocol Team
 * @notice Main interface for interacting with the ZeUSD protocol
 * @dev Combines all protocol functionality including minting, burning, and bridging
 * @custom:security Implements access control and validation checks
 */
interface IZeUSDRouter is IZeUSDRouterEvents, IZeUSDRouterErrors {
    /**
     * @notice Mints ZeUSD tokens against deposited assets
     * @dev Creates NFT representing the deposit position
     * @param asset Address of asset to deposit
     * @param amount Amount to deposit
     * @return tokenId ID of the minted NFT
     * @custom:security Requires prior token approval
     * @custom:emits DepositProcessed
     */
    function mintWithCollateral(address asset, uint256 amount) external returns (uint256 tokenId);

    /**
     * @notice Mints ZeUSD tokens with Stable Coins
     * @dev Specialized function for Stable deposits with different collateral
     * @param collateralAddress Address of collateral asset
     * @param asset Address of Stable Coins
     * @param amount Amount of Stable to deposit
     * @return tokenId ID of the minted NFT
     * @custom:security Requires prior token approval
     * @custom:emits DepositProcessed
     */
    function mintWithStable(
        address collateralAddress,
        address asset,
        uint256 amount
    ) external returns (uint256 tokenId);

    /**
     * @notice Burns ZeUSD tokens to reclaim deposited assets
     * @dev Initiates withdrawal process through WithdrawalSystem
     * @param depositId User's deposit NFT ID
     * @custom:security Requires NFT ownership and sufficient zeUSD balance
     * @custom:emits Burned
     */
    function burn(uint256 depositId) external;

    /**
     * @notice Mints and bridges ZeUSD via LayerZero
     * @dev Combines minting and cross-chain transfer
     * @param asset Address of asset to deposit
     * @param amount Amount to deposit
     * @param sendParam LayerZero send parameters
     * @param nativeFee LayerZero messaging fee
     * @return tokenId ID of the minted NFT
     * @custom:security Requires sufficient native token for bridge fee
     * @custom:emits DepositProcessed, BridgeInitiated
     */
    function mintWithCollateralAndBridge(
        address asset,
        uint256 amount,
        SendParam memory sendParam,
        MessagingFee memory nativeFee
    ) external payable returns (uint256 tokenId);

    /**
     * @notice Mints and bridges Stables via LayerZero
     * @dev Combines stable deposit and cross-chain transfer
     * @param collateralAddress Address of collateral asset
     * @param asset Address of Stable to deposit
     * @param amount Amount to deposit
     * @param sendParam LayerZero send parameters
     * @param nativeFee LayerZero messaging fee
     * @return tokenId ID of the minted NFT
     * @custom:security Requires sufficient native token for bridge fee
     * @custom:emits DepositProcessed, BridgeInitiated
     */
    function mintWithStableAndBridge(
        address collateralAddress,
        address asset,
        uint256 amount,
        SendParam memory sendParam,
        MessagingFee memory nativeFee
    ) external payable returns (uint256 tokenId);

    /**
     * @notice Sets global deposit pause status
     * @param paused New pause status
     * @dev Admin only function
     */
    function setDepositsPaused(bool paused) external;

    /**
     * @notice Gets LayerZero fee quote
     * @param sendParam Send parameters
     * @return MessagingFee Fee details
     */
    function getQuoteFee(SendParam memory sendParam) external view returns (MessagingFee memory);

    /**
     * @notice Gets all active positions for a user
     * @param user Address to query positions for
     * @return tokenIds Array of active token IDs
     * @return metadata Array of corresponding deposit metadata
     */
    function getUserActivePositions(
        address user
    )
        external
        view
        returns (uint256[] memory tokenIds, DataTypes.DepositMetadata[] memory metadata);

    /**
     * @notice Gets details for a specific active position
     * @param tokenId NFT token ID to query
     * @return metadata Deposit metadata for the active position
     */
    function getActivePositionDetails(
        uint256 tokenId
    ) external view returns (DataTypes.DepositMetadata memory metadata);
}

