// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.30;

/// @title Data Types Library
/// @notice Centralizes type definitions used across the protocol
/// @dev Contains all shared data structures and enums for protocol-wide use
/// @custom:security-contact paras@zoth.io
library DataTypes {
    /// @notice Defines the type classification for tokens in the protocol
    /// @dev Uses uint8 internally for gas optimization since enum is stored as uint8
    /// @custom:usage Used for token classification and validation in deposit/withdrawal flows
    enum TokenType {
        /// @notice Represents non-stablecoin tokens (e.g., ETH, BTC)
        /// @dev Value = 0, optimized for gas when checking non-stable status
        NotStableCoin,
        /// @notice Represents stablecoins (e.g., USDC, DAI)
        /// @dev Value = 1, optimized for gas when checking stable status
        StableCoin
    }

    /// @notice Configuration for supported assets
    /// @dev Struct is packed to optimize gas usage
    /// @param isSupported Whether the asset is currently supported
    /// @param integrationType The type of integration used for this asset
    /// @param tokenType Classification of the token (stable/non-stable)
    struct AssetConfig {
        bool isSupported;
        string integrationType;
        TokenType tokenType; // Added enum field
    }

    // /// @notice Stores information about user deposits
    // /// @dev Struct ordering optimized for packing into storage slots
    // /// @param depositId Unique identifier for this deposit
    // /// @param asset Address of the deposited asset
    // /// @param amount Amount of asset deposited
    // /// @param zeusdMinted Amount of zeUSD minted for this deposit
    // /// @param timestamp When the deposit was made
    // /// @param subVault Address of the subvault holding the deposit
    // /// @param integrationType Type of integration used for this deposit
    // /// @param active Whether this deposit is still active
    // /// @param isPrimary Indicates if this is a primary asset deposit
    // /// @param tokenType Classification of the deposited token
    // struct UserDeposit {
    //     uint256 depositId;
    //     address collateralAddress;
    //     address asset;
    //     uint256 amount;
    //     uint256 zeusdMinted;
    //     uint256 timestamp;
    //     address subVault;
    //     string integrationType;
    //     bool active;
    //     bool isPrimary;
    //     TokenType tokenType; // Added enum field
    // }

    /// @notice Configuration for protocol integrations
    /// @dev Uses mapping for efficient asset support lookups
    /// @param subVault Address of the subvault
    /// @param isActive Whether the integration is currently active
    /// @param registeredAt When the integration was first registered
    /// @param lastUpdated When the integration was last updated
    /// @param supportedAssets Mapping of supported assets for this integration
    struct Integration {
        address subVault;
        bool isActive;
        uint256 registeredAt;
        uint256 lastUpdated;
        mapping(address => bool) supportedAssets;
    }

    /// @notice Detailed information about collateral assets
    /// @dev Struct ordering optimized for packing into storage slots
    /// @param integrationType Type of integration used
    /// @param collateralAddress Address of the collateral token
    /// @param subVaultAddress Address of the associated subvault
    /// @param price Current price of the collateral
    /// @param ltv Loan-to-Value ratio for the collateral
    /// @param isActive Whether this collateral is currently active
    /// @param registeredAt Timestamp of collateral registration
    /// @param lastUpdatedAt Timestamp of last update
    /// @param tokenType Classification of the collateral token
    struct CollateralDetails {
        string integrationType;
        address collateralAddress;
        address subVaultAddress;
        uint256 price;
        uint256 ltv;
        bool isActive;
        uint256 registeredAt;
        uint256 lastUpdatedAt;
        TokenType tokenType; // Added enum field
    }

    /// @notice Parameters for updating subvault configuration
    /// @dev Boolean flags used to selectively update specific parameters
    /// @param price New price value
    /// @param ltv New LTV value
    /// @param isActive New active status
    /// @param updatePrice Whether to update the price
    /// @param updateLTV Whether to update the LTV
    /// @param updateActive Whether to update the active status
    struct SubVaultUpdateParams {
        uint256 price;
        uint256 ltv;
        bool isActive;
        bool updatePrice;
        bool updateLTV;
        bool updateActive;
    }

    /// @notice Parameters for updating protocol addresses
    /// @dev Used in updateAddresses function to specify which addresses to update
    /// @param updateCollateralVault If true, update collateralVault address
    /// @param updateZeusdToken If true, update zeusdToken address
    /// @param updateLzAdapter If true, update lzAdapter address
    /// @param collateralVault New collateral vault contract address (only used if updateCollateralVault is true)
    /// @param zeusdToken New ZeUSD token contract address (only used if updateZeusdToken is true)
    /// @param lzAdapter New LayerZero adapter contract address (only used if updateLzAdapter is true)
    struct AddressUpdateParams {
        bool updateCollateralVault;
        bool updateZeusdToken;
        bool updateLzAdapter;
        address collateralVault;
        address zeusdToken;
        address lzAdapter;
    }

    /// @notice Parameters for updating user deposit configuration
    /// @dev Boolean flags used to selectively update specific parameters
    /// @param collateralAddress New collateral address value
    /// @param asset New asset address value
    /// @param amount New amount value for the deposit
    /// @param zeusdMinted New minted ZeUSD value
    /// @param active New active status
    /// @param isPrimary New primary status flag
    /// @param updateCollateralAddress Whether to update the collateral address
    /// @param updateAsset Whether to update the asset address
    /// @param updateAmount Whether to update the amount
    /// @param updateZeusdMinted Whether to update the minted ZeUSD amount
    /// @param updateActive Whether to update the active status
    /// @param updateIsPrimary Whether to update the primary status
    struct UserDepositUpdateParams {
        address collateralAddress;
        address asset;
        uint256 amount;
        uint256 zeusdMinted;
        bool active;
        bool isPrimary;
        bool updateCollateralAddress;
        bool updateAsset;
        bool updateAmount;
        bool updateZeusdMinted;
        bool updateActive;
        bool updateIsPrimary;
    }

    /// @notice Metadata for deposits
    /// @dev Struct to store deposit-related information
    /// @param issuer Original depositor
    /// @param collateralAddress Address of the collateral
    /// @param asset Address of the deposited asset
    /// @param amount Amount of the deposit
    /// @param zeusdMinted Amount of zeUSD minted for this deposit
    /// @param depositTimestamp Timestamp of the deposit
    /// @param collateralPrice Price of the collateral at the time of deposit
    /// @param subVault Address of the subvault
    /// @param integrationType Type of integration used for this deposit
    /// @param tokenType Classification of the deposited token
    /// @param tokenId NFT token ID representing this deposit
    struct DepositMetadata {
        address issuer; // Address of the depositor
        address collateralAddress;
        address asset; // Address of the deposited asset
        uint256 amount; // Amount of asset deposited
        uint256 zeusdMinted; // Amount of ZeUSD minted
        uint256 depositTimestamp;
        uint256 tokenId; // NFT token ID representing this deposit
        uint256 collateralPrice; // Price at deposit time
        address subVault;
        string integrationType;
        TokenType tokenType; // Type of token deposited
    }

    /// @notice Stores information about user deposits
    /// @dev Struct ordering optimized for packing into storage slots
    /// @param depositId Unique identifier for this deposit
    /// @param asset Address of the deposited asset
    /// @param amount Amount of asset deposited
    /// @param zeusdMinted Amount of zeUSD minted for this deposit
    /// @param timestamp When the deposit was made
    /// @param subVault Address of the subvault holding the deposit
    /// @param integrationType Type of integration used for this deposit
    /// @param active Whether this deposit is still active
    /// @param isPrimary Indicates if this is a primary asset deposit
    /// @param tokenType Classification of the deposited token
    struct UserDeposit {
        uint256 depositId;
        address collateralAddress;
        address asset;
        uint256 amount;
        uint256 zeusdMinted;
        uint256 timestamp;
        address subVault;
        string integrationType;
        bool active;
        bool isPrimary;
        TokenType tokenType; // Added enum field
    }

    /// @notice Parameters for updating deposit metadata
    /// @dev Boolean flags used to selectively update specific parameters
    /// @param updateIssuer Whether to update the issuer address
    /// @param issuer New issuer address
    /// @param updateCollateralAddress Whether to update the collateral address
    /// @param collateralAddress New collateral address
    struct MetadataUpdateParams {
        bool updateIssuer;
        address issuer;
        bool updateCollateralAddress;
        address collateralAddress;
        bool updateAsset;
        address asset;
        bool updateAmount;
        uint256 amount;
        bool updateZeusdMinted;
        uint256 zeusdMinted;
        bool updateDepositTimestamp;
        uint256 depositTimestamp;
        bool updateCollateralPrice;
        uint256 collateralPrice;
        bool updateSubVault;
        address subVault;
        bool updateIntegrationType;
        string integrationType;
        bool updateTokenType;
        TokenType tokenType;
    }
}

contracts/libraries/SystemRoles.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;
import '../utils/Constants.sol';

/**
 * @title System Roles
 * @notice Defines roles and their configurations
 * @dev Uses constants from main Constants library
 * @author ZeUSD Protocol Team
 * @custom:security-contact paras@zoth.io
 */
library SystemRoles {
    // Core Administrative Roles
    bytes32 public constant DEFAULT_ADMIN_ROLE = 0x00;
    bytes32 public constant EMERGENCY_ROLE = keccak256('EMERGENCY_ROLE');
    bytes32 public constant UPGRADER_ROLE = keccak256('UPGRADER_ROLE');
    bytes32 public constant WITHDRAWAL_MANAGER_ROLE = keccak256('WITHDRAWAL_MANAGER_ROLE');

    // Vault Management Roles
    bytes32 public constant VAULT_ADMIN_ROLE = keccak256('VAULT_ADMIN_ROLE');
    bytes32 public constant ASSET_MANAGER_ROLE = keccak256('ASSET_MANAGER_ROLE');

    // Risk & Control Roles
    bytes32 public constant RISK_CONTROLLER_ROLE = keccak256('RISK_CONTROLLER_ROLE');
    bytes32 public constant LIQUIDATOR_ROLE = keccak256('LIQUIDATOR_ROLE');
    bytes32 public constant PRICE_ADMIN_ROLE = keccak256('PRICE_ADMIN_ROLE');

    // Rewards & Incentives
    bytes32 public constant REWARD_MANAGER_ROLE = keccak256('REWARD_MANAGER_ROLE');

    /**
     * @notice Role configuration data structure
     * @param adminRole Role that can grant/revoke this role
     * @param timelock Required delay for critical operations
     * @param requiresConsensus Whether consensus is required
     * @param revocable Whether role can be revoked
     * @param pausable Whether role can be paused
     */
    struct RoleConfig {
        bytes32 adminRole;
        uint256 timelock;
        bool requiresConsensus;
        bool revocable;
        bool pausable;
    }

    /**
     * @notice Permission configuration data structure
     * @param role Role identifier
     * @param functionSig Function signature
     * @param enabled Whether permission is active
     * @param restrictions Additional restrictions (bitmap)
     */
    struct Permission {
        bytes32 role;
        bytes4 functionSig;
        bool enabled;
        uint256 restrictions;
    }

    /**
     * @notice Returns DEFAULT_ADMIN_ROLE configuration
     * @dev Highest authority, requires consensus and delay
     */
    function getDefaultAdminConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: DEFAULT_ADMIN_ROLE,
                timelock: Constants.TWO_DAYS,
                requiresConsensus: true,
                revocable: false,
                pausable: false
            });
    }

    /**
     * @notice Returns EMERGENCY_ROLE configuration
     * @dev Quick response role, no delay but revocable
     */
    function getEmergencyConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: DEFAULT_ADMIN_ROLE,
                timelock: 0,
                requiresConsensus: false,
                revocable: true,
                pausable: true
            });
    }

    /**
     * @notice Returns UPGRADER_ROLE configuration
     * @dev Contract upgrade role, requires consensus
     */
    function getWithdrawalManagerConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: DEFAULT_ADMIN_ROLE,
                timelock: Constants.ONE_DAY,
                requiresConsensus: true,
                revocable: true,
                pausable: true
            });
    }

    /**
     * @notice Returns UPGRADER_ROLE configuration
     * @dev Contract upgrade role, requires consensus
     */
    function getUpgraderConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: DEFAULT_ADMIN_ROLE,
                timelock: Constants.ONE_DAY,
                requiresConsensus: true,
                revocable: true,
                pausable: true
            });
    }

    /**
     * @notice Returns VAULT_ADMIN_ROLE configuration
     * @dev Vault management role with delay
     */
    function getVaultAdminConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: DEFAULT_ADMIN_ROLE,
                timelock: Constants.ONE_DAY,
                requiresConsensus: true,
                revocable: true,
                pausable: true
            });
    }

    /**
     * @notice Returns ASSET_MANAGER_ROLE configuration
     * @dev Asset management under vault admin
     */
    function getAssetManagerConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: VAULT_ADMIN_ROLE,
                timelock: Constants.TWELVE_HOURS,
                requiresConsensus: true,
                revocable: true,
                pausable: true
            });
    }

    /**
     * @notice Returns RISK_CONTROLLER_ROLE configuration
     * @dev Risk parameter management role
     */
    function getRiskControllerConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: DEFAULT_ADMIN_ROLE,
                timelock: Constants.TWELVE_HOURS,
                requiresConsensus: true,
                revocable: true,
                pausable: true
            });
    }

    /**
     * @notice Returns LIQUIDATOR_ROLE configuration
     * @dev Liquidation execution role, no delay
     */
    function getLiquidatorConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: RISK_CONTROLLER_ROLE,
                timelock: 0,
                requiresConsensus: false,
                revocable: true,
                pausable: true
            });
    }

    /**
     * @notice Returns PRICE_ADMIN_ROLE configuration
     * @dev Oracle management role
     */
    function getPriceAdminConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: DEFAULT_ADMIN_ROLE,
                timelock: Constants.TWELVE_HOURS,
                requiresConsensus: true,
                revocable: true,
                pausable: true
            });
    }

    /**
     * @notice Returns REWARD_MANAGER_ROLE configuration
     * @dev Rewards management role
     */
    function getRewardManagerConfig() internal pure returns (RoleConfig memory) {
        return
            RoleConfig({
                adminRole: DEFAULT_ADMIN_ROLE,
                timelock: Constants.ONE_DAY,
                requiresConsensus: true,
                revocable: true,
                pausable: true
            });
    }
}

