pragma solidity ^0.8.30;

interface IUniswapV2Pair {
    event Approval(address indexed owner, address indexed spender, uint value);
    event Transfer(address indexed from, address indexed to, uint value);

    function name() external pure returns (string memory);
    function symbol() external pure returns (string memory);
    function decimals() external pure returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);
    function PERMIT_TYPEHASH() external pure returns (bytes32);
    function nonces(address owner) external view returns (uint);

    function permit(address owner, address spender, uint value, uint deadline, uint8 v, bytes32 r, bytes32 s) external;

    event Mint(address indexed sender, uint amount0, uint amount1);
    event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
    event Swap(
        address indexed sender,
        uint amount0In,
        uint amount1In,
        uint amount0Out,
        uint amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint);
    function factory() external view returns (address);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function price0CumulativeLast() external view returns (uint);
    function price1CumulativeLast() external view returns (uint);
    function kLast() external view returns (uint);

    function mint(address to) external returns (uint liquidity);
    function burn(address to) external returns (uint amount0, uint amount1);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
    function skim(address to) external;
    function sync() external;

    function initialize(address, address) external;
}

contracts/dso/LPRewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "../interfaces/ILPRewards.sol";
import "../interfaces/IPriceFeedAggregator.sol";
import "./PoolHelper.sol";

/// @title Contract for handling LP rewards
/// @notice Each LP token has its own LPRewards contract
contract LPRewards is ILPRewards, Ownable {
  using SafeERC20 for IERC20;
  using SafeCast for uint256;

  uint8 public immutable decimals;
  IUniswapV2Pair public immutable lpToken;
  IPriceFeedAggregator public immutable priceFeedAggregator;

  address public ochi;
  uint64 public currentEpoch;
  int256 public totalAmountLocked;
  uint256 public epochMinLPBalance;
  uint256 public currentLPValue;
  int256 public cumulativeProfit;

  mapping(uint256 tokenId => LockingTokenData) public lockingTokenData;
  mapping(uint256 epochId => EpochData) public epochData;

  modifier onlyOCHI() {
    if (msg.sender != ochi) {
      revert NotOCHI();
    }
    _;
  }

  constructor(IUniswapV2Pair _lpToken, IPriceFeedAggregator _priceFeedAggregator) {
    lpToken = _lpToken;
    decimals = lpToken.decimals();
    priceFeedAggregator = _priceFeedAggregator;
    currentEpoch = 1;
    totalAmountLocked = 0;
  }

  function setOCHI(address _ochi) external onlyOwner {
    ochi = _ochi;
    emit SetOCHI(_ochi);
  }

  /// @notice Locks LP tokens for given period, user gets rewards until the end of the period
  /// @param lockingTokenId Unique OCHI id
  /// @param amount Amount of LP tokens to lock
  /// @param epochDuration Locking duration in epochs
  /// @custom:usage This function should be called from OCHI contract in purpose of locking LP tokens
  function lockLP(uint256 lockingTokenId, uint256 amount, uint64 epochDuration) external onlyOCHI {
    if (amount == 0) {
      return;
    }

    LockingTokenData storage position = lockingTokenData[lockingTokenId];

    if (position.amountLocked != 0) {
      revert LockingTokenIdAlreadyUsed(lockingTokenId);
    }

    uint64 nextEpoch = currentEpoch + 1;
    epochData[nextEpoch].totalDeltaAmountLocked += amount.toInt256();
    epochData[nextEpoch + epochDuration].totalDeltaAmountLocked -= amount.toInt256();

    position.amountLocked = amount;
    position.endingEpoch = nextEpoch + epochDuration;
    position.lastClaimedEpoch = currentEpoch;

    emit LockLP(lockingTokenId, amount, currentEpoch, position.endingEpoch);
  }

  /// @notice Claims rewards for given token id
  /// @param lockingTokenId Unique OCHI id
  /// @param account Account to send rewards to
  /// @custom:usage This function should be called from OCHI contract in purpose of claiming rewards
  function claimRewards(uint256 lockingTokenId, address account) external onlyOCHI {
    int256 rewardUSD = calculateUnclaimedReward(lockingTokenId);
    if (rewardUSD < 0) {
      return;
    }

    uint256 totalPoolValue = PoolHelper.getTotalPoolUSDValue(lpToken, priceFeedAggregator);
    uint256 lpTokensToBurn = Math.mulDiv(uint256(rewardUSD), lpToken.totalSupply(), totalPoolValue);
    if (lpTokensToBurn == 0) {
      return;
    }

    IERC20(address(lpToken)).safeTransfer(address(lpToken), lpTokensToBurn);
    lpToken.burn(account);

    uint256 newBalance = lpToken.balanceOf(address(this));
    if (newBalance < epochMinLPBalance) epochMinLPBalance = newBalance;

    lockingTokenData[lockingTokenId].lastClaimedEpoch = currentEpoch - 1;

    emit ClaimRewards(lockingTokenId, account, lpTokensToBurn, rewardUSD);
  }

  /// @notice End current epoch and start new one
  /// @custom:usage This function should be called from OCHI contract when epoch is updated
  function updateEpoch() external onlyOCHI {
    EpochData storage epoch = epochData[currentEpoch];

    totalAmountLocked += epoch.totalDeltaAmountLocked;

    uint256 prevLPValue = currentLPValue;
    currentLPValue = PoolHelper.getUSDValueForLP(10 ** decimals, lpToken, priceFeedAggregator);

    int256 totalProfit = (currentLPValue.toInt256() - prevLPValue.toInt256()) * epochMinLPBalance.toInt256();
    int256 profitPerLockedToken;
    if (totalAmountLocked != 0) {
      profitPerLockedToken = totalProfit / totalAmountLocked;
    }

    cumulativeProfit += profitPerLockedToken;
    epoch.cumulativeProfit = cumulativeProfit;

    epochMinLPBalance = lpToken.balanceOf(address(this));
    currentEpoch++;

    emit UpdateEpoch(currentEpoch - 1, currentLPValue, totalAmountLocked, profitPerLockedToken);
  }

  /// @notice Takes LP tokens from contract and sends them to receiver
  /// @param receiver Account to send LP tokens to
  /// @custom:usage This function should be called only in case of moving liquidity to another pool
  function recoverLPTokens(address receiver) external onlyOCHI {
    uint256 amount = lpToken.balanceOf(address(this));
    IERC20(address(lpToken)).safeTransfer(receiver, amount);
    emit RecoverLPTokens(receiver, amount);
  }

  /// @notice Calculates unclaimed rewards for given token id
  /// @param lockingTokenId Unique OCHI id
  function calculateUnclaimedReward(uint256 lockingTokenId) public view returns (int256) {
    LockingTokenData storage position = lockingTokenData[lockingTokenId];
    if (position.endingEpoch == 0) {
      return 0;
    }

    uint64 fromEpoch = position.lastClaimedEpoch;
    uint64 toEpoch = currentEpoch - 1;
    if (position.endingEpoch - 1 < toEpoch) toEpoch = position.endingEpoch - 1;
    if (toEpoch <= fromEpoch) return 0;

    int256 profitDelta = epochData[toEpoch].cumulativeProfit - epochData[fromEpoch].cumulativeProfit;
    int256 totalUSDreward = (position.amountLocked.toInt256() * profitDelta) / (10 ** decimals).toInt256();
    return totalUSDreward;
  }

  /// @notice Calculates the profit in last epoch in usd value
  function getLastEpochProfit() external view returns (int256) {
    if (currentEpoch < 2) return 0;
    return epochData[currentEpoch - 1].cumulativeProfit - epochData[currentEpoch - 2].cumulativeProfit;
  }
}

contracts/dso/OCHI.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../interfaces/IOCHI.sol";
import "../interfaces/IPriceFeedAggregator.sol";
import "../interfaces/IMintableBurnable.sol";
import "../interfaces/ILPRewards.sol";
import "../library/ExternalContractAddresses.sol";
import "./PoolHelper.sol";

/// @title Contract for creating and executing options
/// @notice Each LP token has its own LPRewards contract
/// @dev This contract is upgradeable
/// @dev This contract handles creation and execution of options but does not hold LP tokens
contract OCHI is IOCHI, ERC721EnumerableUpgradeable, OwnableUpgradeable {
  using SafeERC20 for IERC20;
  using SafeERC20 for IMintableBurnable;
  using SafeERC20 for IUniswapV2Pair;

  uint256 public constant MAX_LOCK_PERIOD_EPOCHS = 52;
  uint256 public constant EPOCH_DURATION = 1 weeks;
  uint256 public constant BASE_PRICE = 1e8;
  uint256 public constant MULTIPLIER = 1e18;
  uint256 public constant TARGET_RATIO = 80_00;
  uint256 public constant DENOMINATOR = 100_00;
  address public constant WETH = ExternalContractAddresses.WETH;

  IERC20 public usc;
  IMintableBurnable public chi;
  IUniswapV2Pair public uscEthPair;
  IUniswapV2Pair public chiEthPair;
  IPriceFeedAggregator public priceFeedAggregator;

  uint64 public currentEpoch;
  uint256 public firstEpochTimestamp;
  uint256 public mintedOCHI;
  uint256 public totalOCHIlocked;

  mapping(IUniswapV2Pair token => ILPRewards) public lpRewards;
  mapping(uint256 id => ChiOption) public options;

  // Upgrade
  bool public pauseMint;
  bool public pauseBurn;
  bool public pauseClaimRewards;

  modifier notPausedMint() {
    if (pauseMint) {
      revert MintPaused();
    }
    _;
  }

  modifier notPausedBurn() {
    if (pauseBurn) {
      revert BurnPaused();
    }
    _;
  }

  modifier notPausedClaimRewards() {
    if (pauseClaimRewards) {
      revert ClaimRewardsPaused();
    }
    _;
  }

  function initialize(
    IERC20 _usc,
    IMintableBurnable _chi,
    IPriceFeedAggregator _priceFeedAggregator,
    IUniswapV2Pair _uscEthPair,
    IUniswapV2Pair _chiEthPair,
    ILPRewards _uscEthPairRewards,
    ILPRewards _chiEthPairRewards,
    uint256 _firstEpochTimestamp
  ) external initializer {
    __ERC721_init("Option CHI", "oCHI");
    __Ownable_init();
    usc = _usc;
    chi = _chi;
    priceFeedAggregator = _priceFeedAggregator;
    uscEthPair = _uscEthPair;
    chiEthPair = _chiEthPair;

    lpRewards[_uscEthPair] = _uscEthPairRewards;
    lpRewards[_chiEthPair] = _chiEthPairRewards;

    currentEpoch = 1;
    firstEpochTimestamp = _firstEpochTimestamp;
  }

  /// @inheritdoc IOCHI
  function setPauseMint(bool _pauseMint) external onlyOwner {
    pauseMint = _pauseMint;
  }

  /// @inheritdoc IOCHI
  function setPauseBurn(bool _pauseBurn) external onlyOwner {
    pauseBurn = _pauseBurn;
  }

  /// @inheritdoc IOCHI
  function setPauseClaimRewards(bool _pauseClaimRewards) external onlyOwner {
    pauseClaimRewards = _pauseClaimRewards;
  }

  /// @inheritdoc IOCHI
  function mint(
    uint256 chiAmount,
    uint256 uscEthPairAmount,
    uint256 chiEthPairAmount,
    uint64 lockPeriodInEpochs
  ) external notPausedMint {
    if (lockPeriodInEpochs > MAX_LOCK_PERIOD_EPOCHS || lockPeriodInEpochs == 0) {
      revert InvalidLockPeriod(lockPeriodInEpochs);
    }

    (uint256 strikePrice, uint256 oChiAmount) = calculateOptionData(
      chiAmount,
      uscEthPairAmount,
      chiEthPairAmount,
      lockPeriodInEpochs
    );

    uint64 nextEpoch = currentEpoch + 1;
    uint256 tokenId = ++mintedOCHI;
    options[tokenId] = ChiOption({
      amount: oChiAmount,
      strikePrice: strikePrice,
      uscEthPairAmount: uscEthPairAmount,
      chiEthPairAmount: chiEthPairAmount,
      lockedUntil: nextEpoch + lockPeriodInEpochs,
      validUntil: nextEpoch + 2 * lockPeriodInEpochs
    });

    totalOCHIlocked += oChiAmount;

    ILPRewards uscEthLPRewards = lpRewards[uscEthPair];
    ILPRewards chiEthLPRewards = lpRewards[chiEthPair];

    if (chiAmount > 0) {
      chi.burnFrom(msg.sender, chiAmount);
    }
    IERC20(address(uscEthPair)).safeTransferFrom(msg.sender, address(uscEthLPRewards), uscEthPairAmount);
    IERC20(address(chiEthPair)).safeTransferFrom(msg.sender, address(chiEthLPRewards), chiEthPairAmount);

    uscEthLPRewards.lockLP(tokenId, uscEthPairAmount, uint64(lockPeriodInEpochs));
    chiEthLPRewards.lockLP(tokenId, chiEthPairAmount, uint64(lockPeriodInEpochs));

    _safeMint(msg.sender, tokenId);

    emit Mint(tokenId, chiAmount, uscEthPairAmount, chiEthPairAmount, lockPeriodInEpochs, strikePrice, oChiAmount);
  }

  /// @inheritdoc IOCHI
  function burn(uint256 tokenId) external notPausedBurn {
    if (!_isApprovedOrOwner(msg.sender, tokenId)) {
      revert NotAllowed(tokenId);
    }

    ChiOption storage option = options[tokenId];

    if (currentEpoch < option.lockedUntil) {
      revert OptionLocked(tokenId);
    }
    if (currentEpoch > option.validUntil) {
      revert OptionExpired(tokenId);
    }

    lpRewards[uscEthPair].claimRewards(tokenId, msg.sender);
    lpRewards[chiEthPair].claimRewards(tokenId, msg.sender);

    uint256 chiAmount = option.amount;
    uint256 chiBalance = chi.balanceOf(address(this));
    uint256 amountToTransfer = Math.min(chiAmount, chiBalance);
    uint256 amountToMint = chiAmount - amountToTransfer;

    if (amountToMint > 0) {
      chi.mint(msg.sender, amountToMint);
    }
    if (amountToTransfer > 0) {
      chi.transfer(msg.sender, amountToTransfer);
    }

    _burn(tokenId);

    totalOCHIlocked -= options[tokenId].amount;

    emit Burn(msg.sender, tokenId, chiAmount);
  }

  /// @inheritdoc IOCHI
  function updateEpoch() public {
    if (block.timestamp < firstEpochTimestamp + (currentEpoch - 1) * EPOCH_DURATION) {
      revert EpochNotFinished();
    }

    lpRewards[uscEthPair].updateEpoch();
    lpRewards[chiEthPair].updateEpoch();

    currentEpoch++;
    emit UpdateEpoch(currentEpoch - 1, block.timestamp);
  }

  /// @inheritdoc IOCHI
  function claimRewards(uint256 tokenId) public notPausedClaimRewards {
    if (!_isApprovedOrOwner(msg.sender, tokenId)) {
      revert NotAllowed(tokenId);
    }

    lpRewards[uscEthPair].claimRewards(tokenId, msg.sender);
    lpRewards[chiEthPair].claimRewards(tokenId, msg.sender);

    emit ClaimRewardsOCHI(tokenId);
  }

  /// @notice Claims rewards for all users tokens
  function claimAllRewards() public {
    uint256 balance = balanceOf(msg.sender);
    for (uint256 i = 0; i < balance; i++) {
      claimRewards(tokenOfOwnerByIndex(msg.sender, i));
    }
  }

  /// @inheritdoc IOCHI
  function recoverLPTokens() external onlyOwner {
    lpRewards[uscEthPair].recoverLPTokens(msg.sender);
    lpRewards[chiEthPair].recoverLPTokens(msg.sender);

    emit RecoverLPTokens();
  }

  /// @inheritdoc IOCHI
  function getUnclaimedRewardsValue(uint256 tokenId) external view returns (int256) {
    return
      lpRewards[uscEthPair].calculateUnclaimedReward(tokenId) + lpRewards[chiEthPair].calculateUnclaimedReward(tokenId);
  }

  /// @notice Calulates strike price and ochi amount for supplied asets
  /// @param chiAmount amount of chi token to supply
  /// @param uscEthPairAmount amount of USC/ETH LP token to supply
  /// @param chiEthPairAmount amount of CHI/ETH LP token to supply
  /// @param lockPeriodInEpochs locking duration
  /// @return strikePrice strike price given for supplied assets
  /// @return oChiAmount ochi amount given for supplied assets
  function calculateOptionData(
    uint256 chiAmount,
    uint256 uscEthPairAmount,
    uint256 chiEthPairAmount,
    uint256 lockPeriodInEpochs
  ) public view returns (uint256 strikePrice, uint256 oChiAmount) {
    uint256 timeMultiplier = Math.mulDiv(lockPeriodInEpochs, MULTIPLIER, 4 * MAX_LOCK_PERIOD_EPOCHS);
    uint256 chiMultiplier = Math.mulDiv(chiAmount, MULTIPLIER, chi.totalSupply());
    (uint256 poolMultiplier, uint256 positionsValue) = getAndValidatePositionsData(uscEthPairAmount, chiEthPairAmount);

    uint256 discount = timeMultiplier + Math.min(poolMultiplier + chiMultiplier, MULTIPLIER / 4);
    uint256 chiPrice = _peek(address(chi));
    strikePrice = Math.mulDiv(chiPrice, MULTIPLIER - discount, MULTIPLIER);

    uint256 chiValue = Math.mulDiv(chiAmount, chiPrice, MULTIPLIER);
    oChiAmount = Math.mulDiv(positionsValue + chiValue, MULTIPLIER, strikePrice);
  }

  /// @notice Calculates multiplier for supplied LP assets
  /// @param uscEthPairAmount amount of USC/ETH LP token to supply
  /// @param chiEthPairAmount amount of CHI/ETH LP token to supply
  /// @return multiplier multiplier used for caluclating discount
  /// @param value parameter used for calculating ochi amount
  function getAndValidatePositionsData(
    uint256 uscEthPairAmount,
    uint256 chiEthPairAmount
  ) public view returns (uint256 multiplier, uint256 value) {
    uint256 uscEthPairTotalSupply = uscEthPair.totalSupply();
    uint256 chiEthPairTotalSupply = chiEthPair.totalSupply();

    if (
      uscEthPair.balanceOf(address(this)) + uscEthPairAmount >
      Math.mulDiv(uscEthPairTotalSupply, TARGET_RATIO, DENOMINATOR) ||
      chiEthPair.balanceOf(address(this)) + chiEthPairAmount >
      Math.mulDiv(chiEthPairTotalSupply, TARGET_RATIO, DENOMINATOR)
    ) {
      revert PolTargetRatioExceeded();
    }

    multiplier =
      Math.mulDiv(uscEthPairAmount, MULTIPLIER, uscEthPairTotalSupply) +
      Math.mulDiv(chiEthPairAmount, MULTIPLIER, chiEthPairTotalSupply);

    value = (PoolHelper.getUSDValueForLP(uscEthPairAmount, uscEthPair, priceFeedAggregator) +
      PoolHelper.getUSDValueForLP(chiEthPairAmount, chiEthPair, priceFeedAggregator));
  }

  /// @notice Calculates the total LP reward for the last epoch
  /// @return totalReward total reward in usd value
  function getLastEpochTotalReward() external view returns (int256 totalReward) {
    if (currentEpoch < 2) return 0;
    return lpRewards[uscEthPair].getLastEpochProfit() + lpRewards[chiEthPair].getLastEpochProfit();
  }

  function _peek(address asset) internal view returns (uint256 price) {
    price = priceFeedAggregator.peek(asset);
  }
}

contracts/dso/PoolHelper.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../interfaces/IPriceFeedAggregator.sol";

library PoolHelper {
  function getTotalPoolUSDValue(
    IUniswapV2Pair pair,
    IPriceFeedAggregator priceFeedAggregator
  ) internal view returns (uint256) {
    (uint112 token0amount, uint112 token1amount, ) = pair.getReserves();
    uint256 price0 = priceFeedAggregator.peek(pair.token0());
    uint256 price1 = priceFeedAggregator.peek(pair.token1());

    // assuming both tokens have 18 decimals!
    uint256 totalValue = Math.mulDiv(token0amount, price0, 1e18) + Math.mulDiv(token1amount, price1, 1e18);
    return totalValue;
  }

  function getUSDValueForLP(
    uint256 lpAmount,
    IUniswapV2Pair pair,
    IPriceFeedAggregator priceFeedAggregator
  ) internal view returns (uint256) {
    return Math.mulDiv(getTotalPoolUSDValue(pair, priceFeedAggregator), lpAmount, pair.totalSupply());
  }
}

contracts/interfaces/IArbitrage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

interface IArbitrage {
  event SetPriceTolerance(uint16 priceTolerance);
  event SetMaxMintPriceDiff(uint256 maxMintPriceDiff);
  event Mint(address indexed account, address token, uint256 amount, uint256 uscAmount);
  event ExecuteArbitrage(
    address indexed account,
    uint256 indexed arbNum,
    uint256 deltaUsd,
    uint256 reserveDiff,
    uint256 ethPrice,
    uint256 rewardValue
  );

  error DeltaBiggerThanAmountReceivedETH(uint256 deltaETH, uint256 receivedETH);
  error ToleranceTooBig(uint16 _tolerance);
  error PriceSlippageTooBig();

  /// @notice Sets spot price tolerance from TWAP price
  /// @dev 100% = 10000
  /// @param _priceTolerance Price tolerance in percents
  /// @custom:usage This function should be called from owner in purpose of setting price tolerance
  function setPriceTolerance(uint16 _priceTolerance) external;

  /// @notice Sets max mint price diff
  /// @param _maxMintPriceDiff Max mint price diff
  /// @custom:usage This function should be called from owner in purpose of setting max mint price diff
  function setMaxMintPriceDiff(uint256 _maxMintPriceDiff) external;

  /// @notice Mint USC tokens for ETH
  /// @dev If USC price is different from target price for less then max mint price diff, then minting is allowed without performing arbitrage
  /// @return uscAmount Amount of USC tokens minted
  function mint() external payable returns (uint256 uscAmount);

  /// @notice Mint USC tokens for WETH
  /// @dev If USC price is different from target price for less then max mint price diff, then minting is allowed without performing arbitrage
  /// @param wethAmount Amount of WETH to mint with
  /// @return uscAmount Amount of USC tokens minted
  function mintWithWETH(uint256 wethAmount) external returns (uint256 uscAmount);

  /// @notice Mint USC tokens for stETH
  /// @dev If USC price is different from target price for less then max mint price diff, then minting is allowed without performing arbitrage
  /// @param stETHAmount Amount of stETH to mint with
  /// @return uscAmount Amount of USC tokens minted
  function mintWithStETH(uint256 stETHAmount) external returns (uint256 uscAmount);

  /// @notice Executes arbitrage, profit sent to caller
  /// @notice Returns reward value in USD
  /// @return rewardValue Reward value in USD
  /// @custom:usage This function should be called from external keeper in purpose of pegging USC price and getting reward
  /// @custom:usage This function has no restrictions, anyone can be arbitrager
  function executeArbitrage() external returns (uint256 rewardValue);

  /// @notice Gets information for perfoming arbitrage such as price diff, reserve diff, discount
  /// @return isPriceAboveTarget True if USC price is above target price
  /// @return isExcessOfReserves True if there is excess of reserves
  /// @return reserveDiff Reserve diff, excess or deficit of reserves
  /// @return discount Discount in percents, only if price is equal to target price
  function getArbitrageData()
    external
    view
    returns (bool isPriceAboveTarget, bool isExcessOfReserves, uint256 reserveDiff, uint256 discount);
}

