  struct StEthReward {
    uint256 tokenValue;
    uint256 usdValue;
  }

  struct ChiReward {
    uint256 tokenValue;
    uint256 usdValue;
  }

  struct UscReward {
    uint256 tokenValue;
    uint256 usdValue;
  }

  struct Reward {
    StEthReward stEthReward;
    ChiReward chiReward;
    UscReward uscReward;
    uint256 totalReward;
  }

  struct Rewards {
    Reward stUscRewards;
    Reward stChiRewards;
    Reward veChiRewards;
    Reward uscEthLpRewards;
    Reward chiEthLpRewards;
    uint256 totalStEthReward;
    uint256 totalStEthRewardUsd;
    uint256 totalChiReward;
    uint256 totalChiRewardUsd;
  }

  function POLdata(
    OCHI dso,
    IUniswapV2Pair uniPair,
    IPriceFeedAggregator priceFeed
  ) external view returns (uint256 polUsdValue, uint256 polPercent) {
    uint256 lpAmount = uniPair.balanceOf(address(dso.lpRewards(uniPair)));
    polUsdValue = PoolHelper.getUSDValueForLP(lpAmount, uniPair, priceFeed);
    polPercent = Math.mulDiv(lpAmount, 10 ** 18, uniPair.totalSupply());
  }

  function getLPTokenPrice(
    IUniswapV2Pair pair,
    IPriceFeedAggregator priceFeedAggregator
  ) public view returns (uint256) {
    return PoolHelper.getUSDValueForLP(1 ether, pair, priceFeedAggregator);
  }

  function getTotalPoolValue(
    IUniswapV2Pair pair,
    IPriceFeedAggregator priceFeedAggregator
  ) public view returns (uint256) {
    return PoolHelper.getTotalPoolUSDValue(pair, priceFeedAggregator);
  }

  function chiStakingAPR(
    address chi,
    ChiStaking chiStaking,
    ChiLocking chiLocking,
    USCStaking uscStaking,
    LPStaking uscEthLpStaking,
    LPStaking chiEthLpStaking,
    ChiVesting chiVesting,
    RewardControllerV2 rewardController,
    IPriceFeedAggregator priceFeedAggregator,
    ReserveHolder reserveHolder
  )
    public
    view
    returns (
      uint256 chiStakingAprInStEth,
      uint256 chiLockingAprInStEth,
      uint256 uscStakingAprInStEth,
      uint256 uscEthLpStakingAprInStEth,
      uint256 chiEthLpStakingAprInStEth
    )
  {
    uint256 stEthPrice = priceFeedAggregator.peek(ExternalContractAddresses.stETH);
    uint256 currentEpoch = chiStaking.currentEpoch();

    (, uint256 totalRewardsTwoEpochsAgo) = currentEpoch >= 2 ? rewardController.epochs(currentEpoch - 2) : (0, 0);
    (, uint256 totalRewardsLastEpoch) = rewardController.epochs(currentEpoch - 1);

    uint256 totalEthReward;
    if (currentEpoch < 4) {
      totalEthReward = (reserveHolder.totalStEthDeposited() * 4) / 100 / 52;
    } else {
      totalEthReward = totalRewardsLastEpoch - totalRewardsTwoEpochsAgo;
    }

    uint256 chiPrice = priceFeedAggregator.peek(chi);
    uint256 totalEthRewardValue = Math.mulDiv(totalEthReward, stEthPrice, 1e8);

    uint256 stEthForChiStaking = Math.mulDiv(
      totalEthRewardValue,
      rewardController.stEthPercentageForChiStaking(),
      10000
    );
    uint256 chiStakedValue = Math.mulDiv(chiStaking.getStakedChi(), chiPrice, 1e8);
    chiStakingAprInStEth = Math.mulDiv(stEthForChiStaking * 52, 1e18, chiStakedValue);

    uint256 stEthForChiLocking = Math.mulDiv(
      totalEthRewardValue,
      rewardController.stEthPercentageForChiLocking(),
      10000
    );
    uint256 chiLockedValue = Math.mulDiv(chiLocking.getStakedChi(), chiPrice, 1e8);
    chiLockingAprInStEth = Math.mulDiv(stEthForChiLocking * 52, 1e18, chiLockedValue);

    uint256 stEthForUscStaking = Math.mulDiv(
      totalEthRewardValue,
      rewardController.stEthPercentageForUscStaking(),
      10000
    );
    uint256 uscStakedValue = uscStaking.totalSupply();
    uscStakingAprInStEth = Math.mulDiv(stEthForUscStaking * 52, 1e18, uscStakedValue);

    uint256 uscEthLpTokenPrice = getLPTokenPrice(
      IUniswapV2Pair(address(uscEthLpStaking.stakeToken())),
      priceFeedAggregator
    );
    uint256 stEthForUscEthLPStaking = Math.mulDiv(
      totalEthRewardValue,
      rewardController.stEthPercentageForUscEthLPStaking(),
      10000
    );
    uint256 uscEthLPStakedValue = Math.mulDiv(uscEthLpStaking.totalSupply(), uscEthLpTokenPrice, 1e8);
    uscEthLpStakingAprInStEth = Math.mulDiv(stEthForUscEthLPStaking * 52, 1e18, uscEthLPStakedValue);

    uint256 chiEthLpTokenPrice = getLPTokenPrice(
      IUniswapV2Pair(address(chiEthLpStaking.stakeToken())),
      priceFeedAggregator
    );
    uint256 stEthForChiEthLPStaking = Math.mulDiv(
      totalEthRewardValue,
      rewardController.stEthPercentageForChiEthLPStaking(),
      10000
    );
    uint256 chiEthLPStakedValue = Math.mulDiv(chiEthLpStaking.totalSupply(), chiEthLpTokenPrice, 1e8);
    chiEthLpStakingAprInStEth = Math.mulDiv(stEthForChiEthLPStaking * 52, 1e18, chiEthLPStakedValue);

    return (
      chiStakingAprInStEth,
      chiLockingAprInStEth,
      uscStakingAprInStEth,
      uscEthLpStakingAprInStEth,
      chiEthLpStakingAprInStEth
    );
  }

  function uscStakingAPR(
    address chi,
    ChiStaking chiStaking,
    ChiLocking chiLocking,
    USCStaking uscStaking,
    LPStaking uscEthLpStaking,
    LPStaking chiEthLpStaking,
    ChiVesting chiVesting,
    RewardControllerV2 rewardController,
    IPriceFeedAggregator priceFeedAggregator,
    ReserveHolder reserveHolder
  ) external view returns (uint256 totalApr, uint256 uscApr, uint256 chiApr) {
    uint256 currentEpoch = rewardController.currentEpoch();
    (, , uint256 stChiApr, , ) = chiStakingAPR(
      chi,
      chiStaking,
      chiLocking,
      uscStaking,
      uscEthLpStaking,
      chiEthLpStaking,
      chiVesting,
      rewardController,
      priceFeedAggregator,
      reserveHolder
    );
    uint256 chiEmissions = rewardController.chiIncentivesForUscStaking();
    uint256 totalUscStaked = uscStaking.totalSupply();

    uint256 chiPrice = priceFeedAggregator.peek(chi);
    uint256 chiEmissionsValue = Math.mulDiv(chiEmissions, chiPrice, 1e8);
    chiApr = Math.mulDiv(chiEmissionsValue * 52, 1e18, totalUscStaked);

    (uint256 uscRewardAmount, ) = rewardController.epochs(currentEpoch - 1);

    if (currentEpoch == 9) {
      uscRewardAmount += 3750 ether;
    }

    uscApr = Math.mulDiv(uscRewardAmount * 52, 1e18, totalUscStaked);

    return (chiApr + uscApr, uscApr, chiApr);
  }

  function chiLockingAPR(
    address chi,
    ChiStaking chiStaking,
    ChiLocking chiLocking,
    USCStaking uscStaking,
    LPStaking uscEthLpStaking,
    LPStaking chiEthLpStaking,
    ChiVesting chiVesting,
    RewardControllerV2 rewardController,
    IPriceFeedAggregator priceFeedAggregator,
    ReserveHolder reserveHolder
  ) public view returns (uint256 totalApr, uint256 chiApr, uint256 stChiApr) {
    uint256 chiPrice = priceFeedAggregator.peek(chi);
    uint256 totalLockedChiValue = Math.mulDiv(chiLocking.getLockedChi() + chiVesting.getLockedChi(), chiPrice, 1e8);
    uint256 chiEmissions = rewardController.chiIncentivesForChiLocking();
    uint256 chiEmissionsValue = Math.mulDiv(chiEmissions, chiPrice, 1e8);
    chiApr = Math.mulDiv(chiEmissionsValue * 52, 1e18, totalLockedChiValue);
    (, stChiApr, , , ) = chiStakingAPR(
      chi,
      chiStaking,
      chiLocking,
      uscStaking,
      uscEthLpStaking,
      chiEthLpStaking,
      chiVesting,
      rewardController,
      priceFeedAggregator,
      reserveHolder
    );

    return (chiApr + stChiApr, chiApr, stChiApr);
  }

  function dsoAPR(
    address chi,
    OCHI ochi,
    IPriceFeedAggregator priceFeedAggregator
  ) external view returns (uint256 apr, int256 tradingFees) {
    LPRewards uscEthLpRewards = LPRewards(address(ochi.lpRewards(ochi.uscEthPair())));
    LPRewards chiEthLpRewards = LPRewards(address(ochi.lpRewards(ochi.chiEthPair())));

    int256 profitPerTokenUscEth = uscEthLpRewards.getLastEpochProfit();
    int256 profitPerTokenChiEth = chiEthLpRewards.getLastEpochProfit();

    int256 lpBalanceUscEth = uscEthLpRewards.epochMinLPBalance().toInt256();
    int256 lpBalanceChiEth = chiEthLpRewards.epochMinLPBalance().toInt256();

    int256 totalProfitPrevWeek = ((profitPerTokenUscEth * lpBalanceUscEth) / 1e18) +
      ((profitPerTokenChiEth * lpBalanceChiEth) / 1e18);

    if (totalProfitPrevWeek < 0) {
      return (0, 0);
    }

    uint256 totalChiLocked = ochi.totalOCHIlocked();
    uint256 chiPrice = priceFeedAggregator.peek(chi);
    uint256 totalChiLockedValue = Math.mulDiv(totalChiLocked, chiPrice, 1e18);

    return (Math.mulDiv(uint256(totalProfitPrevWeek) * 52, 1e18, totalChiLockedValue), totalProfitPrevWeek);
  }

  function uscEthLPAPR(
    address chi,
    ChiStaking chiStaking,
    ChiLocking chiLocking,
    USCStaking uscStaking,
    LPStaking uscEthLpStaking,
    LPStaking chiEthLpStaking,
    ChiVesting chiVesting,
    RewardControllerV2 rewardController,
    IPriceFeedAggregator priceFeedAggregator,
    IUniswapV2Pair uscEthPair,
    ReserveHolder reserveHolder
  ) external view returns (uint256 totalApr, uint256 chiApr, uint256 stChiApr) {
    uint256 chiPrice = priceFeedAggregator.peek(chi);
    uint256 chiEmissions = rewardController.chiIncentivesForUscEthLPStaking();
    uint256 chiEmissionsValue = Math.mulDiv(chiEmissions, chiPrice, 1e8);

    uint256 totalStaked = uscEthLpStaking.totalSupply();
    uint256 uscEthPairPrice = getLPTokenPrice(uscEthPair, priceFeedAggregator);
    uint256 totalStakedValue = Math.mulDiv(totalStaked, uscEthPairPrice, 1e8);
    chiApr = Math.mulDiv(chiEmissionsValue * 52, 1e18, totalStakedValue);

    (, , , uint256 chiStakingAPR, ) = chiStakingAPR(
      chi,
      chiStaking,
      chiLocking,
      uscStaking,
      uscEthLpStaking,
      chiEthLpStaking,
      chiVesting,
      rewardController,
      priceFeedAggregator,
      reserveHolder
    );
    stChiApr = Math.mulDiv(chiEmissionsValue, chiStakingAPR * 52, totalStakedValue);

    return (chiApr + stChiApr, chiApr, stChiApr);
  }

  function chiEthLPAPR(
    address chi,
    ChiStaking chiStaking,
    ChiLocking chiLocking,
    USCStaking uscStaking,
    LPStaking uscEthLpStaking,
    LPStaking chiEthLpStaking,
    ChiVesting chiVesting,
    RewardControllerV2 rewardController,
    IPriceFeedAggregator priceFeedAggregator,
    IUniswapV2Pair chiEthPair,
    ReserveHolder reserveHolder
  ) external view returns (uint256 totalApr, uint256 chiApr, uint256 stChiApr) {
    uint256 chiPrice = priceFeedAggregator.peek(chi);
    uint256 chiEmissions = rewardController.chiIncentivesForChiEthLPStaking();
    uint256 chiEmissionsValue = Math.mulDiv(chiEmissions, chiPrice, 1e8);

    uint256 totalStaked = chiEthLpStaking.totalSupply();
    uint256 chiEthPairPrice = getLPTokenPrice(chiEthPair, priceFeedAggregator);
    uint256 totalStakedValue = Math.mulDiv(totalStaked, chiEthPairPrice, 1e8);
    chiApr = Math.mulDiv(chiEmissionsValue * 52, 1e18, totalStakedValue);

    (, , , , uint256 chiStakingAPR) = chiStakingAPR(
      chi,
      chiStaking,
      chiLocking,
      uscStaking,
      uscEthLpStaking,
      chiEthLpStaking,
      chiVesting,
      rewardController,
      priceFeedAggregator,
      reserveHolder
    );
    stChiApr = Math.mulDiv(chiEmissionsValue, chiStakingAPR * 52, totalStakedValue);

    return (chiApr + stChiApr, chiApr, stChiApr);
  }

  function estimatedYieldPerWeek(
    address chi,
    ChiStaking chiStaking,
    ChiLocking chiLocking,
    USCStaking uscStaking,
    LPStaking uscEthLpStaking,
    LPStaking chiEthLpStaking,
    ChiVesting chiVesting,
    RewardControllerV2 rewardController,
    IPriceFeedAggregator priceFeedAggregator,
    ReserveHolder reserveHolder
  ) external view returns (uint256) {
    (uint256 chiLockingApr, , ) = chiLockingAPR(
      chi,
      chiStaking,
      chiLocking,
      uscStaking,
      uscEthLpStaking,
      chiEthLpStaking,
      chiVesting,
      rewardController,
      priceFeedAggregator,
      reserveHolder
    );
    uint256 totalLockedChi = chiLocking.getLockedChi() + chiVesting.getLockedChi();
    uint256 chiPrice = priceFeedAggregator.peek(chi);
    uint256 totalLockedChiValue = Math.mulDiv(totalLockedChi, chiPrice, 1e18);
    uint256 chiLockingWeeklyAPR = chiLockingApr / 52;

    return Math.mulDiv(totalLockedChiValue, chiLockingWeeklyAPR, 1e18);
  }

  function totalValueOfLockedChi(
    IPriceFeedAggregator priceFeedAggregator,
    ChiLocking chiLocking,
    ChiVesting chiVesting,
    address chi,
    address account
  ) external view returns (uint256) {
    IChiLocking.AllLockedPositionsOutput[] memory lockedPositions = chiLocking.getAllLockedPositions(account);

    uint256 totalChi;
    for (uint256 i = 0; i < lockedPositions.length; i++) {
      totalChi += lockedPositions[i].totalAccumulatedChi;
    }

    (uint256 totalVestedChi, , , , , ) = chiVesting.vestingData(account);
    totalChi += totalVestedChi;

    uint256 chiPrice = priceFeedAggregator.peek(address(chi));

    return Math.mulDiv(totalChi, chiPrice, 1e18);
  }

  function rewards(
    address chi,
    address usc,
    ChiStaking chiStaking,
    ChiLocking chiLocking,
    USCStaking uscStaking,
    LPStaking uscEthLPStaking,
    LPStaking chiEthLPStaking,
    ChiVesting chiVesting,
    RewardControllerV2 rewardController,
    IPriceFeedAggregator priceFeedAggregator,
    ReserveHolder reserveHolder
  ) external view returns (Rewards memory) {
    uint256 chiIncentivesForUscStaking = rewardController.chiIncentivesForUscStaking();
    uint256 chiIncentivesForChiLocking = rewardController.chiIncentivesForChiLocking();
    uint256 chiIncentivesForUscEthLPStaking = rewardController.chiIncentivesForUscEthLPStaking();
    uint256 chiIncentivesForChiEthLPStaking = rewardController.chiIncentivesForChiEthLPStaking();
    uint256 uscRewards = IERC20(usc).balanceOf(address(rewardController));
    uint256 uscRewardsUsd = Math.mulDiv(uscRewards, 1e8, 1e18);

    uint256 chiPrice = priceFeedAggregator.peek(chi);

    uint256 chiIncentivesForUscStakingUsd = Math.mulDiv(chiIncentivesForUscStaking, chiPrice, 1e18);
    uint256 chiIncentivesForChiLockingUsd = Math.mulDiv(chiIncentivesForChiLocking, chiPrice, 1e18);
    uint256 chiIncentivesForUscEthLPStakingUsd = Math.mulDiv(chiIncentivesForUscEthLPStaking, chiPrice, 1e18);
    uint256 chiIncentivesForChiEthLPStakingUsd = Math.mulDiv(chiIncentivesForChiEthLPStaking, chiPrice, 1e18);

    uint256 currentEpoch = rewardController.currentEpoch();
    (, uint256 totalRewardsTwoEpochsAgo) = currentEpoch >= 2 ? rewardController.epochs(currentEpoch - 2) : (0, 0);
    (, uint256 totalRewardsLastEpoch) = rewardController.epochs(currentEpoch - 1);

    uint256 stETHEpochrewards = totalRewardsLastEpoch - totalRewardsTwoEpochsAgo;
    uint256 stETHEcpohrewardsUsd = Math.mulDiv(
      stETHEpochrewards,
      priceFeedAggregator.peek(ExternalContractAddresses.stETH),
      1e18
    );

    uint256 uscStakedChi = uscStaking.getStakedChi();
    uint256 chiStakedChi = chiStaking.getStakedChi();
    uint256 chiLockedChi = chiLocking.getStakedChi();
    uint256 chiVestingChi = chiVesting.getLockedChi();
    uint256 uscEthLPStakingChi = uscEthLPStaking.getStakedChi();
    uint256 chiEthLPStakingChi = chiEthLPStaking.getStakedChi();
    uint256 totalChi = uscStakedChi +
      chiStakedChi +
      chiLockedChi +
      chiVestingChi +
      uscEthLPStakingChi +
      chiEthLPStakingChi;

    uint256 stEthPrice = priceFeedAggregator.peek(ExternalContractAddresses.stETH);
    uint256 maxPercentage = rewardController.MAX_PERCENTAGE();
    uint256 stEthPercentageForUscStaking = rewardController.stEthPercentageForUscStaking();
    uint256 stEthPercentageForChiStaking = rewardController.stEthPercentageForChiStaking();
    uint256 stEthPercentageForChiLocking = rewardController.stEthPercentageForChiLocking();
    uint256 stEthPercentageForChiVesting = rewardController.stEthPercentageForChiVesting();
    uint256 stEthPercentageForUscEthLPStaking = rewardController.stEthPercentageForUscEthLPStaking();
    uint256 stEthPercentageForChiEthLPStaking = rewardController.stEthPercentageForChiEthLPStaking();

    uint256 uscStakingStEthReward = Math.mulDiv(stETHEpochrewards, stEthPercentageForUscStaking, maxPercentage);
    uint256 chiStakingStEthReward = Math.mulDiv(stETHEpochrewards, stEthPercentageForChiStaking, maxPercentage);
    uint256 chiLockingStEthReward = Math.mulDiv(stETHEpochrewards, stEthPercentageForChiLocking, maxPercentage);
    uint256 chiVestingStEthReward = Math.mulDiv(stETHEpochrewards, stEthPercentageForChiVesting, maxPercentage);
    uint256 uscEthLPStakingStEthReward = Math.mulDiv(
      stETHEpochrewards,
      stEthPercentageForUscEthLPStaking,
      maxPercentage
    );
    uint256 chiEthLPStakingStEthReward = Math.mulDiv(
      stETHEpochrewards,
      stEthPercentageForChiEthLPStaking,
      maxPercentage
    );

    uint256 uscStakingStEthRewardUsd = Math.mulDiv(uscStakingStEthReward, stEthPrice, 1e18);
    uint256 chiStakingStEthRewardUsd = Math.mulDiv(chiStakingStEthReward, stEthPrice, 1e18);
    uint256 chiLockingStEthRewardUsd = Math.mulDiv(chiLockingStEthReward, stEthPrice, 1e18);
    uint256 chiVestingStEthRewardUsd = Math.mulDiv(chiVestingStEthReward, stEthPrice, 1e18);
    uint256 uscEthLPStakingStEthRewardUsd = Math.mulDiv(uscEthLPStakingStEthReward, stEthPrice, 1e18);
    uint256 chiEthLPStakingStEthRewardUsd = Math.mulDiv(chiEthLPStakingStEthReward, stEthPrice, 1e18);

    uint256 totalStEthReward = uscStakingStEthReward +
      chiStakingStEthReward +
      chiLockingStEthReward +
      chiVestingStEthReward +
      uscEthLPStakingStEthReward +
      chiEthLPStakingStEthReward;

    uint totalStEthRewardUsd = uscStakingStEthRewardUsd +
      chiStakingStEthRewardUsd +
      chiLockingStEthRewardUsd +
      chiVestingStEthRewardUsd +
      uscEthLPStakingStEthRewardUsd +
      chiEthLPStakingStEthRewardUsd;

    uint256 totalChiReward = chiIncentivesForUscStaking +
      chiIncentivesForChiLocking +
      chiIncentivesForUscEthLPStaking +
      chiIncentivesForChiEthLPStaking;

    uint256 totalChiRewardUsd = chiIncentivesForUscStakingUsd +
      chiIncentivesForChiLockingUsd +
      chiIncentivesForUscEthLPStakingUsd +
      chiIncentivesForChiEthLPStakingUsd;

    return (
      Rewards({
        stUscRewards: Reward({
          stEthReward: StEthReward({tokenValue: uscStakingStEthReward, usdValue: uscStakingStEthRewardUsd}),
          chiReward: ChiReward({tokenValue: chiIncentivesForUscStaking, usdValue: chiIncentivesForUscStakingUsd}),
          uscReward: UscReward({tokenValue: uscRewards, usdValue: uscRewardsUsd}),
          totalReward: uscStakingStEthRewardUsd + chiIncentivesForUscStakingUsd + uscRewardsUsd
        }),
        stChiRewards: Reward({
          stEthReward: StEthReward({tokenValue: chiStakingStEthReward, usdValue: chiStakingStEthRewardUsd}),
          chiReward: ChiReward({tokenValue: 0, usdValue: 0}),
          uscReward: UscReward({tokenValue: 0, usdValue: 0}),
          totalReward: chiStakingStEthRewardUsd
        }),
        veChiRewards: Reward({
          stEthReward: StEthReward({
            tokenValue: chiVestingStEthReward + chiLockingStEthReward,
            usdValue: chiVestingStEthRewardUsd + chiLockingStEthRewardUsd
          }),
          chiReward: ChiReward({tokenValue: chiIncentivesForChiLocking, usdValue: chiIncentivesForChiLockingUsd}),
          uscReward: UscReward({tokenValue: 0, usdValue: 0}),
          totalReward: chiVestingStEthRewardUsd + chiLockingStEthRewardUsd + chiIncentivesForChiLockingUsd
        }),
        uscEthLpRewards: Reward({
          stEthReward: StEthReward({tokenValue: uscEthLPStakingStEthReward, usdValue: uscEthLPStakingStEthRewardUsd}),
          chiReward: ChiReward({
            tokenValue: chiIncentivesForUscEthLPStaking,
            usdValue: chiIncentivesForUscEthLPStakingUsd
          }),
          uscReward: UscReward({tokenValue: 0, usdValue: 0}),
          totalReward: uscEthLPStakingStEthRewardUsd + chiIncentivesForUscEthLPStakingUsd
        }),
        chiEthLpRewards: Reward({
          stEthReward: StEthReward({tokenValue: chiEthLPStakingStEthReward, usdValue: chiEthLPStakingStEthRewardUsd}),
          chiReward: ChiReward({
            tokenValue: chiIncentivesForChiEthLPStaking,
            usdValue: chiIncentivesForChiEthLPStakingUsd
          }),
          uscReward: UscReward({tokenValue: 0, usdValue: 0}),
          totalReward: chiEthLPStakingStEthRewardUsd + chiIncentivesForChiEthLPStakingUsd
        }),
        totalStEthReward: totalStEthReward,
        totalStEthRewardUsd: totalStEthRewardUsd,
        totalChiReward: totalChiReward,
        totalChiRewardUsd: totalChiRewardUsd
      })
    );
  }
}

contracts/ReserveHolder.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "./interfaces/IReserveHolder.sol";
import "./interfaces/IPriceFeedAggregator.sol";
import "./interfaces/ISTETH.sol";
import "./interfaces/IWETH.sol";
import "./interfaces/ICurvePool.sol";
import "./library/ExternalContractAddresses.sol";

/// @title Contract for holding stETH reserves
/// @notice This contract holds stETH reserves and rebalances them
/// @notice Part of reserves are is WETH so arbitrage can take them and perform aribtrage without swapping stETH for WETH
/// @dev This contract is upgradeable
contract ReserveHolder is IReserveHolder, OwnableUpgradeable {
  using SafeERC20 for ISTETH;
  using SafeERC20 for IWETH;

  uint256 public constant BASE_PRICE = 1e8;
  uint256 public constant MAX_PERCENTAGE = 100_00;
  IWETH public constant WETH = IWETH(ExternalContractAddresses.WETH);
  ISTETH public constant stETH = ISTETH(ExternalContractAddresses.stETH);
  ICurvePool public constant curvePool = ICurvePool(ExternalContractAddresses.CURVE_ETH_STETH_POOL);

  IPriceFeedAggregator public priceFeedAggregator;
  address public claimer;
  uint256 public totalClaimed;
  uint256 public swapEthTolerance;
  uint256 public ethThreshold;
  uint256 public totalStEthDeposited;
  uint256 public curveStEthSafeGuardPercentage;

  mapping(address account => bool status) public isArbitrager;

  modifier onlyArbitrager() {
    if (isArbitrager[msg.sender] != true) {
      revert NotArbitrager(msg.sender);
    }
    _;
  }

  modifier onlyClaimer() {
    if (msg.sender != claimer) {
      revert NotClaimer(msg.sender);
    }
    _;
  }

  receive() external payable {
    emit Receive(msg.sender, msg.value);
  }

  function initialize(
    IPriceFeedAggregator _priceFeedAggregator,
    address _claimer,
    uint256 _ethThreshold,
    uint256 _curveStEthSafeGuardPercentage
  ) external initializer {
    if (_ethThreshold > MAX_PERCENTAGE) {
      revert ThresholdTooHigh(_ethThreshold);
    }
    __Ownable_init();
    claimer = _claimer;
    priceFeedAggregator = _priceFeedAggregator;
    ethThreshold = _ethThreshold;
    curveStEthSafeGuardPercentage = _curveStEthSafeGuardPercentage;
    swapEthTolerance = 0.1 ether;
  }

  /// @inheritdoc IReserveHolder
  function setArbitrager(address arbitrager, bool status) external onlyOwner {
    isArbitrager[arbitrager] = status;
    emit SetArbitrager(arbitrager, status);
  }

  /// @inheritdoc IReserveHolder
  function setClaimer(address _claimer) external onlyOwner {
    claimer = _claimer;
    emit SetClaimer(_claimer);
  }

  /// @inheritdoc IReserveHolder
  function setEthThreshold(uint256 _ethThreshold) external onlyOwner {
    if (_ethThreshold > MAX_PERCENTAGE) {
      revert ThresholdTooHigh(_ethThreshold);
    }
    ethThreshold = _ethThreshold;
    emit SetEthThreshold(_ethThreshold);
  }

  /// @inheritdoc IReserveHolder
  function setSwapEthTolerance(uint256 _swapEthTolerance) external onlyOwner {
    swapEthTolerance = _swapEthTolerance;
    emit SetSwapEthTolerance(_swapEthTolerance);
  }

  /// @inheritdoc IReserveHolder
  function setCurveStEthSafeGuardPercentage(uint256 _curveStEthSafeGuardPercentage) external onlyOwner {
    if (_curveStEthSafeGuardPercentage > MAX_PERCENTAGE) {
      revert SafeGuardTooHigh(_curveStEthSafeGuardPercentage);
    }
    curveStEthSafeGuardPercentage = _curveStEthSafeGuardPercentage;
    emit SetCurveStEthSafeGuardPercentage(_curveStEthSafeGuardPercentage);
  }

  /// @inheritdoc IReserveHolder
  function getReserveValue() external view returns (uint256) {
    uint256 stEthPrice = priceFeedAggregator.peek(address(stETH));
    uint256 ethPrice = priceFeedAggregator.peek(address(WETH));
    uint256 ethBalance = address(this).balance + WETH.balanceOf(address(this));
    return Math.mulDiv(totalStEthDeposited, stEthPrice, 1e18) + Math.mulDiv(ethBalance, ethPrice, 1e18);
  }

  /// @inheritdoc IReserveHolder
  function getCurrentRewards() external view returns (uint256) {
    return stETH.balanceOf(address(this)) - totalStEthDeposited;
  }

  /// @inheritdoc IReserveHolder
  function getCumulativeRewards() external view returns (uint256) {
    return stETH.balanceOf(address(this)) - totalStEthDeposited + totalClaimed;
  }

  /// @inheritdoc IReserveHolder
  function deposit(uint256 amount) external {
    uint256 balanceBefore = stETH.balanceOf(address(this));
    stETH.safeTransferFrom(msg.sender, address(this), amount);
    totalStEthDeposited += stETH.balanceOf(address(this)) - balanceBefore;

    emit Deposit(msg.sender, amount);
  }

  /// @inheritdoc IReserveHolder
  function rebalance() external {
    uint256 ethPrice = _peek(address(WETH));
    uint256 stEthPrice = _peek(address(stETH));
    uint256 ethValue = Math.mulDiv(WETH.balanceOf(address(this)), ethPrice, BASE_PRICE);
    uint256 stEthValue = Math.mulDiv(stETH.balanceOf(address(this)), stEthPrice, BASE_PRICE);
    uint256 ethThresholdValue = Math.mulDiv((ethValue + stEthValue), ethThreshold, MAX_PERCENTAGE);

    if (ethThresholdValue > ethValue) {
      uint256 stEthAmountToSwap = Math.mulDiv((ethThresholdValue - ethValue), BASE_PRICE, stEthPrice);
      uint256 stEthBalanceBefore = stETH.balanceOf(address(this));
      _swap(stEthAmountToSwap);
      uint256 stEthBalanceAfter = stETH.balanceOf(address(this));
      totalStEthDeposited -= stEthBalanceBefore - stEthBalanceAfter;

      emit Rebalance(0, stEthAmountToSwap);
    } else if (ethThresholdValue < ethValue) {
      uint256 stEthBalanceBefore = stETH.balanceOf(address(this));
      uint256 ethAmountToSwap = Math.mulDiv((ethValue - ethThresholdValue), BASE_PRICE, ethPrice);

      WETH.withdraw(ethAmountToSwap);
      stETH.submit{value: ethAmountToSwap}(address(this));

      uint256 stEthBalanceAfter = stETH.balanceOf(address(this));
      totalStEthDeposited += stEthBalanceAfter - stEthBalanceBefore;

      emit Rebalance(ethAmountToSwap, 0);
    }
  }

  /// @inheritdoc IReserveHolder
  function redeem(uint256 amount) external onlyArbitrager returns (uint256) {
    uint256 ethBalance = WETH.balanceOf(address(this));
    if (amount > ethBalance) {
      uint256 stEthBalanceBefore = stETH.balanceOf(address(this));
      uint256 stEthAmountToSwap = amount - ethBalance;
      uint256 safeStEthAmountToSwap = stEthAmountToSwap +
        Math.mulDiv(stEthAmountToSwap, curveStEthSafeGuardPercentage, MAX_PERCENTAGE);
      _swap(safeStEthAmountToSwap);
      uint256 stEthBalanceAfter = stETH.balanceOf(address(this));
      totalStEthDeposited -= stEthBalanceBefore - stEthBalanceAfter;

      emit RedeemSwap(amount - ethBalance, safeStEthAmountToSwap);
    }
    WETH.safeTransfer(msg.sender, amount);

    emit Redeem(msg.sender, amount);
    return amount;
  }

  /// @inheritdoc IReserveHolder
  function claimRewards(address account, uint256 amount) external onlyClaimer {
    totalClaimed += amount;
    stETH.safeTransfer(account, amount);
    emit ClaimRewards(account, amount);
  }

  /// @inheritdoc IReserveHolder
  function wrapETH() external {
    WETH.deposit{value: address(this).balance}();
  }

  function _swap(uint256 amountIn) private {
    stETH.approve(address(curvePool), amountIn);
    uint256 ethReceived = curvePool.exchange(1, 0, amountIn, 0);
    WETH.deposit{value: ethReceived}();
  }

  function _peek(address asset) private view returns (uint256) {
    uint256 price = priceFeedAggregator.peek(asset);
    return price;
  }

  function _safeTransferETH(address to, uint256 value) private {
    (bool success, ) = to.call{value: value}(new bytes(0));
    if (!success) {
      revert EtherSendFailed(to, value);
    }
  }
}

contracts/staking/ChiLocking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../interfaces/IChiLocking.sol";
import "../library/ExternalContractAddresses.sol";

/// @title Contract for locking CHI tokens
/// @notice This contract holds CHI tokens that are locked
/// @notice Locking of reward distribution of CHI tokens is done per epoch
contract ChiLocking is IChiLocking, OwnableUpgradeable {
  using SafeERC20 for IERC20;

  uint256 public constant MAX_LOCK_DURATION = 208; // 4 years in weeks
  IERC20 public constant stETH = IERC20(ExternalContractAddresses.stETH);

  IERC20 public chi;
  address public rewardController;
  uint256 public currentEpoch;
  uint256 public totalLockedChi;
  uint256 public totalUnlockedChi;
  uint256 public totalLockedShares;
  uint256 public totalVotingPower;
  uint256 public sumOfLockedDurations;
  uint256 public numberOfLockedPositions;
  uint256 public addVotingPowerInEpoch;
  uint256 public sumShareDurationProduct;
  uint256 public addedAmountInEpoch;

  mapping(address account => bool status) public chiLockers;
  mapping(address account => LockingData) public locks;
  mapping(uint256 id => EpochData) public epochs;

  modifier onlyRewardController() {
    if (msg.sender != rewardController) {
      revert NotRewardController();
    }
    _;
  }

  modifier onlyChiLockers() {
    if (!chiLockers[msg.sender]) {
      revert NotChiLocker();
    }
    _;
  }

  function initialize(IERC20 _chi, address _chiStaking) external initializer {
    __Ownable_init();
    chiLockers[_chiStaking] = true;
    chi = _chi;
    currentEpoch = 1;
  }

  /// @inheritdoc IChiLocking
  function setUscStaking(address _uscStaking) external onlyOwner {
    chiLockers[_uscStaking] = true;
    emit SetUscStaking(_uscStaking);
  }

  /// @inheritdoc IChiLocking
  function setRewardController(address _rewardController) external onlyOwner {
    rewardController = _rewardController;
    emit SetRewardController(_rewardController);
  }

  /// @inheritdoc IChiLocking
  function setChiLocker(address contractAddress, bool toSet) external onlyOwner {
    chiLockers[contractAddress] = toSet;
    emit SetChiLocker(contractAddress, toSet);
  }

  /// @inheritdoc IChiLocking
  function getLockedPosition(address account, uint256 pos) external view returns (LockedPosition memory) {
    return locks[account].positions[pos];
  }

  function getAllLockedPositions(address account) external view returns (AllLockedPositionsOutput[] memory out) {
    LockingData storage lockData = locks[account];

    out = new AllLockedPositionsOutput[](lockData.positions.length);

    for (uint256 i = 0; i < lockData.positions.length; i++) {
      LockedPosition storage position = lockData.positions[i];
      out[i].position = position;
      out[i].votingPower = _getCurrentVotingPowerForPosition(position);
      out[i].stETHreward = _getUnclaimedStETHPositionAmount(lockData.positions[i], lockData.lastUpdatedEpoch);
      out[i].totalAccumulatedChi = _totalAccumulatedAtEpoch(currentEpoch, lockData.positions[i]);
      out[i].totalChiRewards = (out[i].totalAccumulatedChi > position.amount)
        ? out[i].totalAccumulatedChi - position.amount
        : 0;
    }
  }

  /// @inheritdoc IChiLocking
  function getStakedChi() public view returns (uint256) {
    return totalLockedChi + totalUnlockedChi;
  }

  /// @inheritdoc IChiLocking
  function getLockedChi() public view returns (uint256) {
    return totalLockedChi;
  }

  /// @inheritdoc IChiLocking
  function getTotalVotingPower() external view returns (uint256) {
    return totalVotingPower;
  }

  /// @inheritdoc IChiLocking
  function availableChiWithdraw(address account) public view returns (uint256 availableTotal) {
    LockingData storage lockData = locks[account];
    for (uint256 i = 0; i < lockData.positions.length; i++) {
      availableTotal += _availableToWithdrawFromPosition(lockData.positions[i]);
    }
  }

  /// @inheritdoc IChiLocking
  function lockChi(address account, uint256 amount, uint256 duration) external onlyChiLockers {
    if (amount == 0) {
      revert ZeroAmount();
    }

    uint256 shares = _getNumberOfShares(amount);
    duration++;

    EpochData storage currentEpochData = epochs[currentEpoch];
    EpochData storage afterEndEpoch = epochs[currentEpoch + duration];

    locks[account].positions.push(
      LockedPosition({
        amount: amount,
        startEpoch: currentEpoch,
        duration: duration,
        shares: shares,
        withdrawnChiAmount: 0
      })
    );

    totalLockedChi += amount;
    totalLockedShares += shares;
    currentEpochData.lockedSharesInEpoch += shares;
    currentEpochData.totalLockedChiInEpoch += amount;
    afterEndEpoch.sharesToUnlock += shares;

    totalVotingPower += Math.mulDiv(amount, duration, MAX_LOCK_DURATION);
    sumShareDurationProduct += Math.mulDiv(shares, duration, MAX_LOCK_DURATION);
    sumOfLockedDurations += duration + 1;
    numberOfLockedPositions += 1;
    afterEndEpoch.numberOfEndingPositions += 1;

    emit LockChi(account, amount, shares, currentEpoch, currentEpoch + duration);
  }

  /// @inheritdoc IChiLocking
  function updateEpoch(uint256 chiEmissions, uint256 stETHrewards) external onlyRewardController {
    EpochData storage epoch = epochs[currentEpoch];

    uint256 stETHrewardsForLocked;
    uint256 stakedChi = getStakedChi();
    if (stakedChi != 0) {
      stETHrewardsForLocked = Math.mulDiv(stETHrewards, totalLockedChi, stakedChi);
    }
    uint256 stETHrewardsForUnlocked = stETHrewards - stETHrewardsForLocked;

    uint256 stEthPerLockedShare;
    if (epoch.lockedSharesInEpoch != 0) {
      stEthPerLockedShare = Math.mulDiv(stETHrewardsForLocked, 1e18, epoch.lockedSharesInEpoch);
    }
    epoch.cumulativeStETHPerLockedShare = epochs[currentEpoch - 1].cumulativeStETHPerLockedShare + stEthPerLockedShare;

    uint256 stEthPerUnlocked;
    if (totalUnlockedChi != 0) {
      stEthPerUnlocked = Math.mulDiv(stETHrewardsForUnlocked, 1e18, totalUnlockedChi);
    }

    epoch.cumulativeStETHPerUnlocked = epochs[currentEpoch - 1].cumulativeStETHPerUnlocked + stEthPerUnlocked;

    totalVotingPower -= totalLockedChi / MAX_LOCK_DURATION;
    sumShareDurationProduct -= totalLockedShares / MAX_LOCK_DURATION;

    if (totalLockedShares != 0) {
      totalVotingPower += Math.mulDiv(chiEmissions, sumShareDurationProduct, totalLockedShares);
    }

    totalLockedChi += chiEmissions;
    epoch.totalLockedChiInEpoch += chiEmissions;

    EpochData storage nextEpoch = epochs[currentEpoch + 1];

    uint256 amountToUnlock;
    if (totalLockedShares != 0) {
      amountToUnlock = Math.mulDiv(epoch.totalLockedChiInEpoch, nextEpoch.sharesToUnlock, epoch.lockedSharesInEpoch);
    }

    totalLockedChi = totalLockedChi - amountToUnlock;
    totalUnlockedChi = totalUnlockedChi + amountToUnlock;
    totalLockedShares -= nextEpoch.sharesToUnlock;

    nextEpoch.lockedSharesInEpoch = totalLockedShares;
    nextEpoch.totalLockedChiInEpoch = totalLockedChi;

    currentEpoch++;
    emit UpdateEpoch(currentEpoch - 1, totalLockedChi, chiEmissions, stETHrewards, stEthPerLockedShare);
  }

  /// @inheritdoc IChiLocking
  function claimStETH(address account) external onlyRewardController returns (uint256 amount) {
    _updateUnclaimedStETH(account);
    amount = locks[account].unclaimedStETH;
    locks[account].unclaimedStETH = 0;

    emit ClaimStETH(account, amount);
  }

  /// @inheritdoc IChiLocking
  function withdrawChiFromAccount(address account, uint256 amount) public onlyChiLockers {
    withdrawChiFromAccountToAddress(account, account, amount);
  }

  /// @inheritdoc IChiLocking
  function withdrawChiFromAccountToAddress(address account, address toAddress, uint256 amount) public onlyChiLockers {
    if (amount == 0) {
      return;
    }

    _updateUnclaimedStETH(account);

    uint256 toWithdraw = amount;

    LockingData storage lockData = locks[account];

    uint256 pos = 0;
    while (toWithdraw > 0) {
      if (pos >= lockData.positions.length) {
        revert UnavailableWithdrawAmount(amount);
      }

      LockedPosition storage position = lockData.positions[pos];

      if (currentEpoch < position.startEpoch + position.duration) {
        pos++;
      } else {
        uint256 availableToWithdraw = _availableToWithdrawFromPosition(position);

        if (availableToWithdraw > toWithdraw) {
          position.withdrawnChiAmount += toWithdraw;
          toWithdraw = 0;
        } else {
          position.withdrawnChiAmount += toWithdraw;
          toWithdraw -= availableToWithdraw;
          _removePosition(lockData, pos);
        }
      }
    }

    totalUnlockedChi -= amount;
    chi.safeTransfer(toAddress, amount);

    emit WithdrawChiFromAccount(account, toAddress, amount);
  }

  /// @inheritdoc IChiLocking
  function unclaimedStETHAmount(address account) public view returns (uint256 totalAmount) {
    LockingData storage lockData = locks[account];
    totalAmount = lockData.unclaimedStETH;
    if (lockData.lastUpdatedEpoch == currentEpoch) return totalAmount;

    for (uint256 i = 0; i < lockData.positions.length; i++) {
      totalAmount += _getUnclaimedStETHPositionAmount(lockData.positions[i], lockData.lastUpdatedEpoch);
    }
  }

  /// @inheritdoc IChiLocking
  function getVotingPower(address account) public view returns (uint256) {
    uint256 votingPower = 0;
    LockingData storage lockData = locks[account];
    for (uint256 i = 0; i < lockData.positions.length; i++) {
      votingPower += _getCurrentVotingPowerForPosition(lockData.positions[i]);
    }
    return votingPower;
  }

  function _getNumberOfShares(uint256 chiAmount) internal view returns (uint256) {
    if (totalLockedChi == 0) return chiAmount;
    return Math.mulDiv(chiAmount, totalLockedShares, totalLockedChi);
  }

  function _updateUnclaimedStETH(address account) internal {
    locks[account].unclaimedStETH = unclaimedStETHAmount(account);
    locks[account].lastUpdatedEpoch = currentEpoch;
  }

  function _getUnclaimedStETHPositionAmount(
    LockedPosition storage position,
    uint256 lastUpdated
  ) internal view returns (uint256 unclaimedAmount) {
    if (lastUpdated == currentEpoch) return 0;

    uint256 fromEpoch = lastUpdated < position.startEpoch ? position.startEpoch : lastUpdated;
    uint256 toEpoch = currentEpoch - 1;

    uint256 lockEndsInEpoch = position.startEpoch + position.duration - 1;
    if (fromEpoch <= lockEndsInEpoch) {
      if (toEpoch <= lockEndsInEpoch) {
        unclaimedAmount += _unclaiemdStETHDuringLocked(position, fromEpoch, toEpoch);
        return unclaimedAmount;
      } else {
        unclaimedAmount += _unclaiemdStETHDuringLocked(position, fromEpoch, lockEndsInEpoch);
      }

      fromEpoch = lockEndsInEpoch + 1;
    }

    unclaimedAmount += _unclaiemdStETHAfterLocked(position, fromEpoch, toEpoch);
  }

  function _unclaiemdStETHDuringLocked(
    LockedPosition storage position,
    uint256 fromEpoch,
    uint256 toEpoch
  ) internal view returns (uint256) {
    uint256 rewardPerShare = epochs[toEpoch].cumulativeStETHPerLockedShare -
      epochs[fromEpoch - 1].cumulativeStETHPerLockedShare;

    return Math.mulDiv(rewardPerShare, position.shares, 1e18);
  }

  function _unclaiemdStETHAfterLocked(
    LockedPosition storage position,
    uint256 fromEpoch,
    uint256 toEpoch
  ) internal view returns (uint256) {
    uint256 unlockedChiAmount = _availableToWithdrawFromPosition(position);
    uint256 rewardPerChi = epochs[toEpoch].cumulativeStETHPerUnlocked -
      epochs[fromEpoch - 1].cumulativeStETHPerUnlocked;
    return Math.mulDiv(rewardPerChi, unlockedChiAmount, 1e18);
  }

  function _getCurrentVotingPowerForPosition(LockedPosition storage position) internal view returns (uint256) {
    if (currentEpoch >= position.startEpoch + position.duration || currentEpoch < position.startEpoch) return 0;
    uint256 epochsUntilEnd = position.startEpoch + position.duration - currentEpoch;

    if (currentEpoch == position.startEpoch) {
      return Math.mulDiv(position.amount, epochsUntilEnd, MAX_LOCK_DURATION);
    } else {
      return Math.mulDiv(_totalAccumulatedAtEpoch(currentEpoch - 1, position), epochsUntilEnd, MAX_LOCK_DURATION);
    }
  }

  function _availableToWithdrawFromPosition(LockedPosition storage position) internal view returns (uint256) {
    uint256 endLockingEpoch = position.startEpoch + position.duration - 1;
    if (currentEpoch <= endLockingEpoch) return 0;
    return _totalAccumulatedAtEpoch(endLockingEpoch, position) - position.withdrawnChiAmount;
  }

  function _totalAccumulatedAtEpoch(uint256 epochNum, LockedPosition storage position) internal view returns (uint256) {
    uint256 endEpoch = position.startEpoch + position.duration - 1;
    if (endEpoch < epochNum) epochNum = endEpoch;

    EpochData storage epoch = epochs[epochNum];
    return Math.mulDiv(position.shares, epoch.totalLockedChiInEpoch, epoch.lockedSharesInEpoch);
  }

  function _removePosition(LockingData storage lockData, uint256 pos) internal {
    lockData.positions[pos] = lockData.positions[lockData.positions.length - 1];
    lockData.positions.pop();
  }
}

contracts/staking/ChiStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../interfaces/IChiStaking.sol";
import "../interfaces/IChiLocking.sol";
import "./StakingWithEpochs.sol";

/// @title Contract for staking chi tokens
/// @notice This contract holds staked CHI tokens, staking is done per epoch
/// @dev This contract is upgradeable
contract ChiStaking is IChiStaking, OwnableUpgradeable, StakingWithEpochs {
  using SafeERC20 for IERC20;

  uint256 public constant MIN_LOCK_DURATION = 4; // 4 epochs = 4 weeks = 1 month
  uint256 public constant MAX_LOCK_DURATION = 208; // 208 epochs = 208 weeks = 4 years

  IChiLocking public chiLocking;

  function initialize(IERC20 _chiAddress) external initializer {
    __Ownable_init();
    __StakingWithEpochs_init("Staked CHI", "stCHI", _chiAddress);
  }

  /// @inheritdoc IChiStaking
  function setChiLocking(IChiLocking _chiLocking) external onlyOwner {
    chiLocking = _chiLocking;
    emit SetChiLocking(address(_chiLocking));
  }

  /// @inheritdoc IStaking
  function setRewardController(address _rewardController) external onlyOwner {
    _setRewardController(_rewardController);
    emit SetRewardController(_rewardController);
  }

  /// @inheritdoc IStaking
  function getStakedChi() external view returns (uint256) {
    return epochs[currentEpoch].shares;
  }

  /// @inheritdoc IChiStaking
  function updateEpoch(uint256 stETHrewards) external onlyRewardController {
    _updateCumulativeRewardsForToken(epochs[currentEpoch], epochs[currentEpoch - 1], RewardToken.STETH, stETHrewards);
    _updateEpoch();
  }

  /// @inheritdoc IStakingWithEpochs
  function unstake(uint256 amount) public override(IStakingWithEpochs, StakingWithEpochs) {
    unstake(amount, msg.sender);
  }

  /// @inheritdoc IStakingWithEpochs
  function unstake(uint256 amount, address toAddress) public override(IStakingWithEpochs, StakingWithEpochs) {
    uint256 availableOnLocking = chiLocking.availableChiWithdraw(msg.sender);
    if (amount <= availableOnLocking) {
      chiLocking.withdrawChiFromAccountToAddress(msg.sender, toAddress, amount);
    } else {
      chiLocking.withdrawChiFromAccountToAddress(msg.sender, toAddress, availableOnLocking);
      StakingWithEpochs.unstake(amount - availableOnLocking, toAddress);
    }
  }

  /// @inheritdoc IChiStaking
  function lock(uint256 amount, uint256 duration, bool useStakedTokens) external {
    if (duration < MIN_LOCK_DURATION || duration > MAX_LOCK_DURATION) {
      revert InvalidDuration(duration);
    }

    if (!useStakedTokens) {
      stakeToken.safeTransferFrom(msg.sender, address(chiLocking), amount);
      chiLocking.lockChi(msg.sender, amount, duration);
    } else {
      unstake(amount, address(chiLocking));
      chiLocking.lockChi(msg.sender, amount, duration + 1);
    }

    emit Lock(msg.sender, amount, duration, useStakedTokens);
  }

  /// @inheritdoc IStaking
  function claimStETH(address account) external onlyRewardController returns (uint256) {
    uint256 amount = _claimAndUpdateReward(account, RewardToken.STETH);
    emit ClaimStETH(account, amount);
    return amount;
  }

  /// @inheritdoc IStaking
  function unclaimedStETHAmount(address account) public view returns (uint256) {
    return _getCurrentReward(account, RewardToken.STETH);
  }

  function _calculatingRewards(RewardToken token) internal pure override returns (bool) {
    if (token == RewardToken.STETH) return true;
    return false;
  }
}

contracts/staking/ChiVesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../interfaces/IChiVesting.sol";

/// @title Contract for vesting chi tokens
/// @notice This contract holds chi tokens that are vested
/// @notice This contract hold vested chi tokens, vesting is done on epochs
/// @dev This contract is upgradeable
contract ChiVesting is IChiVesting, OwnableUpgradeable {
  using SafeERC20 for IERC20;

  uint256 public constant MAX_LOCK_DURATION = 208;
  IERC20 public chi;

  uint256 public cliffDuration;
  uint256 public vestingDuration;
  uint256 public totalLockedChi;
  uint256 public totalUnlockedChi;
  uint256 public totalShares;
  uint256 public currentEpoch;
  uint256 public totalVotingPower;
  address public rewardController;

  mapping(address account => bool status) public chiVesters;
  mapping(address account => VestingData) public vestingData;
  mapping(uint256 id => EpochData) public epochs;

  modifier onlyRewardController() {
    if (msg.sender != rewardController) {
      revert NotRewardController();
    }
    _;
  }

  modifier onlyChiVesters() {
    if (!chiVesters[msg.sender]) {
      revert NotChiVester();
    }
    _;
  }

  function initialize(IERC20 _chi, uint256 _cliffDuration, uint256 _vestingDuration) external initializer {
    __Ownable_init();

    chi = _chi;
    cliffDuration = _cliffDuration;
    vestingDuration = _vestingDuration;
    currentEpoch = 1;
  }

  /// @inheritdoc IChiVesting
  function setRewardController(address _rewardController) external onlyOwner {
    rewardController = _rewardController;
    emit SetRewardController(_rewardController);
  }

  /// @inheritdoc IChiVesting
  function setChiVester(address contractAddress, bool toSet) external onlyOwner {
    chiVesters[contractAddress] = toSet;
    emit SetChiVester(contractAddress, toSet);
  }

  /// @inheritdoc IChiVesting
  function getLockedChi() public view returns (uint256) {
    return totalLockedChi;
  }

  /// @inheritdoc IChiVesting
  function addVesting(address account, uint256 chiAmount) external onlyChiVesters {
    if (currentEpoch > cliffDuration) {
      revert CliffPassed();
    }

    _updateUnclaimedStETH(account);

    uint256 shares = _getNumberOfShares(chiAmount);
    VestingData storage vesting = vestingData[account];
    vesting.shares += shares;
    vesting.startAmount += chiAmount;

    totalLockedChi += chiAmount;
    totalShares += shares;

    totalVotingPower += Math.mulDiv(chiAmount, _epochsUntilEnd(), MAX_LOCK_DURATION);

    emit AddVesting(account, chiAmount, shares);
  }

  /// @inheritdoc IChiVesting
  function updateEpoch(uint256 chiEmissions, uint256 stETHrewards) external onlyRewardController {
    EpochData storage epoch = epochs[currentEpoch];

    uint256 stETHRewardPerShare;
    if (totalShares != 0) {
      stETHRewardPerShare = Math.mulDiv(stETHrewards, 1e18, totalShares);
    }
    epoch.cumulativeStETHRewardPerShare = epochs[currentEpoch - 1].cumulativeStETHRewardPerShare + stETHRewardPerShare;

    uint256 epochsUntilEnd = _epochsUntilEnd();

    totalLockedChi += chiEmissions;

    if (epochsUntilEnd > 0) {
      totalVotingPower += Math.mulDiv(chiEmissions, _epochsUntilEnd(), MAX_LOCK_DURATION);
    }

    uint256 amountToUnlock;
    if (totalLockedChi > 0 && currentEpoch > cliffDuration && epochsUntilEnd > 0) {
      amountToUnlock = totalLockedChi / _epochsUntilEnd();
    }

    uint256 amountUnlockedPerShare;
    if (totalShares > 0) {
      amountUnlockedPerShare = Math.mulDiv(amountToUnlock, 1e18, totalShares);
    }
    epoch.cumulativeUnlockedPerShare = epochs[currentEpoch - 1].cumulativeUnlockedPerShare + amountUnlockedPerShare;

    totalLockedChi -= amountToUnlock;
    totalUnlockedChi += amountToUnlock;

    if (epochsUntilEnd > 0) {
      // decrease for unlocked tokens
      uint256 decreaseVotingPower = Math.mulDiv(amountToUnlock, epochsUntilEnd, MAX_LOCK_DURATION);
      if (decreaseVotingPower > totalVotingPower) {
        totalVotingPower = 0;
      } else {
        totalVotingPower -= decreaseVotingPower;
      }

      // regular decrease in voting power because of less time until end of locking
      totalVotingPower = Math.mulDiv(totalVotingPower, epochsUntilEnd - 1, epochsUntilEnd);
    }

    currentEpoch++;
    emit UpdateEpoch(currentEpoch - 1, stETHrewards, totalLockedChi);
  }

  /// @inheritdoc IChiVesting
  function withdrawChi(uint256 amount) external {
    VestingData storage vesting = vestingData[msg.sender];

    _updateAvailabeWithdraw(msg.sender);
    if (amount > vesting.unlockedChi) {
      revert UnavailableWithdrawAmount(amount);
    }

    vesting.unlockedChi -= amount;
    totalUnlockedChi -= amount;

    chi.safeTransfer(msg.sender, amount);

    emit WithdrawChi(msg.sender, amount);
  }

  /// @inheritdoc IChiVesting
  function claimStETH(address account) external onlyRewardController returns (uint256) {
    _updateUnclaimedStETH(account);
    uint256 amount = vestingData[account].unclaimedStETH;
    vestingData[account].unclaimedStETH = 0;

    emit ClaimStETH(account, amount);
    return amount;
  }

  /// @inheritdoc IChiVesting
  function unclaimedStETHAmount(address account) public view returns (uint256) {
    VestingData storage vesting = vestingData[account];
    uint256 totalAmount = vesting.unclaimedStETH;

    uint256 rewardPerShare = epochs[currentEpoch - 1].cumulativeStETHRewardPerShare -
      epochs[vesting.lastClaimedEpoch].cumulativeStETHRewardPerShare;
    totalAmount += Math.mulDiv(rewardPerShare, vesting.shares, 1e18);

    return totalAmount;
  }

  /// @inheritdoc IChiVesting
  function getVotingPower(address account) external view returns (uint256) {
    if (currentEpoch > cliffDuration + vestingDuration) return 0;
    VestingData storage vesting = vestingData[account];
    return vesting.shares != 0 ? Math.mulDiv(totalVotingPower, vesting.shares, totalShares) : 0;
  }

  /// @inheritdoc IChiVesting
  function getTotalVotingPower() external view returns (uint256) {
    return totalVotingPower;
  }

  /// @inheritdoc IChiVesting
  function availableChiWithdraw(address account) public view returns (uint256) {
    VestingData storage vesting = vestingData[account];
    return vesting.unlockedChi + _availableUnlockFromTo(vesting.lastWithdrawnEpoch, currentEpoch - 1, vesting.shares);
  }

  // includes available amount in `toEpoch`
  function _availableUnlockFromTo(uint256 fromEpoch, uint256 toEpoch, uint256 shares) internal view returns (uint256) {
    uint256 availableWithdrawPerShare = epochs[toEpoch].cumulativeUnlockedPerShare -
      epochs[fromEpoch].cumulativeUnlockedPerShare;
    return Math.mulDiv(availableWithdrawPerShare, shares, 1e18);
  }

  function _getNumberOfShares(uint256 chiAmount) internal view returns (uint256) {
    if (totalLockedChi == 0) return chiAmount;
    return Math.mulDiv(chiAmount, totalShares, totalLockedChi);
  }

  function _updateAvailabeWithdraw(address account) internal {
    vestingData[account].unlockedChi = availableChiWithdraw(account);
    vestingData[account].lastWithdrawnEpoch = currentEpoch - 1;
  }

  function _updateUnclaimedStETH(address account) internal {
    vestingData[account].unclaimedStETH = unclaimedStETHAmount(account);
    vestingData[account].lastClaimedEpoch = currentEpoch - 1;
  }

  function _epochsUntilEnd() internal view returns (uint256) {
    if (currentEpoch > cliffDuration + vestingDuration + 1) return 0;
    return cliffDuration + vestingDuration + 1 - currentEpoch;
  }
}

contracts/staking/LPStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../interfaces/ILPStaking.sol";
import "../interfaces/IChiLocking.sol";
import "./StakingWithEpochs.sol";

/// @title Contract for staking USC/ETH and CHI/ETH LP tokens
/// @notice Staking is done in epochs
/// @dev Contract is upgradeable
contract LPStaking is ILPStaking, OwnableUpgradeable, StakingWithEpochs {
  using SafeERC20 for IERC20;

  uint256 public constant MIN_LOCK_DURATION = 4;
  uint256 public constant MAX_LOCK_DURATION = 208;

  IERC20 public chi;
  IChiLocking public chiLockingContract;

  uint256 public chiAmountFromEmissions;

  function initialize(
    IERC20 _chi,
    IChiLocking _chiLockingContract,
    IERC20 _token,
    string memory _name,
    string memory _symbol
  ) external initializer {
    __Ownable_init();
    __StakingWithEpochs_init(_name, _symbol, _token);

    chi = _chi;
    chiLockingContract = _chiLockingContract;
  }

  /// @notice Sets reward controller contract address
  /// @param _rewardController Reward controller contract address
  function setRewardController(address _rewardController) external onlyOwner {
    _setRewardController(_rewardController);
  }

  /// @notice Updates epoch data
  /// @param chiEmissions Amount of CHI token incentives emitted in current epoch for USC stakers
  /// @custom:usage This function should be called from rewardController contract in purpose of updating epoch data
  function updateEpoch(uint256 chiEmissions, uint256 stETHrewards) external onlyRewardController {
    EpochData storage epoch = epochs[currentEpoch];
    EpochData storage prevEpoch = epochs[currentEpoch - 1];

    _updateCumulativeRewardsForToken(epoch, prevEpoch, RewardToken.CHI, chiEmissions);
    _updateCumulativeRewardsForToken(epoch, prevEpoch, RewardToken.STETH, stETHrewards);
    _updateEpoch();

    chiAmountFromEmissions += chiEmissions;

    emit UpdateEpoch(currentEpoch - 1, chiEmissions);
  }

  /// @inheritdoc IStaking
  function claimStETH(address account) external onlyRewardController returns (uint256) {
    uint256 amount = _claimAndUpdateReward(account, RewardToken.STETH);
    emit ClaimStETH(account, amount);
    return amount;
  }

  /// @inheritdoc IStaking
  function getStakedChi() external view returns (uint256) {
    return chiAmountFromEmissions;
  }

  /// @inheritdoc IStaking
  function unclaimedStETHAmount(address account) public view returns (uint256) {
    return _getCurrentReward(account, RewardToken.STETH);
  }

  /// @notice Locks CHI tokens that user earned from incentives for given duration
  /// @param duration Locking duration in epochs
  function lockChi(uint256 duration) external {
    if (duration < MIN_LOCK_DURATION || duration > MAX_LOCK_DURATION) {
      revert InvalidDuration(duration);
    }

    uint256 amount = _claimAndUpdateReward(msg.sender, RewardToken.CHI);
    chiLockingContract.lockChi(msg.sender, amount, duration);

    chiAmountFromEmissions -= amount;

    chi.safeTransfer(address(chiLockingContract), amount);

    emit LockChi(msg.sender, amount, duration);
  }

  function _calculatingRewards(RewardToken token) internal pure override returns (bool) {
    if (token == RewardToken.CHI) return true;
    if (token == RewardToken.STETH) return true;
    return false;
  }
}

