// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../interfaces/IRewardController.sol";
import "../interfaces/IMintableERC20.sol";
import "../interfaces/IBurnableERC20.sol";
import "../interfaces/IArbitrage.sol";
import "../interfaces/IReserveHolder.sol";
import "../interfaces/IStaking.sol";
import "../interfaces/IUSCStaking.sol";
import "../interfaces/IChiStaking.sol";
import "../interfaces/IChiLocking.sol";
import "../interfaces/IChiVesting.sol";
import "../interfaces/ILPStaking.sol";

/// @title Contract for managing rewards
/// @notice This contract manages rewards for chi lockers, chi stakers, chi vesters and usc stakers
/// @notice This contract holds chi incentives for all contracts and distributes then at the end of epoch
/// @dev This contract is upgradeable
contract RewardControllerV2 is IRewardController, OwnableUpgradeable {
  using SafeERC20 for IERC20;

  uint256 public constant EPOCH_DURATION = 1 weeks;

  IERC20 public chi;
  IERC20 public usc;
  IReserveHolder public reserveHolder;
  IArbitrage public arbitrager;
  IUSCStaking public uscStaking;
  IChiStaking public chiStaking;
  IChiLocking public chiLocking;
  IChiVesting public chiVesting;
  ILPStaking public uscEthLPStaking;
  ILPStaking public chiEthLPStaking;

  uint256 public currentEpoch;
  uint256 public firstEpochTimestamp;
  uint256 public chiIncentivesForChiLocking;
  uint256 public chiIncentivesForUscStaking;
  uint256 public chiIncentivesForUscEthLPStaking;
  uint256 public chiIncentivesForChiEthLPStaking;

  mapping(uint256 id => EpochData) public epochs;

  // Upgrade
  uint256 public constant MAX_PERCENTAGE = 100_00;
  uint256 public uscStakingProtocolFee; // Protocol fee from USC staking rewards, maximum 100_00 = 100%

  // Upgrade
  mapping(address => bool) public isArbitrager;

  // Upgrade
  uint256 public stEthPercentageForUscStaking;
  uint256 public stEthPercentageForChiStaking;
  uint256 public stEthPercentageForChiLocking;
  uint256 public stEthPercentageForChiVesting;
  uint256 public stEthPercentageForUscEthLPStaking;
  uint256 public stEthPercentageForChiEthLPStaking;

  modifier onlyArbitrager() {
    if (!isArbitrager[msg.sender] && address(arbitrager) != msg.sender) {
      revert NotArbitrager();
    }
    _;
  }

  function initialize(
    IERC20 _chi,
    IERC20 _usc,
    IReserveHolder _reserveHolder,
    IUSCStaking _uscStaking,
    IChiStaking _chiStaking,
    IChiLocking _chiLocking,
    IChiVesting _chiVesting,
    ILPStaking _uscEthLPStaking,
    ILPStaking _chiEthLPStaking,
    uint256 _firstEpochTimestamp
  ) external initializer {
    __Ownable_init();
    chi = _chi;
    usc = _usc;
    reserveHolder = _reserveHolder;
    uscStaking = _uscStaking;
    chiStaking = _chiStaking;
    chiLocking = _chiLocking;
    chiVesting = _chiVesting;
    uscEthLPStaking = _uscEthLPStaking;
    chiEthLPStaking = _chiEthLPStaking;
    firstEpochTimestamp = _firstEpochTimestamp;
    currentEpoch = 1;
  }

  /// @inheritdoc IRewardController
  function setStEthPercentages(
    uint256 _stEthPercentageForUscStaking,
    uint256 _stEthPercentageForChiStaking,
    uint256 _stEthPercentageForChiLocking,
    uint256 _stEthPercentageForChiVesting,
    uint256 _stEthPercentageForUscEthLPStaking,
    uint256 _stEthPercentageForChiEthLPStaking
  ) external onlyOwner {
    uint256 sumOfPercentages = _stEthPercentageForUscStaking +
      _stEthPercentageForChiStaking +
      _stEthPercentageForChiLocking +
      _stEthPercentageForChiVesting +
      _stEthPercentageForUscEthLPStaking +
      _stEthPercentageForChiEthLPStaking;

    if (sumOfPercentages != MAX_PERCENTAGE) {
      revert StEthYieldPercentagesNotCorrect();
    }

    stEthPercentageForUscStaking = _stEthPercentageForUscStaking;
    stEthPercentageForChiStaking = _stEthPercentageForChiStaking;
    stEthPercentageForChiLocking = _stEthPercentageForChiLocking;
    stEthPercentageForChiVesting = _stEthPercentageForChiVesting;
    stEthPercentageForUscEthLPStaking = _stEthPercentageForUscEthLPStaking;
    stEthPercentageForChiEthLPStaking = _stEthPercentageForChiEthLPStaking;
  }

  /// @inheritdoc IRewardController
  function setChiIncentivesForChiLocking(uint256 _chiIncentivesForChiLocking) external onlyOwner {
    chiIncentivesForChiLocking = _chiIncentivesForChiLocking;
  }

  /// @inheritdoc IRewardController
  function setChiIncentivesForUscStaking(uint256 _chiIncentivesForUscStaking) external onlyOwner {
    chiIncentivesForUscStaking = _chiIncentivesForUscStaking;
  }

  /// @inheritdoc IRewardController
  function setChiIncentivesForUscEthLPStaking(uint256 _chiIncentivesForUscEthLPStaking) external onlyOwner {
    chiIncentivesForUscEthLPStaking = _chiIncentivesForUscEthLPStaking;
  }

  /// @inheritdoc IRewardController
  function setChiIncentivesForChiEthLPStaking(uint256 _chiIncentivesForChiEthLPStaking) external onlyOwner {
    chiIncentivesForChiEthLPStaking = _chiIncentivesForChiEthLPStaking;
  }

  /// @inheritdoc IRewardController
  function setArbitrager(IArbitrage _arbitrager) external onlyOwner {
    arbitrager = _arbitrager;
    emit SetArbitrager(address(_arbitrager));
  }

  function updateArbitrager(address account, bool status) external onlyOwner {
    isArbitrager[account] = status;
    emit UpdateArbitrager(account, status);
  }

  function setUscStakingProtocolFee(uint256 _uscStakingProtocolFee) external onlyOwner {
    uscStakingProtocolFee = _uscStakingProtocolFee;
  }

  /// @inheritdoc IRewardController
  function rewardUSC(uint256 amount) external onlyArbitrager {
    if (amount == 0) {
      revert ZeroAmount();
    }

    usc.safeTransferFrom(msg.sender, address(this), amount);
    epochs[currentEpoch].totalUscReward += amount;

    emit RewardUSC(msg.sender, amount);
  }

  /// @inheritdoc IRewardController
  function updateEpoch() public {
    if (block.timestamp < firstEpochTimestamp + currentEpoch * EPOCH_DURATION) {
      revert EpochNotFinished();
    }

    if (
      stEthPercentageForUscStaking +
        stEthPercentageForChiStaking +
        stEthPercentageForChiLocking +
        stEthPercentageForChiVesting +
        stEthPercentageForUscEthLPStaking +
        stEthPercentageForChiEthLPStaking !=
      MAX_PERCENTAGE
    ) {
      revert StEthYieldPercentagesNotCorrect();
    }

    uint256 totalUscRewards = epochs[currentEpoch].totalUscReward;
    uint256 uscProtocolFee = Math.mulDiv(totalUscRewards, uscStakingProtocolFee, MAX_PERCENTAGE);
    usc.safeTransfer(owner(), uscProtocolFee);

    epochs[currentEpoch].totalUscReward -= uscProtocolFee;

    StETHRewards memory stEthRewards = _updateAndGetStETHRewards();
    ChiIncentives memory chiIncentives = _updateAndGetChiIncentives();

    _updateEpochsInSubcontracts(stEthRewards, chiIncentives, epochs[currentEpoch].totalUscReward);

    usc.safeTransfer(address(uscStaking), epochs[currentEpoch].totalUscReward);
    chi.safeTransfer(address(uscStaking), chiIncentives.uscStakingChiIncentives);
    chi.safeTransfer(address(chiLocking), chiIncentives.chiLockingChiIncentives);
    chi.safeTransfer(address(chiVesting), chiIncentives.chiVestingChiIncentives);
    chi.safeTransfer(address(uscEthLPStaking), chiIncentivesForUscEthLPStaking);
    chi.safeTransfer(address(chiEthLPStaking), chiIncentivesForChiEthLPStaking);

    currentEpoch++;

    uint256 totalStEthRewards = stEthRewards.uscStakingStEthReward +
      stEthRewards.chiStakingStEthReward +
      stEthRewards.chiLockingStEthReward +
      stEthRewards.chiVestingStEthReward;
    uint256 totalChiIncentives = chiIncentives.uscStakingChiIncentives +
      chiIncentives.chiLockingChiIncentives +
      chiIncentives.chiVestingChiIncentives +
      chiIncentivesForUscEthLPStaking +
      chiIncentivesForChiEthLPStaking;
    emit UpdateEpoch(currentEpoch - 1, totalStEthRewards, totalChiIncentives);
  }

  /// @inheritdoc IRewardController
  function claimStEth() external {
    uint256 totalAmount;
    totalAmount += IStaking(address(uscStaking)).claimStETH(msg.sender);
    totalAmount += IStaking(address(chiStaking)).claimStETH(msg.sender);
    totalAmount += IStaking(address(chiLocking)).claimStETH(msg.sender);
    totalAmount += IStaking(address(chiVesting)).claimStETH(msg.sender);
    totalAmount += IStaking(address(uscEthLPStaking)).claimStETH(msg.sender);
    totalAmount += IStaking(address(chiEthLPStaking)).claimStETH(msg.sender);

    reserveHolder.claimRewards(msg.sender, totalAmount);

    emit ClaimStEth(msg.sender, totalAmount);
  }

  /// @inheritdoc IRewardController
  function unclaimedStETHAmount(address account) external view returns (uint256) {
    uint256 totalAmount;
    totalAmount += IStaking(address(uscStaking)).unclaimedStETHAmount(account);
    totalAmount += IStaking(address(chiStaking)).unclaimedStETHAmount(account);
    totalAmount += IStaking(address(chiLocking)).unclaimedStETHAmount(account);
    totalAmount += IStaking(address(chiVesting)).unclaimedStETHAmount(account);
    totalAmount += IStaking(address(uscEthLPStaking)).unclaimedStETHAmount(account);
    totalAmount += IStaking(address(chiEthLPStaking)).unclaimedStETHAmount(account);

    return totalAmount;
  }

  function _updateAndGetChiIncentives() internal view returns (ChiIncentives memory) {
    uint256 chiLockingLocked = chiLocking.getLockedChi();
    uint256 chiVestingLocked = chiVesting.getLockedChi();
    uint256 totalLockedChi = chiLockingLocked + chiVestingLocked;

    uint256 chiLockingChiIncentives;
    uint256 chiVestingChiIncentives;
    if (totalLockedChi != 0) {
      chiLockingChiIncentives = Math.mulDiv(chiLockingLocked, chiIncentivesForChiLocking, totalLockedChi);
      chiVestingChiIncentives = Math.mulDiv(chiVestingLocked, chiIncentivesForChiLocking, totalLockedChi);
    }

    return
      ChiIncentives({
        uscStakingChiIncentives: chiIncentivesForUscStaking,
        chiLockingChiIncentives: chiLockingChiIncentives,
        chiVestingChiIncentives: chiVestingChiIncentives
      });
  }

  function _updateEpochsInSubcontracts(
    StETHRewards memory stEthRewards,
    ChiIncentives memory chiIncentives,
    uint256 uscReward
  ) internal {
    uscStaking.updateEpoch(chiIncentives.uscStakingChiIncentives, uscReward, stEthRewards.uscStakingStEthReward);
    chiStaking.updateEpoch(stEthRewards.chiStakingStEthReward);
    chiLocking.updateEpoch(chiIncentives.chiLockingChiIncentives, stEthRewards.chiLockingStEthReward);
    chiVesting.updateEpoch(chiIncentives.chiVestingChiIncentives, stEthRewards.chiVestingStEthReward);
    uscEthLPStaking.updateEpoch(chiIncentivesForUscEthLPStaking, stEthRewards.uscEthLPStakingStEthReward);
    chiEthLPStaking.updateEpoch(chiIncentivesForChiEthLPStaking, stEthRewards.chiEthLPStakingStEthReward);
  }

  function _updateAndGetStETHRewards() internal returns (StETHRewards memory) {
    epochs[currentEpoch].reserveHolderTotalRewards = reserveHolder.getCumulativeRewards();
    uint256 stETHEpochrewards = epochs[currentEpoch].reserveHolderTotalRewards -
      epochs[currentEpoch - 1].reserveHolderTotalRewards;

    uint256 uscStakingStEthReward = Math.mulDiv(stETHEpochrewards, stEthPercentageForUscStaking, MAX_PERCENTAGE);
    uint256 chiStakingStEthReward = Math.mulDiv(stETHEpochrewards, stEthPercentageForChiStaking, MAX_PERCENTAGE);
    uint256 chiLockingStEthReward = Math.mulDiv(stETHEpochrewards, stEthPercentageForChiLocking, MAX_PERCENTAGE);
    uint256 chiVestingStEthReward = Math.mulDiv(stETHEpochrewards, stEthPercentageForChiVesting, MAX_PERCENTAGE);
    uint256 uscEthLPStakingStEthReward = Math.mulDiv(
      stETHEpochrewards,
      stEthPercentageForUscEthLPStaking,
      MAX_PERCENTAGE
    );
    uint256 chiEthLPStakingStEthReward = Math.mulDiv(
      stETHEpochrewards,
      stEthPercentageForChiEthLPStaking,
      MAX_PERCENTAGE
    );

    return
      StETHRewards({
        uscStakingStEthReward: uscStakingStEthReward,
        chiStakingStEthReward: chiStakingStEthReward,
        chiLockingStEthReward: chiLockingStEthReward,
        chiVestingStEthReward: chiVestingStEthReward,
        uscEthLPStakingStEthReward: uscEthLPStakingStEthReward,
        chiEthLPStakingStEthReward: chiEthLPStakingStEthReward
      });
  }
}

contracts/staking/StakingWithEpochs.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../interfaces/IStakingWithEpochs.sol";
import "../interfaces/IMintableERC20.sol";
import "../interfaces/IBurnableERC20.sol";
import "../interfaces/IArbitrage.sol";

/// @title Contract for staking tokens
/// @notice Staking logic is placed inside this contract
/// @dev This contract is abstract and CHIStaking and USCStaking should inherit from him
abstract contract StakingWithEpochs is IStakingWithEpochs, ERC20Upgradeable {
  using SafeERC20 for IERC20;

  IERC20 public stakeToken;

  address public rewardController;
  uint256 public currentEpoch;

  mapping(address account => StakeData) public stakes;
  mapping(uint256 id => EpochData) public epochs;

  modifier onlyRewardController() {
    if (msg.sender != rewardController) {
      revert NotRewardController();
    }
    _;
  }

  function __StakingWithEpochs_init(
    string memory _name,
    string memory _symbol,
    IERC20 _stakeToken
  ) internal onlyInitializing {
    __ERC20_init(_name, _symbol);
    stakeToken = _stakeToken;
    currentEpoch = 1;
  }

  /// @inheritdoc IStakingWithEpochs
  function getUnclaimedRewards(address account, RewardToken token) external view returns (uint256) {
    return stakes[account].unclaimedRewards[token];
  }

  /// @inheritdoc IStakingWithEpochs
  function getCumulativeRewardsPerShare(uint256 epoch, RewardToken token) external view returns (uint256) {
    return epochs[epoch].cumulativeRewardsPerShare[token];
  }

  function unclaimedStChiAmount(address account) public view returns (uint256) {
    return _getCurrentReward(account, RewardToken.CHI);
  }

  /// @inheritdoc IStakingWithEpochs
  function stake(uint256 amount) external {
    if (amount == 0) {
      revert ZeroAmount();
    }

    stakeToken.safeTransferFrom(msg.sender, address(this), amount);
    _updateUnclaimedRewards(msg.sender);

    stakes[msg.sender].addSharesNextEpoch += amount;
    epochs[currentEpoch + 1].shares += amount;
    _mint(msg.sender, amount);

    emit Stake(msg.sender, amount);
  }

  /// @inheritdoc IStakingWithEpochs
  function unstake(uint256 amount) public virtual {
    unstake(amount, msg.sender);
  }

  /// @inheritdoc IStakingWithEpochs
  function unstake(uint256 amount, address toAddress) public virtual {
    if (amount == 0) {
      revert ZeroAmount();
    }

    StakeData storage stakeData = stakes[msg.sender];

    uint256 addSharesNextEpoch = stakeData.addSharesNextEpoch;
    if (stakeData.shares + addSharesNextEpoch < amount) {
      revert AmountBelowStakedBalance(stakeData.shares + addSharesNextEpoch, amount);
    }

    _updateUnclaimedRewards(msg.sender);
    addSharesNextEpoch = stakeData.addSharesNextEpoch;

    if (addSharesNextEpoch > amount) {
      stakeData.addSharesNextEpoch -= amount;
      epochs[currentEpoch + 1].shares -= amount;
    } else {
      uint256 fromCurrentShares = amount - addSharesNextEpoch;
      stakeData.shares -= fromCurrentShares;
      epochs[currentEpoch].shares -= fromCurrentShares;
      epochs[currentEpoch + 1].shares -= addSharesNextEpoch;
      stakeData.addSharesNextEpoch = 0;
    }

    stakeToken.safeTransfer(toAddress, amount);
    _burn(msg.sender, amount);

    emit Unstake(msg.sender, toAddress, amount);
  }

  function _updateUnclaimedRewards(address account) internal {
    StakeData storage stakeData = stakes[account];

    if (currentEpoch == stakeData.lastUpdatedEpoch) return;

    uint256 fromEpoch = stakeData.lastUpdatedEpoch > 0 ? stakeData.lastUpdatedEpoch - 1 : 0;
    _updateUnclaimedRewardsFromTo(stakeData, epochs[fromEpoch], epochs[currentEpoch - 1], stakeData.shares);

    if (stakeData.addSharesNextEpoch > 0) {
      _updateUnclaimedRewardsFromTo(
        stakeData,
        epochs[stakeData.lastUpdatedEpoch],
        epochs[currentEpoch - 1],
        stakeData.addSharesNextEpoch
      );
      stakeData.shares += stakeData.addSharesNextEpoch;
      stakeData.addSharesNextEpoch = 0;
    }

    stakeData.lastUpdatedEpoch = currentEpoch;
  }

  function _updateUnclaimedRewardsFromTo(
    StakeData storage stakeData,
    EpochData storage fromEpoch,
    EpochData storage toEpoch,
    uint256 shares
  ) internal {
    for (uint8 i = 0; i <= uint8(type(RewardToken).max); i++) {
      RewardToken token = RewardToken(i);
      if (_calculatingRewards(token)) {
        stakeData.unclaimedRewards[token] += Math.mulDiv(
          toEpoch.cumulativeRewardsPerShare[token] - fromEpoch.cumulativeRewardsPerShare[token],
          shares,
          1e18
        );
      }
    }
  }

  function _updateCumulativeRewardsForToken(
    EpochData storage epoch,
    EpochData storage prevEpoch,
    RewardToken token,
    uint256 amount
  ) internal {
    if (epoch.shares == 0) return;

    epoch.cumulativeRewardsPerShare[token] =
      prevEpoch.cumulativeRewardsPerShare[token] +
      Math.mulDiv(amount, 1e18, epoch.shares);
  }

  function _updateEpoch() internal {
    epochs[currentEpoch + 1].shares += epochs[currentEpoch].shares;
    currentEpoch++;
  }

  function _claimAndUpdateReward(address account, RewardToken token) internal returns (uint256) {
    _updateUnclaimedRewards(account);
    uint256 amount = stakes[account].unclaimedRewards[token];
    stakes[account].unclaimedRewards[token] = 0;
    return amount;
  }

  function _getCurrentReward(address account, RewardToken token) public view returns (uint256) {
    StakeData storage stakeData = stakes[account];
    uint256 totalAmount = stakeData.unclaimedRewards[token];

    if (currentEpoch == stakeData.lastUpdatedEpoch) return totalAmount;
    totalAmount += Math.mulDiv(
      epochs[currentEpoch - 1].cumulativeRewardsPerShare[token] -
        epochs[stakeData.lastUpdatedEpoch - 1].cumulativeRewardsPerShare[token],
      stakeData.shares,
      1e18
    );

    if (stakeData.addSharesNextEpoch > 0 && currentEpoch > stakeData.lastUpdatedEpoch + 1) {
      totalAmount += Math.mulDiv(
        epochs[currentEpoch - 1].cumulativeRewardsPerShare[token] -
          epochs[stakeData.lastUpdatedEpoch].cumulativeRewardsPerShare[token],
        stakeData.addSharesNextEpoch,
        1e18
      );
    }

    return totalAmount;
  }

  function _setRewardController(address _rewardController) internal {
    rewardController = _rewardController;
  }

  function _calculatingRewards(RewardToken token) internal virtual returns (bool);

  uint256[50] private __gap;
}

contracts/staking/USCStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../interfaces/IUSCStaking.sol";
import "../interfaces/IArbitrage.sol";
import "../interfaces/IChiLocking.sol";
import "./StakingWithEpochs.sol";

/// @title Contract for staking USC tokens
/// @notice Staking is done in epochs
/// @dev Contract is upgradeable
contract USCStaking is IUSCStaking, OwnableUpgradeable, StakingWithEpochs {
  using SafeERC20 for IERC20;

  uint256 public constant MIN_LOCK_DURATION = 4; // 4 epochs = 4 weeks = 1 month
  uint256 public constant MAX_LOCK_DURATION = 208; // 208 epochs = 208 weeks = 4 years

  IERC20 public chi;
  IChiLocking public chiLockingContract;

  uint256 public chiAmountFromEmissions;

  function initialize(IERC20 _usc, IERC20 _chi, IChiLocking _chiLockingContract) external initializer {
    __Ownable_init();
    __StakingWithEpochs_init("Staked USC", "stUSC", _usc);
    chi = _chi;
    chiLockingContract = _chiLockingContract;
  }

  /// @inheritdoc IStaking
  function setRewardController(address _rewardController) external onlyOwner {
    _setRewardController(_rewardController);
  }

  /// @inheritdoc IUSCStaking
  function updateEpoch(uint256 chiEmissions, uint256 uscRewards, uint256 stETHrewards) external onlyRewardController {
    EpochData storage epoch = epochs[currentEpoch];
    EpochData storage prevEpoch = epochs[currentEpoch - 1];

    _updateCumulativeRewardsForToken(epoch, prevEpoch, RewardToken.CHI, chiEmissions);
    _updateCumulativeRewardsForToken(epoch, prevEpoch, RewardToken.USC, uscRewards);
    _updateCumulativeRewardsForToken(epoch, prevEpoch, RewardToken.STETH, stETHrewards);
    _updateEpoch();

    chiAmountFromEmissions += chiEmissions;

    emit UpdateEpoch(currentEpoch - 1, chiEmissions, uscRewards, stETHrewards);
  }

  /// @inheritdoc IUSCStaking
  function lockChi(uint256 duration) external {
    if (duration < MIN_LOCK_DURATION || duration > MAX_LOCK_DURATION) {
      revert InvalidDuration(duration);
    }

    uint256 amount = _claimAndUpdateReward(msg.sender, RewardToken.CHI);
    chiLockingContract.lockChi(msg.sender, amount, duration);

    chiAmountFromEmissions -= amount;
    chi.safeTransfer(address(chiLockingContract), amount);

    emit LockChi(msg.sender, amount, duration);
  }

  /// @inheritdoc IUSCStaking
  function claimUSCRewards() external {
    uint256 amount = _claimAndUpdateReward(msg.sender, RewardToken.USC);
    stakeToken.safeTransfer(msg.sender, amount);

    emit ClaimUSCRewards(msg.sender, amount);
  }

  /// @inheritdoc IStaking
  function claimStETH(address account) external onlyRewardController returns (uint256) {
    uint256 amount = _claimAndUpdateReward(account, RewardToken.STETH);
    emit ClaimStETH(account, amount);
    return amount;
  }

  /// @inheritdoc IStaking
  function unclaimedStETHAmount(address account) public view returns (uint256) {
    return _getCurrentReward(account, RewardToken.STETH);
  }

  /// @inheritdoc IStaking
  function getStakedChi() external view returns (uint256) {
    return chiAmountFromEmissions;
  }

  function _calculatingRewards(RewardToken token) internal pure override returns (bool) {
    if (token == RewardToken.USC) return true;
    if (token == RewardToken.CHI) return true;
    if (token == RewardToken.STETH) return true;
    return false;
  }
}
