abi_encode_decode_simple.sol.content:
contract C {
    function f() public pure returns (uint256, bytes memory) {
        bytes memory arg = "abcdefg";
        return abi.decode(abi.encode(uint256(33), arg), (uint256, bytes));
    }
}
// ----
// f() -> 0x21, 0x40, 0x7, "abcdefg"

