mapping_to_struct.sol.content:
contract C {
    struct S {
        uint8 a;
        uint16 b;
        uint128 c;
        uint d;
    }
    mapping(uint => mapping(uint => S)) public x;
    constructor() {
        x[1][2].a = 3;
        x[1][2].b = 4;
        x[1][2].c = 5;
        x[1][2].d = 6;
    }
}
// ----
// x(uint256,uint256): 1, 2 -> 3, 4, 5, 6
// x(uint256,uint256): 0, 0 -> 0x00, 0x00, 0x00, 0x00

