delete_transient_state_variable.sol.content:
contract C {
    uint transient x;
    function f() public returns (uint) {
        x = 10;
        delete x;
        return x;
    }
}
// ====
// EVMVersion: >=cancun
// ----
// f() -> 0

transient_state_variable_cleanup_assignm.content:
contract C {
    uint8 transient x;
    function f() public returns(uint256 r) {
        uint8 y;
        assembly { y := 0xFFFF }
        x = y;
        assembly {
            r := tload(x.slot)
        }

    }
}
// ====
// EVMVersion: >=cancun
// ----
// f() -> 0xff
