for_loop_continue.sol.content:
contract C {
    function f() public returns (uint k) {
        assembly {
            for {let i := 0} lt(i, 10) { i := add(i, 1) }
            {
                if eq(mod(i, 2), 0) { continue }
                k := add(k, 1)
            }
        }
    }
}
// ----
// f() -> 5

for_loop_break.sol.content:
contract C {
    function f() public returns (uint i) {
        assembly {
            for {} lt(i, 10) { i := add(i, 1) }
            {
                if eq(i, 6) { break }
                i := add(i, 1)
            }
        }
    }
}
// ----
// f() -> 6
