rounding.sol.content:
contract C {
    int constant a = 7;
    int constant b = 3;
    int constant c = a / b;
    int constant d = (-a) / b;
    function f() public pure returns (uint, int, uint, int) {
        uint[c] memory x;
        uint[-d] memory y;
        return (x.length, c, y.length, -d);
    }
}
// ----
// f() -> 2, 2, 2, 2

