		x = 1; // This write used to be removed by the Yul optimizer due to the StorageWriteRemovalBeforeConditionalTermination bug.
		g();
		x = 2;
	}
	function g() internal {
		if (msg.data.length > 4) return;
		assembly { return(0, 0) }
	}
}
