increment_decrement.sol.content:
contract C {
    int immutable x = 1;
    int immutable y = 3;

    constructor() {
        x--;
        --x;
        y++;
        ++y;
        --y;
    }

    function f() public view returns (int, int) {
        return (x, y);
    }
}
// ----
// f() -> -1, 4

small_types_in_reverse.sol.content:
contract A {
	uint16 public immutable a;
	uint16 public immutable b;
	uint16 public immutable c;
	uint16[3] public x;
	constructor() {
		c = 0xffff;
		b = 0x0f0f;
		a = 0x1234;
		x = [a, b, c];
	}
}
// ----
// a() -> 4660
// b() -> 0x0f0f
// c() -> 0xffff
// x(uint256): 0 -> 4660
// x(uint256): 1 -> 0x0f0f
// x(uint256): 2 -> 0xffff
