function_modifier_empty.sol.content:
abstract contract A {
    function f() public mod returns (bool r) {
        return true;
    }

    modifier mod virtual;
}


contract C is A {
    modifier mod override {
        if (false) _;
    }
}
// ----
// f() -> false

return_in_modifier.sol.content:
contract C {
    uint256 public x;
    modifier run() {
        for (uint256 i = 1; i < 10; i++) {
            if (i == 5) return;
            _;
        }
    }

    function f() public run {
        uint256 k = x;
        uint256 t = k + 1;
        x = t;
    }
}
// ----
// x() -> 0
// f() ->
// x() -> 4

function_modifier_multi_with_return.sol.content:
// Note that return sets the return variable and jumps to the end of the current function or
// modifier code block.
contract C {
    modifier repeat(bool twice) {
        if (twice) _;
        _;
    }

    function f(bool twice) public repeat(twice) returns (uint256 r) {
        r += 1;
        return r;
    }
}
// via yul disabled because the return variables are
// fresh variables each time, while in the old code generator,
// they share a stack slot when the function is
// invoked multiple times via `_`.

// ====
// compileViaYul: false
// ----
// f(bool): false -> 1
// f(bool): true -> 2

function_modifier_library_inheritance.so.content:
// Tests that virtual lookup for modifiers in libraries does not consider
// the current inheritance hierarchy.
library L {
    struct S {
        uint256 v;
    }
    modifier mod(S storage s) {
        s.v++;
        _;
    }

    function libFun(S storage s) internal mod(s) {
        s.v += 0x100;
    }
}


contract Test {
    using L for *;
    L.S s;
    modifier mod(L.S storage) {
        revert();
        _;
    }

    function f() public returns (uint256) {
        s.libFun();
        L.libFun(s);
        return s.v;
    }
}
// ----
// f() -> 0x202

transient_state_variable_value_type.sol.content:
contract C {
    uint16 transient x;

    modifier m(uint16) {
        x += 10;
        _;
    }

    function f() public m(x) returns (uint16) {
        x *= 10;
        return x;
    }
}

// ====
// EVMVersion: >=cancun
// ----
// f() -> 100

function_modifier_loop_viair.sol.content:
contract C {
    modifier repeat(uint256 count) {
        uint256 i;
        for (i = 0; i < count; ++i) _;
    }

    function f() public repeat(10) returns (uint256 r) {
        r += 1;
    }
}
// ====
// compileViaYul: true
// ----
// f() -> 1

stacked_return_with_modifiers.sol.content:
contract C {
    uint256 public x;
    modifier m() {
        for (uint256 i = 0; i < 10; i++) {
            _;
            ++x;
            return;
        }
    }

    function f() public m m m returns (uint) {
        for (uint256 i = 0; i < 10; i++) {
            ++x;
            return 42;
        }
    }
}
// ----
// x() -> 0
// f() -> 42
// x() -> 4
