conditional_with_assignment.sol.content:
contract A {
    function f() public pure returns (uint, uint, uint, uint) {
		uint y1 = 1;
		uint y2 = 1;
		uint x = 3 < 0 ? y1 = 3 : 6;
		uint z = 3 < 10 ? y2 = 5 : 6;
		return (x, y1, y2, z);
	}
}
// ----
// f() -> 6, 1, 5, 5

conditional_tuple.sol.content:
contract A {
	function f(bool cond) public pure returns (uint, uint) {
		(uint a, uint b) = cond ? (1, 2) : (3, 4);
		return (a, b);
	}
}
// ----
// f(bool): true -> 1, 2
// f(bool): false -> 3, 4

conditional_true_false_literal.sol.content:
contract A {
	function f() public pure returns (uint) {
		uint x = true ? 1 : 0;
		uint y = false ? 0 : 1;
		return x + y;
	}
}
// ----
// f() -> 2
